INSERT INTO `deyimler` (`deyim_id`, `deyim_adi`, `deyim_aciklama`) VALUES
(1,	'Aba altından değnek göstermek',	'Sakin, yumuşak görünmekle birlikte karşısındakini gizliden gizliye korkutmak. <b>Sakın onlara aba altından değnek göstermeye kalkma, yoksa kaçırırsın. </b>'),
(2,	'Abacı, kebeci, ara yerde sen neci?',	'<b>Tamam, ilgililer bu işe karışabilirler, ama sen neci oluyorsun</b> anlamında kullanılır.'),
(3,	'Abayı yakmak',	'Gönül verip âşık olmak, tutulmak. <b>Türkmen kızına abayı yakalı beri, sazı elinden düşürmez oldu. </b>'),
(4,	'Abbas yolcu',	'1. Yola çıkmaya kesin kararlı. <b>Abbas yolcu! Daha fazla oyalamayın. </b> <br />2. Ölmek üzere (olan). <b>Komaya girdi, abbas yolcu mu ne?</b>'),
(5,	'Abesle iştigal etmek',	'Yersiz, yararsız, boş ve anlamsız şeylerle vakit geçirmek. <b>Şu yaşa geldin, ama abesle iştigal etmekten vazgeçmedin. </b>'),
(6,	'Abuk sabuk konuşmak',	'Düşünmeden, birbiriyle ilgisi olmayan, tutarsız, saçma sapan söz söylemek. <b>Yeter artık, abuk sabuk konuşmalarına daha fazla dayanamayacağım. </b>'),
(7,	'Abur cubur',	'Yararlı olup olmadığı düşünülmeksizin rast gele yenen, yemek yerini tutmayan yiyecekler. <b>Ne diye çocukların karnını abur cuburla doyuruyorsun?</b>'),
(8,	'Aceleye getirmek (dara getirmek)',	'1. Bir işi gerektiği gibi yapmayıp, zaman darlığından yararlanarak birini aldatmak. <b>Tezgâhtar aceleye getirerek gömleğin defolusunu vermiş. </b><br />2. Zaman darlığı sebebiyle gereken özeni göstermemek.'),
(9,	'Acemi çaylak',	'Toy, tecrübesiz, beceriksiz. <b>Acemi çaylağa bak hele! Sen mi tamir edeceksin o saati?</b>'),
(10,	'Acı çekmek (duymak)',	'1. Ağrı, sızı duymak. <b>Kazadan sonra çok acı çekti. </b> <br />2. Üzülmek, üzüntü içinde kalmak. <b>Eşini kaybedeli on yıl oldu ama o hâlâ acı çekiyor. </b>'),
(11,	'Acısı içine (yüreğine) çökmek (işlemek)',	'Bir şeyin verdiği acı, üzüntü benliğinde derin iz bırakmak. <b>Elindeki tek evi de yanıp kül olunca acısı yüreğine işledi. </b>'),
(12,	'Acısını çekmek',	'Yapılan yanlış bir işin doğurduğu sıkıntı ve üzüntüyü yaşamak. <b>Kestiğim o ağacın hâlâ acısını çekiyorum. </b>'),
(13,	'Acısını çıkarmak',	'1. Acılığını yok etmek. <b>Yağda kavurarak acısını aldı. </b><br />2. Önceden uğradığı maddî ve manevî zararı sonradan gidermek. <br />3. Öç almak. <b>Bir gün bana yaptıklarının acısını senden çıkaracağım. </b>'),
(14,	'Acı soğuk',	'Keskin, hoşa gitmeyen, çok üşütücü soğuk. <b>Acı soğuk insanın iliklerine işliyordu. </b>'),
(15,	'Acı söz',	'İnsanın gönlünü inciten, onuruna dokunan ağır söz. <b>Bu acı sözlerine kim katlanır sanıyorsun?</b>'),
(16,	'Aç acına',	'Aç olarak, hiçbir şey yemeden. <b>Bu iş aç acına yapılmaz. </b>'),
(17,	'Açığa çıkarılmak (alınmak)',	'İşinden çıkarılmak, görevine son verilmek. <b>İşe üç gün geç geldi diye açığa alındı. </b>'),
(18,	'Açığa vurmak',	'Gizli, saklı bir şeyi herkese duyurmak, ortaya çıkarmak. <b>Yıllardır içinde sakladığı sırrı mahkemede açığa vurdu. </b>'),
(19,	'Açığı çıkmak',	'Saklamakla görevli bulunduğu para, eşya veya başka bir şeyin sayım sonucu eksik olduğu anlaşılmak. <b>Kasiyerin salı günü akşamı on bin lira açığı çıktı. </b>'),
(20,	'Açığını bulmak',	'Herhangi bir işteki eksiği, hileyi veya zararı ortaya çıkarmak. <b>Hemen her yazısında bir açığını bulmak mümkün. </b>'),
(21,	'Açık alınla',	'Başarı, şeref, övünç ve dürüstlükle. <b>Hemen her işten açık alınla çıkar onlar. </b>'),
(22,	'Açık bono vermek',	'Bir kimseye sınırsız, istediği gibi davranma yetkisi tanımak.'),
(23,	'Açık fikirli',	'Olayları, gelişmeleri, yenilikleri iyi anlayıp gereği gibi karşılayan; düşündüğünü olduğu gibi söyleyebilen kimse. <b>Bu toplumun açık fikirli insanlara duyduğu ihtiyaç, bugün daha fazladır. </b>'),
(24,	'Açık kalpli (yürekli)',	'Samimî, içi temiz, içi dışı bir olan kimse. <b>Komşumuz kadar açık kalpli bir adam görmedim. </b>'),
(25,	'Açık kapı bırakmak',	'Gerektiğinde bir konuya yeniden dönebilme imkânı bırakmak, kesip atmamak, ileriyi düşünerek ılımlı davranmak. <b>Bu kadar kesin konuşmayalım, açık kapı bırakalım da iyi düşünebilme fırsatları olsun. </b>'),
(26,	'Açık konuşmak',	'Gerçeği sakınmadan, çekinmeden söylemek. <b>Daima açık konuşan insanları severim. </b>'),
(27,	'Açık saçık',	'Göreneğe, terbiyeye aykırı derecede açık (söz, davranış, elbise). <b>Açık saçık fıkralar anlatmaya utanmıyor musunuz?</b>'),
(28,	'Açık seçik',	'Çok açık, çok belirgin, ayrıntılarına kadar görülebilen. <b>Daha açık seçik konuş da anlayalım ne demek istediğini. </b>'),
(29,	'Açıkta kalmak (olmak)',	'1. İş ve görev bulamamak. <br />2. Yersiz yurtsuz kalmak. <br />3. kimilerinin elde ettikleri bir yarardan mahrum olmak. <b>Çoluk çocuk açıkta kaldılar fabrika kapanınca. </b>'),
(30,	'Açıktan kazanmak',	'Ortaya hiçbir emek ve sermaye koymadan gelir elde etmek, para kazanmak. <b>Günümüz insanı açıktan kazanmayı bir kural hâline getirdi. </b>'),
(31,	'Açık vermek',	'1. Geliri, giderini karşılamamak. <b>Maaşımız yetmeyecek bu ay, galiba açıkvereceğiz. </b><br />2. Ortaya çıkmaması gereken şeyi farkında olmadan belli etmek. <b>Dikkat et de düşmanlarına açık verme. </b>'),
(32,	'Açlıktan nefesi kokmak',	'1. Çok fazla yoksulluk içinde bulunmak. <b>Dün açlıktan nefesimkokuyordu ama bugün çok şükür karnım tok. </b><br />2. Uzun zaman bir şey yemediği anlaşılmak.'),
(33,	'Açmaza düşmek',	'İçinden çıkılması oldukça güç bir durumda kalmak. <b>Beni bu açmazdan ancak çocuklarım kurtarır. </b>'),
(34,	'Aç susuz kalmak',	'Çok yoksul bir duruma düşmek, fakirlikten yaşayamaz hâle gelmek. <b>Afrika kıtasının pek çok insanı aç susuz kalmış durumda. </b>'),
(35,	'Adama dönmek',	'Hoşa giden bir duruma gelmek, düzelmek. <b>Kapılar, pencereler boyanınca ev adama döndü. </b>'),
(36,	'Adamdan saymak',	'Değeri olmadığı hâlde bir kimseye kıymet vermek, saygı duymak. <b>Seni adamdan saydım diye mi naz yapıyorsun?</b>'),
(37,	'Adam etmek',	'1. Eğitmek, yetiştirmek, belli bir seviyeye getirmek. <b>Sen uğraş, didin, adam et, o da sırt çevirsin sana. </b><br />2. Tamir edip kullanılır hâle getirmek, bir yeri düzene sokmak. <b>Bu arabayı eninde sonunda adam edeceğim. </b>'),
(38,	'Adam evladı',	'İyi bir ailenin iyi yetiştirilmiş; özü, sözü doğru çocuğu. <b>Bu iyiliği ancak bir adam evladı yapabilirdi. </b>'),
(39,	'Adam içine çıkmak',	'Topluluğa karışmak, eşe dosta gitmek, değerli insanların bulunduğu yerlerde olmak ve onlarla görüşmek. <b>Adam içine çıkmayalı uzun zaman oldu. </b>'),
(40,	'Adam olmak',	'1. Yetişip büyümek, gelişmek, iş güç sahibi olmak. <b>Umarım o da bir gün adamolur. </b><br />2. Onarılıp işe yarar hâle gelmek.'),
(41,	'Adam (insan) sarrafı',	'Tecrübesi sayesinde insanların iyisini kötüsünü çabuk anlayacak duruma gelmiş kimse. <b>Sen üzülme, baban insan sarrafıdır, onun ne mal olduğunu kolayca anlar. </b>'),
(42,	'Adam sen de (adaaaam!)',	'Bir işin önemli olmadığını, aldırılmaması gerektiğini anlatmak için söylenir. <b>Adam sen de, o katılmazsa katılmasın, biz birlikte oynarız. </b>'),
(43,	'Adam sırasına geçmek (girmek)',	'Toplumda kendisine daha önce değer verilmezken, artık kendisine önem ve değer verilir olmak. <b>Biliyorum, seni de adam sırasına geçiren paran oldu. </b>'),
(44,	'A`dan Z`ye kadar',	'Bütünüyle, baştan aşağı. <b>Bu sınıfın düzeni a`dan z`ye kadar bozuk. </b>'),
(45,	'Adı batmak',	'Adı anılmaz olmak, unutulmak, sözü edilmez olmak. <b>Hatırlatmayın, adı batsın o adamın!</b>'),
(46,	'Adı çıkmak',	'Kötü bir şöhret kazanmak. <b>Bir kere adı çıkmış, ne yapsa fayda etmiyor, kimse dinlemiyor onu. </b>'),
(47,	'Adı kalmak',	'Bir kimse veya şey ortadan kalktıktan, öldükten sonra adı dillerde dolaşır olmak. <b>Birkaç yıl sonra İstanbul`da doğal güzelliklerin sadece adı kalacak. </b>'),
(48,	'Adı karışmak',	'İyi karşılanmayan bir olayla ilgisinin bulunduğu, o olaya karıştığı söylenmek. <b>Soygun işine Ali`nin de adının karıştığı söyleniyor. Doğru mu?</b>'),
(49,	'Adım atmamak',	'Kesinlikle gitmemek, uğramamak, aramamak. <b>Bir daha o eve adım atmamaya yeminliyim. </b>'),
(50,	'Adını anmamak',	'Bir şeyden, bir kimseden hiç söz etmemek; unutmuş görünmek. <b>Evi terk eden oğlunun adını anmamakta sonuna kadar kararlı. </b>'),
(51,	'Adını koymak',	'1. İsim vermek. <b>Yeni doğan çocuğun adını Ali koydular. </b><br />2. Bir şeyin karşılığını veya fiyatını kararlaştırmak. <b>Önce adını koyalım da ona göre hareket edelim. </b>'),
(52,	'Adını vermek',	'1. Birinin adını bildirmek. <br />2. Biri tarafından salık verildiğini gönderildiği kimseye söylemek. <b>Benim adımı ver ki işlerin çabuk görülsün. </b>'),
(53,	'Aforoz etmek',	'1. Kilise birliğinden çıkarmak. <br />2. Birini yakını olmaktan çıkarmak, ilgiyi kesip uzaklaştırmak, ilişkileri tamamen koparmak. <b>Bütün köylü onu aforoz etmekte kararlı. </b>'),
(54,	'Ağır aksak',	'Pek yavaş olarak, düzgün olmayarak. <b>Her zaman işleri ağır aksak yapıyorsunuz. </b>'),
(55,	'Ağır basmak',	'1. Ağırlığı fazla gelmek. <br />2. Bir işte etkili olmak, gücü üstün gelmek, istediğini yaptırmak. <b>Politik gücü ağır basınca ihaleyi kazandı. </b>'),
(56,	'Ağır başlı',	'Ciddî, olgun, hareketlerinde ölçülü, işlerini düşüne taşına yapan kimse. <b>Ağır başlı olmak insana üstün meziyetler kazandırır. </b>'),
(57,	'Ağırdan almak',	'Bir işi yapmakta acele etmemek, yavaş davranmak, isteksiz görünmek. <b>Hiç sebep yokken işi ağırdan almanı bir türlü anlamıyorum. </b>'),
(58,	'Ağır elli',	'1. Oldukça yavaş iş yapan, çabuk yapmayan. <br />2. Vurduğu zaman çok acıtıp can yakan. <b>Adamın eli amma da ağırmış, ense köküm hâlâ ağrıyor. </b>'),
(59,	'Ağır gelmek',	'1. Ağrına gitmek, onuruna dokunmak. <b>Haketmediğim şu sözler öylesine ağırgeldi ki bana. </b><br />2. yapılması güç gelmek. <b>Bu yaştan sonra inşaat işlerinde çalışmak artık ağır geliyor benim gibi ihtiyara. </b>'),
(60,	'Ağır hastalık',	'Sonu ölümle neticelenebilecek gibi olan tehlikeli hastalık. <b>Ağır hastalık geçirdiği için bir türlü kendini toplayamadı ve zayıf kaldı. </b>'),
(61,	'Ağır söz',	'Kişinin gönlünü inciten, gücüne giden, onuruna dokunan, dayanılması güç söz. <b>Söylediğin ağır sözler çocukları çok incitti. </b>'),
(62,	'Ağız aramak (veya yoklamak)',	'Öğrenilmek istenilen şeyi söyletecek yolda dil kullanmak. <b>Ağzını ara bakalım o konuda bir şey biliyor mu?</b>'),
(63,	'Ağız (söz) birliği etmek',	'Daha önce bir konuda anlaşarak aynı şeyi yapmak ya da söylemek. <b>Ağız birliği etmeli, hep birlikte savunmalıyız kendimizi. </b>'),
(64,	'Ağızdan laf (söz) çekme(çalmak)',	'Bir kişinin bildiği şeyleri ustalıklı konuşmalarda ona sezdirmeden öğrenmek. <b>Boşuna uğraşma, ağzından laf çekemezsin onun. </b>'),
(65,	'Ağızda sakız gibi çiğnemek',	'Bir düşünceyi, bir sözü tekrar edip durmak. <b>Dolap da dolap! Artık ağzında sakız gibi çiğneyip durma şu sözü!</b>'),
(66,	'Ağız değiştirmek',	'Daha önce söylediğinin tersini söylemeye başlamak. <b>Babasını görünce korkusundan ağız değiştirdi. </b>'),
(67,	'Ağız, dil vermemek',	'1. Söz söyleyemeyecek kadar hasta olmak. <br />2. Herhangi bir sebeple hiç konuşmamak, susmak. <b>Kurşuna dizilmeyi göze aldılar ama ağız, dil vermediler. </b>'),
(68,	'Ağız eğmek',	'Yalvarmak, hiç de lâyık olmayan birine yüz suyu dökmek. <b>Ölürüm de ağız eğmem o adama!</b>'),
(69,	'Ağız kalabalığı',	'Birbirini tutmayan, gereksiz, konu dışı sözler. <b>Asıl meseleyi ağız kalabalığı ile ört bas edip kaçamazsın!</b>'),
(70,	'Ağız kalabalığına getirmek',	'Birini gereksiz sözler söyleyip çok konuşmak yolu ile şaşırtmak, dikkatini dağıtıp aldatmak. <b>Ağız kalabalığına getirip yok pahasına aldı malları. </b>'),
(71,	'Ağız kavafı',	'Karşısındakini ikna etmek için diller döken, çok konuşan, gerekli gereksiz söz söyleyen kimse. <b>İğreniyorum şunun gibi ağız kavafı heriflerden. </b>'),
(72,	'Ağız yapmak',	'Birini aldatma, yanıltma, oyalama amacıyla duygularını, düşüncelerini olduğundan başka türlü gösterecek biçimde konuşmak. <b>Ne ağız yapıp duruyorsun, gerçeği söylesene!</b>'),
(73,	'Ağzı açık ayran delisi',	'Yeni gördüğü her şeye alık alık bakan, anlamsız bir hayranlıkla seyredip şaşıran. <b>Haydi yürü, ağzı açık ayran delisi gibi ne bakıp duruyorsun vitrine. </b>'),
(74,	'Ağzı (bir karış) açık kalmak',	'Çok şaşırmak, şaşakalmak. <b>Onca seneden sonra sevdiği arkadaşını birden karşısından görünce ağzı açık kaldı. </b>'),
(75,	'Ağzı kalabalık',	'Çok ve manasız, saçma sapan, tutarsız sözler söyleyen. <b>Ağzı kalabalık insanlara tahammül etmek çok güç bir iş. </b>'),
(76,	'Ağzı kulaklarına varmak',	'Çok sevinmek, sevindiği her hâlinden belli olmak. <b>Takdirname eline verilince sevincinden ağzı kulaklarına vardı. </b>'),
(77,	'Ağzı laf yapmak',	'Güzel, inandırıcı söz söyleme yeteneği olmak. <b>Politikacı mı olacaksın, ağzın laf da yapmalı. </b>'),
(78,	'Ağzına (veya ağzının içine) bakmak',	'1. Ne diyeceğini beklemek. <br />2. Onun sözüne göre hareket etmek. <b>İyi, yemek için de onun ağzına bak bari!</b>'),
(79,	'Ağzına baktırmak',	'Etkili, güzel konuşarak kendini zevk ile dinletmek, dinleyenleri kendisine hayran etmek. <b>O, ağzına baktırmasını bilen ender hatiplerdendi. </b>'),
(80,	'Ağzına bir parmak bal çalmak',	'Amacına ulaşmak için birini tatlı sözlerle bir süre oyalamak, kandırmak; umut verip ikna ederek işini yaptırmak. <b>Öyle bir insan ki ağzına bir parmak bal çal, sonra her istediğini yaptır. </b>'),
(81,	'Ağzına girmek',	'Dinlenirken konuşana doğru oldukça fazla yaklaşmak. <b>Çocuklar, masal anlatan dedenin, neredeyse ağzına gireceklerdi. </b>'),
(82,	'Ağzına lâyık',	'Bir yiyeceğin tadı anlatılırken kullanılır, çok lezzetli yiyecek anlamında. <b>Haydi durma, uzan, tam ağzına lâyık bir tatlı!</b>'),
(83,	'Ağzında bakla ıslanmamak',	'Sır saklamayı becerememek, sırrı hemen açığa vurmak. <b>Ağzında bakla ıslanmayan bu adama nasıl oluyor da açılıyorsun?</b>'),
(84,	'Ağzında gevelemek',	'Açık olarak söylememek, belirli konuşmamak. <b>Lütfen lafı ağzında geveleme de ne söyleyeceksen söyle, çok işim var. </b>'),
(85,	'Ağzından bal akmak',	'Çok tatlı, hoşa gider biçimde konuşmak. <b>Konuş, konuş hele; ağzından bal akıyor. </b>'),
(86,	'Ağzından çıkanı kulağı işitmemek',	'Sözlerini tartmadan, düşünmeden, öfke içinde, nere varacağını hesaplamadan konuşmak. <b>İyice çıldırmış olmalısın. Çünkü ağzından çıkanı kulağın duymuyor. </b>'),
(87,	'Ağzından düşürmemek',	'Bir kimseden veya bir şeyden her zaman söz etmek. <b>Ölünceye kadar torunu Esma`nın adını ağzından düşürmedi. </b>'),
(88,	'Ağzından girip burnundan çıkmak',	'Çeşitli yollara başvurarak birini bir şeye razı etmek; veya kandırmak. <b>Ağzından girip burnundan çıktı ve ondan para koparmayı başardı. </b>'),
(89,	'Ağzından kaçırmak',	'Söylemek istemediği bir şeyi, boş bulunup söyleyivermek. <b>Dikkatli ol, lafı ağzından kaçırıp da gideceğimiz yeri söyleme. </b>'),
(90,	'Ağzından laf almak (çekmek)',	'Bir kimseyi değişik yollarla ve ustalıkla konuşturup birtakım gizli şeyleri öğrenmek. <b>Boşuna uğraşma, ağzımdan laf alamazsın. </b>'),
(91,	'Ağzından yel alsın',	'Olumsuz, kötü şeylerden bahsedenlere karşı <b>ağzını hayra aç</b> anlamında söylenir. <b>Bugün kötü şeyler mi bekliyorsun? Ağzından yel alsın, o ne biçim beklenti?</b>'),
(92,	'Ağzını açıp gözünü yummak',	'Kızgınlık ile sonunu düşünmeden ağzına gelen kötü sözleri söylemek, karşısındakine hakaret etmek. <b>Eve geç gelen kızına ağzını açıp gözünü yumdu. </b>'),
(93,	'Ağzını aramak',	'Karşısındakini kurnazca konuşturarak ağzından söz almak, istediğini öğrenmek. <b>Şunun ağzını ara da bahçeyi satıp satmayacağını öğren. </b>'),
(94,	'Ağzını bıçak açmamak',	'Kırgınlıktan, üzüntüden ya da herhangi bir sebepten ötürü söz söyleyecek durumda olmamak. <b>Boşuna uğraşma, evin yanışına öyle üzülmüş ki ağzını bıçak açmıyor. </b>'),
(95,	'Ağzını havaya (poyraza) açmak',	'Umduğunu elde edememek, fırsatı kaçırdıktan sonra boş yere beklemek. <b>Evi o zaman alacaktın, artık geçti, bundan sonra ağzını havaya aç. </b>'),
(96,	'Ağzını kapamak',	'1. Susmak. <br />2. Çıkarının elden gideceğini düşünerek birinin konuşmasını önlemek. <b>Ağzını kapatamazsak konuşup bizi elâleme rezil edecek. </b>'),
(97,	'Ağzının içine bakmak',	'Konuşan bir kimseyi seve seve ve dikkatlice dinlemek. <b>Konuşması onları öyle sarmıştı ki ağzının içine bakıyorlardı. </b>'),
(98,	'Ağzının kokusunu çekmek',	'Bir kimsenin dayanılmaz, çekilmez tutum ve davranışlarına katlanmak. <b>Yeter artık, daha fazla senin ağız kokunu çekemem. </b>'),
(99,	'Ağzını öpeyim (seveyim)',	'Sevindirici bir söz söyleyene <b>ne güzel, hoş söyledin</b> anlamında kullanılır.'),
(100,	'Ağzının payını vermek',	'Sert söz ve davranışlarla karşılık vererek bir kimseyi yaptığına pişman etmek. <b>Demek öyle, ben de senin ağzının payını vermezsem bana da Hasan demesinler!</b>'),
(101,	'Ağzının suyu akmak',	'Çok beğenip isteyecek duruma gelmek, imrenmek. <b>Vitrindeki kızarmış tavuğu görünce ağzımın suyu aktı. </b>'),
(102,	'Ağzının tadı kaçmak',	'Rahatı kaçmak, huzurunu kaybetmek, bir kimsenin kurulu dirliği, düzenliği bozulmak. <b>Şu vızır vızır işleyen yol burdan geçince ağzımızın tadı kaçtı. </b>'),
(103,	'Ağzının tadını bilmek',	'1. Güzel yemeklerden anlamak. <br />2. Bir şeyin güzelini, iyisini bilmek, anlamak. <b>Şunlardaki güzelliğe bak, ağzının tadını da biliyorsun hani. </b>'),
(104,	'Ağzı sulanmak',	'İmrenmek. <b>Karpuzları ağzını şapırdatarak yemeye başlayınca benim de ağzım sulandı. </b>'),
(105,	'Ağzı süt kokmak',	'Çok genç, toy ve tecrübesiz olmak. <b>Şu ağzı süt kokan mı yarışacak benimle. </b>'),
(106,	'Ağzı var dili yok',	'1. Oldukça sessiz, sakin, kendi hâlinde. <br />2. Konuşmayıp susan, derdini anlatmayan. <b>Telâşlanma sakın, ağzı var dili yok o çocuğun, seni hiç üzmez. </b>'),
(107,	'Ağzıyla kuş tutsa. . .',	'<b>Ne kadar çaba gösterse, ne yapsa da</b> anlamında kullanılır. <b>Ağzıyla kuş da tutsa, artık bu eve adım atamaz. </b>'),
(108,	'Ah almak',	'Birinin bedduasını üstüne çekmek. <b>Zalimliğine devam edersen daha çok kişinin ahını alacaksın. </b>'),
(109,	'Ahı çıkmak',	'Eziyete uğrayan bir kimsenin yaptığı bedduanın etkisini göstermesi.'),
(110,	'Ahı tutmak',	'Zulüm görenin bedduasının yerini bulup gerçekleşmesi. <b>Ahım bir tutarsa dünyanın kaç bucak olduğunu görecek o. </b>'),
(111,	'Ahı yerde kalmamak',	'Yaptığı ilenme (beddua) er geç etkisini göstermek. <b>Şunu iyi bil ki ey zalim, ahım yerde kalmayacak; yüz üstü sürüneceksin. </b>'),
(112,	'Ahkâm çıkarmak',	'Kendi düşüncelerine dayanarak birtakım yargılara varmak. <b>Devletler ancak kuvvetli ordu ile ayakta dururlar diye ahkâm çıkardı. </b>'),
(113,	'Ahmak ıslatan',	'İnce ince yağan yağmur, çisenti. <b>Böyle yürümeye devam edersek bu ahmak ıslatan iliklerimize işleyecek. </b>'),
(114,	'Ahret kardeşi',	'Dünya ve ahiret işlerinde birbirlerinden ayrılmayan kimseler; kan bağı olmaksızın manevî olarak kurulan kardeşlik.'),
(115,	'Ahrette on parmağı yakasında olmak',	'Haksızlığa uğrayışını bu dünyada önleyip hakkını alamayanın, öte dünyada (ahrette) kendisine sorumlu olan kimseden davacı olması. <b>Hakkımı vermedin ama ahrette on parmağım yakanda olacaktır. </b>'),
(116,	'Akan sular durmak',	'Artık itiraz edilebilecek, karşı durulacak bir nokta kalmamak. <b>Siz Mehmet Ağa`ya gidin, o devreye girdi mi akan sular durur, kolay anlaşırsınız. </b>'),
(117,	'Akıl defteri',	'Hatırlanıp yapılması gereken şeylerin yazıldığı küçük defter, muhtıra defteri, ajanda.'),
(118,	'Akıl etmek',	'Herhangi bir önlem ve çareyi zamanında düşünmek, vaktinde hatırlamak. <b>Sular kesilecekti ama kovaları doldurmayı akıl edemedim. </b>'),
(119,	'Akıl hocası',	'1. Birine yol gösteren, akıl öğreten kimse. <br />2. Herkese akıl öğretmeye meraklı kimse. <b>Lütfen akıl hocalığı yapmaya kalkma, biz işimizi senden iyi biliriz. </b>'),
(120,	'Akıl kârı olmamak',	'Akıllı, dengeli ve ölçülü bir kişinin yapacağı iş olmamak. <b>Akıl kârımı şimdi senin yaptığın bu iş?</b>'),
(121,	'Akıl kutusu (kumkuması)',	'Çok zeki, akıllı kimse; bilgiç. <b>Akıl kutusu mübarek, her meseleyi çözüyor. </b>'),
(122,	'Akıllara durgunluk vermek',	'Çok şaşılacak bir şey olmak. <b>Bir görmeliydin o olayı, akıllara durgunluk verecek bir olaydı. </b>'),
(123,	'Akıllı uslu',	'Dengeli, yaramazlık etmeyen, ölçüsüz ve taşkın davranışlarda bulunmayan. <b>Senin çocuk pek akıllı uslu görünüyor. </b>'),
(124,	'Akıl öğretmek (vermek)',	'Herhangi bir konuda yol gösterip tavsiyede bulunmak, bilgi vermek. <b>Sana akıl verecek bir adam da mı bulamadın?</b>'),
(125,	'Akıl sır ermemek',	'Bir işin gizli yönlerini, niteliğini, asıl sebebini anlayamamak. <b>Senin bu işi nasıl berbat ettiğine hâlâ akıl sır erdiremedim. </b>'),
(126,	'Akıntıya kürek çekmek',	'Olmayacak, gerçekleşmeyecek bir iş uğrunda boşuna çaba sarf etmek. <b>Desene boşuna kürek çekmişiz, olmayacak bu iş. </b>'),
(127,	'Akla karayı seçmek',	'Bir işi başarmak uğrunda çok yorulmak, sonuca kadar çok zahmet çekmek. <b>Seni buluncaya kadar akla karayı seçtim. </b>'),
(128,	'Aklı almamak',	'1. Akla uygun gelmemek, inanılacak gibi olmamak. <br />2. Anlamamak. <b>Şu işleri bir türlü aklım almıyor. </b>'),
(129,	'Aklı başına gelmek',	'1. Zarar gördüğü işlerden uslanıp akıllıca davranmak. <br />2. Baygınlıktan ayılmak, kendine gelmek. <b>Çabuk koşun, nihayet kendine geliyor!</b>'),
(130,	'Aklı başından gitmek',	'1. Çok korkudan veya çok sevinçten ne yapacağını şaşırmak. <br />2. Kafası çok yorulmuş olduğundan iyi düşünememek. <b>Annemi öyle evin ortasında baygın görünce aklım başımdan gitti. </b>'),
(131,	'Aklı başında olmamak',	'1. İyi düşünebilir durumda olmamak. <br />2. Bayılmak, kendisinden geçmek. <b>Artık aklı başında olmamak onun işine geliyor sanki, böylece sorumluluktan kurtulacak, rahat edecek. </b>'),
(132,	'Aklı çıkmak',	'Titizlikle üzerinde durmak, çok korku geçirmek, çok korkmak. <b>Elbisem yırtılacak diye aklı çıkıyor. </b>'),
(133,	'Aklı durmak',	'Şaşırmak, düşünemez bir hâle gelmek. <b>Resmi öyle güzel yapmış ki görsen aklın durur. </b>'),
(134,	'Aklı karışmak',	'Ne yapacağını bilememek, bocalamak, şaşırmak. <b>Dur hele, bir düşüneyim, söylediklerin aklımı karıştırdı. </b>'),
(135,	'Aklı kesmek',	'Bir şeyin olabileceğine, bir şeyi yapabileceğine inanmak. <b>Seninle bu işi başarabileceğime pek de aklım kesmiyor. </b>'),
(136,	'Aklına düşmek',	'1. Hatırlamak. <br />2. Kafasında bir düşünce doğmak. <b>Aklına düşen her şeyi yapmak zorunda mısın?</b>'),
(137,	'Aklına esmek',	'Daha önce düşünmemiş olduğu şeyi birden yapmaya karar vermek. <b>Birden aklına esti, kalkıp sahile indi. </b>'),
(138,	'Aklına gelen başına gelmek',	'Olmasından korktuğu şeyin zarar verici etkisine uğramak. <b>Aklıma gelen başıma geldi, evi su bastı. </b>'),
(139,	'Aklına gelmek',	'1. Hatırlamak. <br />2. Bir şeyi yapmayı düşünmek, tasarlamak. <b>Aklıma geldi, kalkıp babama gittim. </b>'),
(140,	'Aklına koymak',	'1. Bir şeyi yapmaya kesin olarak karar vermek. <b>Bu sene takıntısız sınıfımı geçmeyi aklıma koydum. </b><br />2. Bir fikri başkasına aşılamak.'),
(141,	'Aklına (aklını) takmak',	'Bir şeyi devamlı olarak düşünmek, bir fikre sürekli olarak zihninde yer vermek ve zihni onunla meşgul etmek. <b>Onu niçin kırdım, aklıma takıldı düşünüp duruyorum. </b>'),
(142,	'Aklına yer etmek',	'Uygun bulduğu bir düşünce kafasına yerleşmek. <b>Onun sana söyledikleri aklına yer eder inşallah. </b>'),
(143,	'Aklından zoru olmak',	'Tutarsız, dengesiz, ölçüsüz, delice davranışlarda bulunmak. <b>Bırak o bıçağı, aklından zorun mu var senin?</b>'),
(144,	'Aklını almak',	'Çekiciliği, güzelliği ile büyülemek, etkisi altına almak. <b>Kızın bir bakışı, aklını başından almaya yetti. </b>'),
(145,	'Aklını başına almak (toplamak, devşirmek)',	'Mantıksız, ölçüsüz davranışlarda bulunmaktan kendini kurtararak akıllıca bir yola girmek. <b>Aklını başına al, yoksa bu içki seni götürecek. </b>'),
(146,	'Aklını başından almak',	'Çok şaşırtmak, düşünemeyecek duruma getirmek. <b>Gördüğü ev aklını başından aldı. </b>'),
(147,	'Aklını (bir şeyle) bozmak',	'1. Sapıtmak, delirmek. <br />2. Yalnızca ilgilendiği, üzerine düştüğü şeyle uğraşıp durmak, başka hiçbir mesele düşünmemek. <b>Bizim çocuk sinema ile aklını bozdu. </b>'),
(148,	'Aklını çalmak (çelmek)',	'1. Kararından, niyetinden vazgeçirip başka bir yola sokmak. <br />2. Baştan çıkarmak, ayartmak. <b>Aklını çelip onu evlenmeye razı et. </b>'),
(149,	'Aklını peynir ekmekle yemek',	'Akılsızca, şaşkınca, delice işler yapmak. <b>Misafirliğe böyle gidilir mi? Sen aklını peynir ekmekle mi yedin?</b>'),
(150,	'Ak pak',	'1. Tertemiz. <br />2. Saçı sakalı ağarmış. <br />3. Alımlı ve beyaz tenli. <b>Ne kadar da ak pak bir çocuk. </b>'),
(151,	'Akşama sabaha',	'Neredeyse, pek yakında, kısa bir süre içinde. <b>Konuklar akşama sabaha burada olurlar, sakın bir yere kaybolma!</b>'),
(152,	'Akşamdan kavur, sabaha savur',	'Kazandığını günü gününe harcayan, har vurup harman savuran, savruk kimselerin durumunu anlatmak için kullanılır.'),
(153,	'Akşamı iple çekmek',	'Gecenin olmasını sabırsızlıkla beklemek. <b>Ne güzel bir ziyaret olacak. Akşamı iple çekiyorum. </b>'),
(154,	'Alacağına şahin, vereceğine karga',	'Alırken bütün gücünü kullanan ve kolaylık gösteren, kimsede parasını bırakmayan; verirken ise bin bir güçlük çıkaran, vereceğini geciktirmek için elinden geleni yapan kimse için kullanılır.'),
(155,	'Alacağı olsun',	'<b>Günün birinde ondan öcümü alırım</b> anlamında göz korkutmak için söylenir.'),
(156,	'Al aşağı etmek',	'Birini bulunduğu yerden, mevkiden indirmek. <b>Ya, gördün mü, demek ki el oğlu adamı al aşağı ediyormuş bir çırpıda!</b>'),
(157,	'Al birini vur birine (ötekine)',	'Hepsi aynı, bir ayarda, hiçbiri işe yaramaz. <b>Onlardan söz etme bana. Al birini vur birine. </b>'),
(158,	'Alçak gönüllü olmak',	'Gurur ve kibre kapılmayıp kendini olduğundan daha aşağı düzeyde sayma, başkalarından yüksek görmeme durumu. <b>İnsanı insan yapan vasıflardan biri de alçak gönüllü olmaktır. </b>'),
(159,	'Al gülüm ver gülüm',	'1. Karşılıklı sevgi gösterisi. <br />2. Çokluk uygun olmayan işlerde birbirinin çıkarını kollamak.'),
(160,	'Alı al, moru mor',	'Telâş veya yorgunluktan yüzü kıpkırmızı kesilmiş (olarak). <b>Uçağı kalkmak üzere olan babama alı al, moru mor bir şekilde yetişebildim. </b>'),
(161,	'Alıcı gözüyle bakmak',	'Çok dikkatli bakmak, inceden inceye gözden geçirmek. <b>Mobilyaya ilk defa alıcı gözüyle baktı. </b>'),
(162,	'Alın teri dökmek',	'Zahmetli iş görüp çok emek vermek. <b>Alın teri dökmeyenler, emeğin ne olduğunu bilemezler. </b>'),
(163,	'Ali Cengiz oyunu',	'<b>Kurnazca, haince aklı durduracak iş yapmak</b> anlamında kullanılır. <b>Bana bir Ali Cengiz oyunu oynadılar ki sormayın gitsin. </b>'),
(164,	'Ali kıran baş kesen',	'Çok zorba, kaba kuvvetle hâkimiyet kuran. <b>Mehmet, sınıfın Ali kıran baş kesini olmuştu. </b>'),
(165,	'Ali`nin külâhını Veli`ye, Veli`nin külâhını Ali`ye giydirmek',	'Kendi sermayesi olmadığı hâlde, birinden aldığını ötekine, ötekinden aldığını bir başkasına vererek işini yürütmek.'),
(166,	'Allah adamı',	'Hile, kötü bilmeyen; hak yol üzerinde olan, Allah`a ibadette kus dini bütün kimse. <b>Allah adamı olmalısın dünya da, hem de ahrette iyilik görebilesin. </b>'),
(167,	'Allah`a emanet',	'Herhangi bir şeyi Yüce Allah`ın korumasına ve esirgemesine terk etmek. <b>Seni Allah`a emanet ederek gidiyorum oğlum. </b>'),
(168,	'Allah Allah!',	'Daha çok şaşkınlık ve hayret hâllerini anlatır. <b>Allah Allah! Nasıl oldu bu iş, aklım almıyor?</b>'),
(169,	'Allah aratmasın',	'Yakınılacak bir durumda, bir şeyin hiç bulunmaması hâlindeki sıkıntı anında <b>Allah daha kötüsünü göstermesin</b> anlamında kullanılır.'),
(170,	'Allah aşkına',	'Yemin vermek veya yalvarmak için <b>Allah`ını seversen</b> anlamında şaşma, usanç bildirir. <b>Allah aşkına şu işi bir daha yapma!</b>'),
(171,	'Allah bilir',	'1. Belli değil, Cenab-ı Hak`tan başka kimse bilmez. <b>Allah bilir bu sırrın iç yüzünü. </b><br />2. Bana öyle geliyor ki. <b>Allah bilir esrar da alıyordur bu çocuk. </b>'),
(172,	'Allah`ın belâsı',	'Varlığı üzüntü veren, varlığından huzursuz olunan şey. <b>Allah`ın belâsı adam yine çıktı ortaya. </b>'),
(173,	'Allah versin',	'1. Dilenciyi savmak için <b>bekleme, sadaka vermeyeceğim</b> anlamında söylenir. <br />2. İyi şey elde edenlere memnunluk bildirmek için, kimi zaman da takılma ve şaka için söylenir. <b>Allah versin, işlerin gayet iyi görünüyor. </b>'),
(174,	'Allah yarattı dememek',	'Kıyasıya dövmek, çok hırpalamak. <b>Adamlar yabancıya bir giriştiler ki Allah yarattı demediler. </b>'),
(175,	'Allah `yürü ya kulum` demiş',	'Az zamanda çok para kazanan ve işinde çok çabuk ilerleyenler için söylenir. <b>Cenab-ı Hak bir kimseyi zengin etmek isterse ona, `yürü ya kulum` demesi yeter. </b>'),
(176,	'Allak bullak etmek',	'Kurulu düzeni bozmak, karmakarışık bir duruma getirmek. <b>Çocuklar evi allak bullak edip gitmişler. </b>'),
(177,	'Allayıp pullamak',	'Kötü görünüşü kapatmak için bir şeyi süslemek, donatmak. <b>Hurda arabaları allayıp pullayıp pazara çıkarmışlar. </b>'),
(178,	'Allem etmek, kallem etmek',	'İstediğini elde etmek için her türlü kurnazlığa başvurmak. <b>Namussuzlar allem edip kallem edip yaşlı adamın evini elinden aldılar. </b>'),
(179,	'Alnı açık yüzü ak (olmak)',	'Herhangi bir ayıbı, çekinecek bir durumu olmamak, iffetli ve şerefli olmak. <b>İşte alnı açık yüzü ak meydandayım; çıksınlar karşıma. </b>'),
(180,	'Alnını karışlamak',	'1. Bir işin çok güç olduğunu, yapılamayacak kadar zor olduğunu anlatır. <br />2. Küçümseyerek meydan okumak, tehdit etmek. <b>Beni polise bildirenin alnını karışlarım. </b>'),
(181,	'Alnının akıyla',	'Küçümsenecek, ayıplanacak bir duruma düşmeden; tertemiz, şerefiyle, başarılı olarak. <b>Allah`ın izniyle bu işten alnımın akıyla çıkacağım. </b>'),
(182,	'Alnının ar damarı çatlamak',	'Utanma, sıkılma duygularını yitirmiş bulunmak. <b>Adama bak nerede soyunuyor, alnının ar damarı çatlamış anlaşılan. </b>'),
(183,	'Alnının damarı çatlamak',	'Başarmak için çok sıkıntı çekmek, çok çaba sarf edip emek vermek. <b>O yolu açıncaya kadar benim alnımın damarı çatladı, sen ne halt etmeye bozuyorsun?</b>'),
(184,	'Alnının kara yazısı',	'Kötü talih, baht. <b>Ne yapayım, alnımın kara yazısı böyle imiş. </b>'),
(185,	'Al takke ver külâh',	'1. Bir mesele üzerinde uzun çekişmelerden sonra. <br />2. Senli benli, samimî dostluğu sürdürerek. <b>Al takke ver külâh yıllarca yaptık bu işi. </b>'),
(186,	'Altı alay, üstü kalay',	'İçi dışı bir olmayan; dışı süslü, içi berbat. <b>Altı alay üstü kalay bir dolaba benziyor bu. </b>'),
(187,	'Altı kaval, üstü şeşhane (Şişhane)',	'Daha çok giyim için <b>altı, üstüne; bir parçası öbür parçasına uymaz. </b> anlamında kullanılır. <b>Çabuk çıkar şu üzerindeki altı kaval üstü şeşhane elbiseyi, yoksa rezil olacaksın el âleme. </b>'),
(188,	'Altın babası',	'Çok zengin, parası çok olan kimse. <b>Adam altın babası, her istediğini kolayca yaptırıyor. </b>'),
(189,	'Altın bilezik',	'Para getiren, hayat boyunca geçimi sağlamaya yarayan sanat ve meslek. <b>Şimdiden bir altın bilezik sahibi ol ki yarın rahat edesin. </b>'),
(190,	'Altında kalmamak',	'1. Bir şeyi karşılıksız bırakmamak. <b>Onun bana yaptığı iyiliğin altında kalır mıyım?</b><br />2. Bir şeyin üstesinden gelmek. <b>Bana verdiği işin altında kalmayacağım. </b>'),
(191,	'Altından Çapanoğlu çıkmak',	'Girişilen bir işte başa dert olacak bir durumla, umulmayan bir tehlike ile karşılaşmak. <b>Bana öyle geliyor ki bu işin altından Çapanoğlu çıkacak. </b>'),
(192,	'Altından girip üstünden çıkmak',	'Bir serveti, bir parayı, bir kaynağı gereksiz yere, düşüncesizce, sorumsuzca harcayıp kısa zamanda bitirmek. <b>Bir ayda o kadar paranın altından girip üstünden çıktı. </b>'),
(193,	'Altından kalkmak',	'Bir zorluğu yenip işi başarmak. <b>Telâşlanma, işin altından kalkacaktır o. </b>'),
(194,	'Altını çizmek',	'Bir şeyin (daha çok sözün) önemini belirtmek, üzerine dikkati çekmek, vurgulamak. <b>Altını çize çize söylüyorum. Eninde sonunda sen de geleceksin. </b>'),
(195,	'Altını üstüne getirmek',	'1. Bir şeyi bulmak için aramadık yer bırakmamak. <b>Evin altını üstüne getirdik ama tabancayı bulamadık. </b> <br />2. Söz ve davranışlarıyla çevreyi birbirine düşürmek, karmakarışık etmek. <b>Adam iki çift laf etti. Topluluğun altını üstüne getirdi. </b>'),
(196,	'Altın kesmek',	'Çok fazla miktarda para kazanır olmak. <b>Adamların açtığı büfe altın kesiyor sanki. </b>'),
(197,	'Altmış altıya bağlamak',	'O an ki durumu temelli olmayan bir çözümle kurtarmak veya bir işi kesin neticeye vardırmış gibi görünmek. <b>İnsanları altmış altıya bağlamakta üstüne yoktur onun. </b>'),
(198,	'Altta kalanın canı çıksın',	'<b>Herkes başının çaresine baksın, güçsüzleri düşünme, gücü yetmeyene ne olursa olsun</b> anlamında kullanılır.'),
(199,	'Alttan (aşağıdan) almak',	'Sert konuşan birine karşı yumuşak, olumlu, onu haklı görüyormuş gibi tavır almak. <b>Amacına ulaşmak istiyorsan onunla konuşurken alttan al, pes perdeden konuş. </b>'),
(200,	'Alttan güreşmek',	'Biraz geriden, pasif hareket edip gizli gizli yenme yollarını kollamak. <b>Vay hınzır vay!. . Alttan güreşip aklın sıra başarı kazanacaksın ha!</b>'),
(201,	'Alt yanı çıkmaz sokak',	'Sonuç alınmayacak iş, umutsuz durum. <b>Çobanlık mı, dağ tepe dolaş dur, alt yanı çıkmaz sokak vesselâm. </b>'),
(202,	'Amana gelmek',	'Teslim olmak, önce direnirken zor karşısında boyun eğmek. <b>Nihayet düşman amana geldi. </b>'),
(203,	'Aman dedirtmek (amana getirmek)',	'Karşı koyan birini boyun eğmek zorunda bırakmak, teslim olmaya zorlamak. <b>Düşmana aman dedirtmek boynumuzun borcu oldu artık. </b>'),
(204,	'Aman dilemek',	'Önce direnirken zor karşısında boyun eğip canının bağışlanmasını istemek, galip gelenin merhametine sığınmak. <b>Aman dileyene kılıç kalkmaz. </b>'),
(205,	'Aman vermemek',	'1. Göz açtırmamak, rahat bırakmamak. <br />2. Düşmanı acımayıp öldürmek, merhamet etmemek. <b>Böyle kahpe insanlara sakın aman vermeyin!</b>'),
(206,	'Ana baba günü',	'1. Mahşer günü. <br />2. Sıkıntılı kalabalık; telâşlı, tehlikeli, kimsenin kimseyi tanımadığı kalabalık. <b>Yangın yeri ana baba gününe dönmüştü. </b>'),
(207,	'Ana kuzusu',	'1. Pek küçük kucak çocuğu. <br />2. Sıkıntıya, güç işlere alışkın olmayan, nazlı çocuk veya genç. <b>Şu torbayı kaldırışına bak hele, tam bir ana kuzusu. </b>'),
(208,	'Anan yahşi, baban yahşi',	'Bir kimseyi işini yaptırabilmek için pohpohlamak, gereğinden fazla överek istediğini elde etmeye çalışmak.'),
(209,	'Anası ağlamak',	'Çok eziyet çekmek, sıkıntıya katlanmak, bitkin duruma düşmek. <b>Onu buraya getirinceye kadar anam ağladı. </b>'),
(210,	'Anasından doğduğuna pişman',	'1. Üşengeç, çok tembel. <br />2. Canından bezmiş. <b>O işi yaptı ama anasından doğduğuna bin pişman. </b>'),
(211,	'Anasından doğduğuna pişman etmek',	'Çok eziyet ederek canından bezdirmek, bir kimseyi çok üzmek. <b>Karşıma bir çıksın, onu anasından doğduğuna pişman edeceğim. </b>'),
(212,	'Anasından emdiği süt burnundan (fitil fitil) gelmek',	'Bir işi yaparken çok sıkıntı çekmek, eziyete katlanmak. <b>Şu arabanın taksitlerini ödeyinceye kadar anamdan emdiğim süt burnumdan geldi. </b>'),
(213,	'Anasını ağlatmak',	'Bir kimseye çok eziyet edip sıkıntı çektirmek. <b>Adamın üzerine öyle gittiler ki iki günde anasını ağlattılar. </b>'),
(214,	'Anasının gözü',	'Hileci, kurnaz, çok açık göz, çıkarcı, hin oğlu hin. <b>Adam anasının gözü, iki dakikada bitiriverdi işi. </b>'),
(215,	'Anasının nikâhını istemek',	'Bir şeye değerinden çok para istemek, olmayacak bir istekte bulunmak. <b>Senin istekli olduğunu duydu adam, şimdi gidersen anasının nikâhını isteyecek o eve. </b>'),
(216,	'Anasını sat! (satayım)',	'Önem verme, aldırma, umursama, bunun için kederlenme, üzülme, <b>Sat anasını o işin, yenisine bak!</b>'),
(217,	'Anca beraber, kanca beraber',	'Birbirimizden ayrılmayacağız, işler iyi de gitse, kötü de gitse hep birlikte yapacağız, beraberliği bozmayacağız. <b>Bu toprağı yalnız ben mi atacağım, hayır arkadaşlar; haydi anca beraber, kanca beraber. </b>'),
(218,	'Anladımsa Arap olayım',	'<b>Hiçbir şey anlamadım</b> anlamında kullanılır. <b>Senin anlattıklarını anladımsa Arap olayım. </b>'),
(219,	'Ant içmek (etmek)',	'Yemin etmek, bir şeyi yapmaya veya yapmamaya söz vermek. <b>Ant içtik, asla bu ülkeyi düşmana bırakmayacağız. </b>'),
(220,	'Apar topar',	'Telâş ve acele ile, yaka paça, hazırlanmadan, <b>Treni kaçırırım korkusuyla apar topar evden ayrıldım. </b>'),
(221,	'Ara (aralarını) bozmak',	'İki kişi arasındaki iyi ilişkiyi, dostluğu, arkadaşlığı yıkmak. <b>Kim ki ara bozar, o toplumun yüz karasıdır. </b>'),
(222,	'Ara bulmak',	'Birbirleriyle anlaşamayan, bir araya gelemeyen kişileri uzlaştırmak, barıştırmak. <b>İki öğrencinin arasını bulmak, tam bir haftamı aldı. </b>'),
(223,	'Araları açılmak (bozulmak)',	'İyi ilişkileri, dostlukları, arkadaşlık bağları kopmak; birbirlerine dargın hâle gelmek. <b>Şu iki çiftin araları nasıl açıldı hâlâ anlayamadım. </b>'),
(224,	'Aralarından kara kedi geçmek (veya aralarına kara kedi girmek)',	'İyi anlaşan iki kişinin veya dostun ilişkileri bozulmak, aralarına soğukluk girmek, birbirlerine gücenmek, <b>Niçin konuşmuyorsunuz? Aranızdan kara kedi mi geçti?</b>'),
(225,	'Aralarından su sızmamak',	'Çok iyi, çok yakın dostluk veya arkadaşlık kurmak, ahbap olmak. <b>Şunlara bak, aralarından su sızmıyor. </b>'),
(226,	'Arap saçına dönmek',	'İşlerin çok karışıp içinden çıkılmaz bir durum alması. <b>Bırak artık sorumsuzluğu, işleri bu tavrınla Arap saçına döndürdün. </b>'),
(227,	'Araya girmek',	'1. İki kişinin arasındaki bir işe karışmak. <br />2. Araları bozuk olan iki kişiyi uzlaştırmaya çalışmak. <br />3. Yapılmakta olan bir işin yapılmasını geciktirmek. <b>Araya başka işler girince seninkini yapamadım, kusura bakma. </b>'),
(228,	'Araya koymak',	'Bir işte sözü geçen bir kimsenin aracılığına başvurmak. <b>Genel müdürü araya koyup senin işe alınmanı sağlayacaklardır. </b>'),
(229,	'Arayı yapmak',	'1. Arası bozuk olan kimse ile barışmak. <br />2. Arası açık olan iki kişiyi uzlaştırıp, barıştırmak. <b>Hasan aramızı yapmasaydı biz hâlâ diken üstünde oturuyor olacaktık. </b>'),
(230,	'Ar damarı çatlamak',	'Utanç duyulacak şeyleri sıkılmadan yapmak, utanmayı bırakmak, yüzsüz olmak. <b>Ar damarı çatlamış bu adamdan ne umuyorsun anlamadım bir türlü. </b>'),
(231,	'Arı kovanı gibi işlemek',	'Girip çıkanı, gelip gideni çok olmak. <b>Şu seçim dolayısıyla doktorun evi arı kovanı gibi işliyor. </b>'),
(232,	'Ârif olan anlasın (anlar)',	'Üstü örtülü olarak söylenen bir sözün, anlayışı kuvvetli kimselerce anlaşılabileceğini belirtmek için kullanılır.'),
(233,	'Arka arkaya vermek',	'Birbirini korumak, kollamak, için birleşmek; dayanışmak, yardımcı olmak. <b>Arka arkaya verirsek karşımızda hiçbir güç duramaz. </b>'),
(234,	'Arka (sırt) çevirmek',	'Birine eskiden duyduğu ilgiyi göstermemek, yabancı gibi davranmak. <b>İşlerim bozulunca bana sırt çevirdi. </b>'),
(235,	'Arka çıkmak',	'Birilerine karşı, birini korumak; savunmak, kayırmak. <b>Babası arka çıkmasaydı onu bir güzel dövecekti. </b>'),
(236,	'Arkadan söylemek',	'Bir kimsenin bulunmadığı yerde onun hakkında ileri geri konuşmak, dedikodusunu yapmak, çekiştirmek. <b>Adamın arkasından söylemeye utanmıyor musun?</b>'),
(237,	'Arkadan vurmak',	'Kendisine inanan, güvenen bir kimseye gizlice kötülük etmek. <b>Onun beni arkamdan vuracağı hiç aklıma gelmezdi. </b>'),
(238,	'Arka kapıdan çıkmak',	'Özellikle bir eğitim kurumundan, bir iş yerinden hiçbir varlık gösteremeden, bir şey öğrenemeden ayrılmak. <b>Övünüp durma, bilgine bakılırsa sen o okulun arka kapısından çıkmışsın. </b>'),
(239,	'Arkası kesilmek',	'Tükenmek, bitmek, süregelen bir şeyin son bulması. <b>Kiranın da arkası kesilirse ne yaparız biz?</b>'),
(240,	'Arkasına düşmek',	'1. Birini gözden ayırmayarak arkasından gitmek. <br />2. Bir işi sona erdirmek için çok sıkı çalışmak. <b>Arkasına düşmezsen nasıl elde edeceksin o evi?</b>'),
(241,	'Arkasında dolaşmak (gezmek)',	'Bir işi sonuca bağlamak için ilgili yerlere giderek görüşme fırsatı aramak, onların yardımını sağlamak.'),
(242,	'Arkasını getirememek',	'Başladığı işi sürdürüp sona erdirememek, sonuçlandıramamak. <b>Ne tembel adamsın, şu işin arkasını getiremedin hâlâ!</b>'),
(243,	'Arkasını sıvamak',	'İltifat etmek, okşamak, övmek, birisini bu yolları kullanarak bir işe sevk etmek. <b>Arkasını sıvayarak yaptırıyorum her işi bu çocuğa. </b>'),
(244,	'Arkasını (birine) vermek',	'Bir kimsenin himayesinden güç almak. <b>Arkasını kaymakama vermiş pervasızca konuşuyor, yolu burdan geçireceğim diyor. </b>'),
(245,	'Arkası (sırtı) pek',	'1. Soğuktan muhafaza edecek biçimde giyinmiş, iyi giyinmiş olan. <br />2. Güçlü bir kimseye ya da yere güvenen. <b>Ona göre hava hoş, çünkü karnı tok, sırtı pek nasıl olsa!</b>'),
(246,	'Arkası (sırtı) yere gelmemek',	'1. Sarsılmamak, sağlam ve sağlıklı durumunu sürdürmek. <br />2. Hiç yenilgi yüzü görmemek. <b>Arkası yere gelmemiş bir adam olarak kalmalı o. </b>'),
(247,	'Armudun sapı var, üzümün çöpü var demek',	'Hiçbir şeyi beğenmemek, her şeyin bir kusurunu bulmak.'),
(248,	'Armut piş, ağzıma düş',	'Bir işin hiç emek harcamadan olmasını, kendiliğinden hazır olup ayağına gelmesini bekleyenlerin durumunu anlatmak için kullanılır.'),
(249,	'Arpa boyu kadar gitmek',	'Pek az ilerlemek. <b>Onca çabaya rağmen arpa boyu kadar gidebildim ancak. </b>'),
(250,	'Arpacı kumrusu gibi düşünmek',	'Derin derin ne yapacağını bilemeden, çaresizlik içinde düşünüp durmak. <b>Öyle arpacı kumrusu gibi ne düşünüp duruyorsun?</b>'),
(251,	'Arpalık yapmak',	'Bir yeri sürekli çıkar kaynağı olarak kullanmak, sömürmek. <b>Batılılar ülkemizi arpalık yaptılar âdeta. </b>'),
(252,	'Art düşünce (niyet)',	'Açığa vurulandan ayrı, gizli tutulan, asıl düşünce. <b>Onun bizim hakkımızda art düşüncelere sahip olduğunu biliyorum. </b>'),
(253,	'Asıp kesmek',	'1. İşkence etmek, zalimce tavırlarda bulunmak. <br />2. Tehdit etmek, zalimce davranışlarda bulunacakmış gibi konuşmak. <b>Dün haktan ve adaletten söz edenler, bugün iktidar olunca asıp kesmeye başladılar. </b>'),
(254,	'Askıda kalmak',	'Bir engel çıkması dolayısıyla bir işin sonuca varamaması, yapılamayıp öylece kalması. <b>Senin gelmemen yüzünden bütün işler askıda kaldı. </b>'),
(255,	'Askıya almak',	'1. Geciktirmek, belirsiz olarak ertelemek, bir işi zamanında yapmayıp savsaklamak. <br />2. Altı boşalmış yapıyı dikmelerle tutturarak yıkılmaktan kurtarmak. <b>Söyle ona, o adamların tayin işlerini askıya alsın. </b>'),
(256,	'Askıya çıkarmak',	'Evlenecek kimselerin nikâhtan önceki durumlarını gösterir belgelerin, belirli bir süre için ilgili dairede görünür bir yere asılması, ilân edilmesi.'),
(257,	'Aslan payı',	'1. Hak edilenden daha çok alınan pay, en güçlünün aldığı pay. <br />2. Bir bölüşmede en büyük pay. <b>Aslan payı Ahmet`e düştü. </b>'),
(258,	'Aslan yürekli',	'Yılmaz, hiçbir şeyden korkmayan, yiğit, kahraman, <b>Aslan yürekli Mehmetçik düşmanı çil yavrusu gibi dağıttı. </b>'),
(259,	'Aslı faslı (astarı) olmamak',	'Yalan, asılsız olmak, gerçek payı bulunmamak. <b>Aslı astarı olmayan işlerin içine sürükleme bizi. </b>'),
(260,	'Astarı yüzünden pahalı olmak',	'Bir işin ayrıntısına ödenen paranın aslına ödenen paradan fazla olması, gerçek değerinden fazlaya malolması. <b>Elbiseyi diktin ama astarı yüzünden pahalı oldu. </b>'),
(261,	'Astığı astık, kestiği kestik',	'Davranışlarından dolayı kimseye hesap vermeyen, istediği gibi davranan, çok sert kimseler için kullanılır.'),
(262,	'Aşağıdan almak',	'Sert konuşan kimselere karşı yumuşak bir dil kullanmak. <b>Biraz aşağıdan alırsan onun sana zarar vermesini kolayca önlersin. </b>'),
(263,	'Aşağı kurtarmaz',	'1. Bundan ucuza verilmez. <br />2. Daha aşağı bir durumu kendine lâyık görmez. <b>Israr etme, bu araba daha aşağı kurtarmaz. </b>'),
(264,	'Aşağı tükürsen sakal, yukarı tükürsen bıyık',	'Sakıncalı oluşları eşit olan iki karşıt davranıştan birine karar verememe zorunluluğunu anlatmak için kullanılır.'),
(265,	'Aşağı yukarı',	'Yaklaşık olarak, hemen hemen, tam değil de tama yakın. <b>Aşağı yukarı on kilo gelir bu yük. </b>'),
(266,	'Aşık atmak',	'Birisiyle yarışmak, özellikle kendisinden üstün birisiyle yarış etmek. <b>Sen benimle aşık atacak biri değilsin. </b>'),
(267,	'Ata et, ite ot vermek (yedirmek)',	'Uygunsuz iş yapmak; birbirini tamamlayan, birbirine uyan unsurları ters kullanmak; kişilere işlerine yaramayan şeyi, ilgili olmadıkları görevi vermek. <b>Ata et, ite ot verilen bir ülkede dirlik düzenlik mi olurmuş?</b>'),
(268,	'Ateş almak',	'1. Yanmak, tutuşmak. <br />2. Ateşli silâhın patlaması. <br />3. Telâşlanmak, öfkelenmek, heyecanlanmak, coşmak. <b>Silâh birden ateş aldı. </b>'),
(269,	'Ateş bacayı sarmak',	'Bir iş ya da olay önüne geçilemez, tehlikeli bir durum almak. <b>Ateş bacayı sarmadan çabuk gidelim buradan!</b>'),
(270,	'Ateş basmak',	'Aşırı ölçüde sıkılmak, heyecanlanmak, utanmak sonucu vücutta sıcaklığın artması, yüzün kızarması. <b>O nadide, paha biçilmez vazoyu kırınca bedenini birden bire ateş bastı. </b>'),
(271,	'Ateşe atmak',	'Birini çok tehlikeli bir işe bile bile sokmak. <b>Hiç aldırmadan, biricik kızını o adamla evlendirip ateşe atamazsın değil mi?</b>'),
(272,	'Ateşe tutmak',	'1. Ateşli silâhla mermi atmak. <br />2. Bir şeyi ateşin üzerinde tutarak ısıtmak. <b>Zalim askerler zavallı köylüleri yaylım ateşine tuttular. </b>'),
(273,	'Ateşe vermek',	'1. Bir yeri bilerek yakıp yok etmek. <br />2. Aşırı ölçüde telâşlandırmak. <br />3. Bir toplumu, bir ülkeyi kargaşalık içine sürükleyerek yıkıma uğratmak. <b>Dış güçler yerli işbirlikçilerle anlaşarak ülkeyi ateşe verdiler. </b>'),
(274,	'Ateşine (nârına) yanmak',	'Birinin yüzünden büyük haksızlığa uğramak, zarar görmek. <b>Eğer bu malı satamazsam senin ateşine yanmış olacağım. </b>'),
(275,	'Ateş kesilmek',	'1. Çok kızgın, öfkeli davranışlar göstermek. <br />2. Çok çalışkan, hareketli ve becerikli olmak. <br />3. Ateşli silâhlarla yapılan atışa son vermek. <b>Taraflar ateş kesilmesine razı olmadılar. </b>'),
(276,	'Ateşle oynamak',	'Çok tehlikeli, zarar verecek bir işin üstüne üstüne gitmek ya da böyle bir işe girişmek. <b>Bırak o silâhı elinden! Ateşle oynadığının farkında mısın sen?</b>'),
(277,	'Ateş pahasına',	'Çok pahalı. <b>Yeni daireler ateş pahası, nasıl alacağız?</b>'),
(278,	'Ateş püskürmek',	'Çok öfkeli olmak, ağır sözler söylemek. <b>Öğretmen kapıyı kıran öğrencilere ateş püskürdü. </b>'),
(279,	'Ateşten gömlek',	'İçinde bulunulan acı, sıkıntılı, dayanılmaz durumu anlatmak için söylenir. <b>İflas etmem, ateşten gömlek giymem demektir. </b>'),
(280,	'Atı alan Üsküdar`ı geçti',	'<b>Fırsat kaçtı, artık yapılacak şey kalmadı</b> anlamında kullanılır. <b>Sen daha dur, atı alan Üsküdar`ı çoktan geçti. </b>'),
(281,	'Atı eşkin, kılıcı keskin',	'Her bakımdan güçlü, dilediğini yapabilir. <b>Zalimlere karşı durmak mı istiyorsun? Atın eşkin, kılıcın keskin olmalı!</b>'),
(282,	'Atın yüğrükse bin de kaç',	'İmkânın varsa kendini kurtarmaya bak.'),
(283,	'Atıp tutmak',	'1. Kendi gücünü aşacağı işler yapacağını söylemek, abartılı konuşmak. <br />2. Birisinin arkasından ileri geri konuşmak, kötü sözler etmek. <b>Yüzüne karşı söyle, arkasından atıp tutma adamın. </b>'),
(284,	'At oynatmak',	'1. Ata hüner göstermek. <br />2. Bildiği ve istediği gibi davranmak. <br />3. Belli bir alanda üstünlük kurmak. <b>Meydan adamlara kaldı, istedikleri gibi at oynatıyorlar. </b>'),
(285,	'Atsan atılmaz, satsan satılmaz',	'İşe yaramadığı, sıkıntı verdiği hâlde vazgeçilemeyen şeyler ve kimseler için kullanılır. <b>Ne yapayım, kardeş işte! Atsan atılmaz, satsan satılmaz!</b>'),
(286,	'Attan inip eşeğe binmek',	'Bulunduğu dereceden, mevkiden, önemli görevden daha aşağı bir yere inmek veya alınmak. <b>Aklını başına toplamazsan adamı işte böyle attan indirip eşeğe bindirirler. </b>'),
(287,	'Avaz avaz bağırmak',	'Olanca gücüyle bağırmak; sesi yettiği kadar, var gücüyle bağırmak. <b>Tamam duyuyorum, öyle avaz avaz bağırma!</b>'),
(288,	'Avucunun içine almak',	'Birini her dediğini yapar duruma getirmek, baskı ve etkisi altına almak. <b>Kaymakam bütün kasabalıyı avucunun içine aldı. </b>'),
(289,	'Avucunu yalamak',	'Umduğunu ele geçirememek, beklediğini elde edememek. <b>Avucunu yalamak istemiyorsan harekete geç, sen de çalış. </b>'),
(290,	'Avuç açmak',	'Yardım istemek, dilenmek, para istemek ya da ister duruma düşmek. <b>Yarın avuç açmamak için bugünden çalışmalısın. </b>'),
(291,	'Ayağa düşmek',	'1. Bir şeyin değerini kaybetmesi. <br />2. Yalvarır duruma gelmek. <br />3. İşe ilgisiz ve yetkisiz kimseler karışır olmak. <b>Sevinmeyin boşuna, bu işi ayağa düşürmeyeceğim hiçbir zaman. </b>'),
(292,	'Ayağa kalkmak',	'1. Hasta iyi olmak. <br />2. Saygı göstermek için oturma durumundan ayak üzeri duruma geçmek. <br />3. Telâşlanmak, heyecanlanmak. <br />4. Dikilmek, ayakları üzerinde durmak. <b>Dedem nihayet ayağa kalktı. </b>'),
(293,	'Ayağı (ayakları birbirine) dolaşmak',	'Yürürken herhangi bir sebepten ötürü ayakları birbirine takılmak, sendelemek. <b>Korkusundan zavallının ayakları birbirine dolaştı. </b>'),
(294,	'Ayağı düşmek',	'Bir yere uğramak, o yer yolu üzerinde bulunmak, yolu düşmek. <b>Bu rezillikten sonra onun ayağının buralara düşeceğini sanmam artık. </b>'),
(295,	'Ayağı düze basmak',	'İşleri iyi gitmek, zorlukları yenerek rahata kavuşmak. <b>Şu borcu da ödedik mi ayağımız düze basacak inşallah. </b>'),
(296,	'Ayağı ile gelmek',	'1. Kendi isteği ile gelmek. <br />2. Çok fazla emek sarf edilmeden elde edilmek. <b>Adam ayağı ile geldi dayak yemeye. </b>'),
(297,	'Ayağına bağ olmak',	'Bir işini yapmasına, bulunduğu yerden ayrılmasına engel olmak. <b>Bu çocuk ayağıma bağ oldu, onu bırakıp da bir yere gidemiyorum. </b>'),
(298,	'Ayağına dolaşmak (veya dolanmak)',	'1. Birisinin yaptığı işe engel olmak. <br />2. Başkasına yaptığı kötülük kendi başına gelmek. <b>Şu köpeği birisi çıkarsın atölyeden, insanın ayaklarına dolanıyor. </b>'),
(299,	'Ayağına gitmek',	'Büyüklük taslamadan alçak gönüllülük edip birinin yanına varmak. <b>O baban senin, ayağına gitmelisin. </b>'),
(300,	'Ayağına kapanmak',	'Kendini küçük düşürerek yalvarıp yakarmak. <b>İnsan ne birisinin ayağına kapanmalı, ne de birisini ayağına kapandırmalı. </b>'),
(301,	'Ayağına (ayaklarına) kara su inmek',	'Bir yerde ayakta beklemekten veya uzun süre dolaşmaktan çok yorulmak. <b>Seni aramaktan ayaklarıma kara sular indi, nerelerdeydin Allah aşkına!</b>'),
(302,	'Ayağını çekmek',	'Daha önce gittiği yere artık uğramaz olmak, ilişkiyi ve ilgiyi kesmek. <b>Artık onlardan elimi ayağımı çektim. </b>'),
(303,	'Ayağını denk almak',	'Birilerinin kendisine karşı yapacakları muhtemel kötülüklere karşı uyanık davranmak, tedbirli olmak. <b>Eğer ayağını denk almazsan o adamlar başına bir iş açacaklar senin. </b>'),
(304,	'Ayağını kaydırmak',	'Bir yolunu bularak birini bulunduğu işten, mevkiden uzaklaştırmak. <b>Adamcağızın hiç suçu yokken ayağını kaydırdılar, şimdi aç susuz dolaşıyor. </b>'),
(305,	'Ayağını kesmek',	'1. Bir yere gitmez, uğramaz olmak. <br />2. Birini bir yere artık uğramaz duruma getirmek. <b>Öyle korkutun ki o adamın ayağı kesilsin bu meyhaneden?</b>'),
(306,	'Ayağının altına almak',	'1. Acımasızca, tekmelerle kıyasıya dövmek. <br />2. Bir şeyi küçük görerek ondan faydalanma yoluna gitmemek, o şeyi tepmek. <b>Önüne serilen bütün nimetleri ayağının altına aldı hiç tınmadan. </b>'),
(307,	'Ayağının tozuyla',	'Henüz dinlenmeden, yoldan gelir gelmez. <b>Adamı ayağının tozuyla kodese tıktılar. </b>'),
(308,	'Ayağını sürümek',	'1. Verilen bir görevi ağırdan yapmak. <br />2. Bir yerden ayrılmak üzere bulunmak. <br />3. Ölmek üzere olmak. <br />4. Halk inanışına göre birinin gelmesi, ardından başkalarının da gelmesine yol açmak. <b>Ayağını mı sürüdün, ne çok gelen oldu?</b>'),
(309,	'Ayağını yorganına göre uzatmak',	'Gelirini giderine uydurmak, harcamalarda geliri aşmamak. <b>Ayağını yorganına göre uzatmazsan ileride aç kalırsın. </b>'),
(310,	'Ayağı (ayakları) suya ermek (değmek)',	'Neden sonra aklı başına gelmek, bir şeyin aslını anlamak, beklenen biçimde olmadığını kavramak. <b>Toy olduğu için doğruyu göremiyor, onun da ayağı suya erecek bir gün. </b>'),
(311,	'Ayak altında kalmak',	'1. Hor görülüp aşağılanmak, değer verilmemek. <br />2. İnsanların sık gelip geçtiği yerde, kalabalık içinde kalmak. <b>Seyyar satıcıların pek çoğu ayak altında kalınacak bir yeri seçerler. </b>'),
(312,	'Ayak atmamak',	'Bir yere hiç gitmemek. <b>O kente ayak atmadım henüz. </b>'),
(313,	'Ayak diremek',	'Bir şeyde ısrar etmek, karşı koymak, kendi kararından vazgeçmemek. <b>Ayak diremeseydi çoktan evini yıkmış olacaklardı. </b>'),
(314,	'Ayaklar altına almak',	'Önem verilmesi gereken şeyleri hiçe saymak, çiğnemek. <b>Babasının onun için verdiği emekleri ayaklar altına alarak o serseriliği seçti. </b>'),
(315,	'Ayakları geri geri gitmek',	'Bir yere istemeye istemeye, gönülsüz gitmek. <b>Hoşlanmadığım bu insanların yanına yaklaştıkça ayaklarım geri geri gitmeye başladı. </b>'),
(316,	'Ayaklı kütüphane',	'Çok şey okumuş, her sorulana cevap veren, çok şey bilen, okudukları aklında kalmış kimse. <b>Adam ayaklı kütüphaneydi sanki!</b>'),
(317,	'Ayakta kalmak',	'1. Bir zorluk karşısında yıkılmamak, çökmemek. <br />2. Oturacak yer bulamamak. <b>Gemi öyle kalabalıktı ki hepimiz ayakta kaldık. </b>'),
(318,	'Ayak takımı',	'İşe yaramaz, bilgisiz, görgüsüz, kaba, serseri, değersiz kimselerin bütünü. <b>Mahallemizde ayak takımı gittikçe çoğalıyor. </b>'),
(319,	'Ayak uydurmak',	'1. Adımlarını başkasınınkine uydurmak. <br />2. Kendi gidiş ve davranışını başkasınınkine benzetmek. <b>Bu bozuk topluma ayak uydurmak zorunda değiliz. </b>'),
(320,	'Ayak üstü (üzeri)',	'1. Kısa süre içinde, acele olarak. <br />2. Ayakta durarak, ayakta dikilerek. <b>Gel de şu büfede ayak üstü atıştıralım biraz. </b>'),
(321,	'Ayasofya`da dilenip Sultanahmet`te sadaka (zekât) vermek',	'Kendisi başkasının yardımı ile geçinirken, gösteriş için elindekini başkalarına yardım amacıyla dağıtmak.'),
(322,	'Ayıkla pirincin taşını',	'Bir işin oldukça karışık, dolaşık, içinden çıkılması güç olduğunu anlatmak için kullanılır. <b>Durup dururken adama olmadık sözler söylemiş, şimdi ayıkla pirincin taşını!</b>'),
(323,	'Ayılıp bayılmak',	'1. Sinir krizi geçirmek, bunalıma düşmek. <br />2. Birini kendinden geçercesine sevmek, beğenmek. <b>Her kan görüşünde ayılıp bayılıyor. </b>'),
(324,	'Ayranı kabarmak',	'Öfkelenmek, kızıp bağırmak; coşmak. <b>O konuştukça adamın elleri titriyor, ayranı kabardıkça kabarıyordu. </b>'),
(325,	'Ayvaz kasap hep bir hesap',	'<b>Ha öyle ha böyle, ikisi de bir; hangi yolu seçersek seçelim aynı sonuca varır</b> anlamında kullanılır.'),
(326,	'Ayyuka çıkmak',	'1. Pek yükselmek (ses için). <br />2. Herkesçe duyulmak, yayılmak (dedikodu için). <b>Öyle kızgındı ki sesi ayyuka çıkıyordu. </b>'),
(327,	'Aza çoğa bakmamak',	'Eline geçenle yetinmek, tok gözlü olmak.'),
(328,	'Azizlik etmek',	'Şaka ile takılmak, muziplik etmek, şaka ile aldatmak. <b>Osman azizlik etmeye bayılır. </b>'),
(329,	'Baba adam',	'Ağır başlı, iyi yürekli, olgun, hoşgörülü, yaşlıca adam. <b>Ne baba adammış meğer, ailesinden değil, komşularından bile kimseyi ihmal etmedi. </b>'),
(330,	'Babası tutmak (veya babaları üstünde olmak)',	'Çok fazla öfkelenmek, kızgınlığı her hâliyle belli olmak. <b>İş meselesini konuşamadım, çünkü babaları üstündeydi odasına girdiğimde. </b>'),
(331,	'Babana rahmet',	'<b>Yaptığın iş, söylediğin söz çok yerinde; Allah senden razı olsun</b> anlamında hoşnutluk, memnunluk bildirmek için kullanılır.'),
(332,	'Baba ocağı (evi veya yurdu)',	'Dededen, babadan kalma ev; toprak, yurt. <b>Borçları yüzünden baba evini satmak zorunda kaldı. </b>'),
(333,	'Babasının hayrına (mı?)',	'Hiçbir çıkar gözetmeksizin. <b>Babasının hayrına mı yaptı sanıyorsun senin işini?</b>'),
(334,	'Bağ bozmak (bağbozumu)',	'1. Bağda son kalan ürünün toplanması. <br />2. Bu işlerin yapıldığı mevsim (güz), gün. <b>Bağbozumu besmele ile başlarsa bereketli olur. </b>'),
(335,	'Bağrına basmak',	'1. Kucaklamak, kolları ile sararak göğsüne yaslamak. <br />2. Birini gözetip kayırmak, koruyup yetiştirmek. <b>Amcası, yeğenini bağrına basmakta geçikmedi. </b>'),
(336,	'Bağrına taş basmak',	'Uğradığı zarara, felakate sesini çıkarmadan katlanmak. <b>Evi yıkılan Hasan bağrına taş basmaktan başka bir yol bulamadı. </b>'),
(337,	'Bağrını delmek',	'İçine işlemek, pek dokunmak, dertli olmasına yol açmak. <b>Yurdundan kovulması, şairin bağrını deldi. </b>'),
(338,	'Bağrı yanık',	'Çok acı çekmiş; dert, sıkıntı, darlık, kahır görmüş; yaslı. <b>Nice bağrı yanık insanlar yaşamış bu topraklarda. </b>'),
(339,	'Bahse girmek',	'Görüşünde veya iddiasında haklı çıkacak tarafa bir şey verilmesini kabul eden sözlü anlaşma yapmak. <b>Erken kalkmak konusunda onunla bahse girdik. </b>'),
(340,	'Bahtı kara',	'Mutsuz, dertten kurtulamayan, işleri hep ters giden. <b>Allahım, şu bahtı kara kuluna yardım et de düzlüğe çıksın!</b>'),
(341,	'Baklayı ağzından çıkarmak',	'Sabrı tükenip o zamana kadar sakladığı şeyleri söylemek. <b>Yeter artık, çıkar ağzından şu baklayı!</b>'),
(342,	'Bal alacak çiçeği bilmek',	'Çıkar sağlanacak yeri veya şeyi bulmak, bu konuda nasıl hareket edileceğini bilmek. <b>Onun bal alacak çiçeği bilmede üstüne yoktur. </b>'),
(343,	'Baldırı çıplak',	'İşsiz güçsüz, serseri, başı boş, ayak takımından. <b>Sokaklar baldırı çıplaklardan geçilmiyor. </b>'),
(344,	'Bal dök (de) yala',	'Bir yerin çok temiz, pırıl pırıl olduğunu anlatmak için kullanılır. <b>Odayı öyle elden geçirmiş ki bal dök de yala!</b>'),
(345,	'Balgam atmak',	'Bir iş ya da konu üzerinde kuşku uyandıracak söz söylemek. <b>Lütfen sus, ortaya bir balgam atıp da insanı huzursuz etme. </b>'),
(346,	'Bal gibi',	'1. Çok tatlı. <br />2. Çok iyi, adamakıllı, pekâlâ. <b>Bal gibi iş, daha ne duruyorsun?</b>'),
(347,	'Balık etinde',	'Ne şişman, ne zayıf; biçimli, kilosu yerinde olan.'),
(348,	'Balık istifi',	'Çok sıkışık bir durumda. <b>Otobüs, balık istifi gibi yerleşmiş insanları zor taşıyordu. </b>'),
(349,	'Balık kavağa çıkınca',	'Gerçekleşmesi mümkün olmayacak işleri anlatmak için kullanılır. <b>O kız, o çocukla ancak balık kavağa çıkınca evlenir. </b>'),
(350,	'Balon uçurmak',	'İlgililerin ne diyeceklerini anlamak veya insanların telâşlanmalarını sağlamak amacıyla aslı olmayan bir haber yaymak. <b>Askerliğin kısalmasıyla ilgili bir balon uçurdu, buna sonra kendisi de inanmaya başladı. </b>'),
(351,	'Balta olmak',	'Musallat olmak, asılmak, direnerek bir şey istemek, istediğini yaptırmak için sürekli ısrar etmek. <b>İnsanın başına balta olan kişileri sevmek mümkün değil. </b>'),
(352,	'Baltayı taşa vurmak',	'Bilmeyerek karşısındakini kıracak söz söylemek, pot kırmak. <b>Baltayı taşa vurunca öyle utandı ki sormayın gitsin. </b>'),
(353,	'Bam teline basmak',	'Bir kimseyi, duyarlılık gösterdiği konuda kızdıracak söz söylemek, öfkelendirecek bir şey yapmak. <b>Bir insanı delirtmek mi istiyorsun? Onun bam teline basacaksın. </b>'),
(354,	'Bana mısın dememek',	'Aldırış etmemek, ona hiçbir şey etkili olmamak. <b>Sırtına o kadar yük vurdular, adam yine de bana mısın demedi. </b>'),
(355,	'Barut fıçısı',	'Her an karışıklık, kavga ve savaşın çıkacağı yer. <b>Nereden çıktığı belli olmayan bir ses, meydanı bir anda barut fıçısına döndürdü. </b>'),
(356,	'Barut kesilmek',	'Çok öfkelenmek, kızmak, sinirlenmek. <b>Elektriği bağlanmayan adam barut kesilmiş, etrafa bağırıp duruyordu. </b>'),
(357,	'Basıp gitmek',	'Aklına koyduğu şeyi yapmak amacıyla, o an bulunduğu yerden kimseye danışmadan ayrılmak. <b>Öyle her aklına estiğinde basıp gidemezsin buradan. </b>'),
(358,	'Basireti bağlanmak',	'Gerçeği göremez, iyi düşünüp kavrayamaz bir duruma düşmek. <b>Öylece kalakaldım, ne yapacağımı bilemiyorum, basiretim bağlandı âdeta. </b>'),
(359,	'Baskın çıkmak',	'Üstünlüğünü göstermek, karşısındakini geçmek. <b>Koşuda değil, ancak güreşte baskın çıkarım ona. </b>'),
(360,	'Bastığı yeri bilmemek',	'1. Çok fazla sevinmek. <br />2. Dengesiz hareketlerde bulunmak, durumunu kontrol edememek, şaşkınlıktan nerede olduğunu bilememek. <b>Eşinin ölümünden sonra bastığı yeri bilmez bir adam oldu. </b>'),
(361,	'Baston (kazık) yutmuş gibi',	'Dimdik duran, yürüyen kimsenin durumu. <b>Baston yutmuş gibi ortalıkta dolaşıp da asabımı bozma!</b>'),
(362,	'Başa baş (gelmek)',	'Birbirine denk, eşit olmak; birlikte olmak. <b>Takımlar başa baş bir mücadele verdiler. </b>'),
(364,	'Başa çıkmak',	'Gücünün üstünlüğünü kanıtlamak, bir şeye gücü yetmek. <b>Onunla başa çıkabilirim, merak etme sen. </b>'),
(365,	'Başa geçmek',	'1. En üstün yeri almak. <br />2. Herhangi bir konu önemce ilk sırayı almak. <b>Ülkede ekonomik yolsuzluklar başa geçti. </b>'),
(366,	'Başa gelmek',	'Kötü bir duruma uğramak. <b>Kim demiş başa gelen çekilir diye?</b>'),
(367,	'Başa güreşmek',	'1. Yağlı güreşte başpehlivanlık için güreşmek. <br />2. En üstün sonucu almak için mücadele etmek, yarışmada birinciliği almak için uğraşmak. <b>Takımımız öteden beri başa güreşir. </b>'),
(368,	'Baş ağrısı',	'Varlığı tedirginlik verici şey, rahatsız edici kimse. <b>Sen ne baş ağrısı bir adammışsın meğer!</b>'),
(369,	'Baş ağrıtmak',	'Yerli yersiz konuşarak, gereksiz sözler söyleyerek, çok konuşarak birisini rahatsız etmek. <b>Baş ağrıtmakta üstüne yoktur senin. </b>'),
(370,	'Başa (başına) kakmak',	'Yapılan iyiliği yüzüne vurarak birisini üzmek, incitmek. <b>Üç kuruş verdi, üç gün geçmeden başına kaktı. </b>'),
(371,	'Baş alamamak',	'Çok uğraştıran bir konudan kurtulup da vakit ve fırsat bulamamak. <b>Şu çocuklarla uğraşmaktan baş alamıyorum ki sana geleyim. </b>'),
(372,	'Baş aşağı gitmek',	'Sürekli kötüleşmek, zarar görmek. <b>Baş aşağı giden işlerinin önünü alamadı bir türlü. </b>'),
(373,	'Baş başa kalmak',	'Biriyle yalnız kalmak, iki kişi bir arada yalnız kalmak. <b>Misafirler gittikten sonra baş başa kaldılar. </b>'),
(374,	'Baş başa (kafa kafaya) vermek',	'Birbirinin düşüncesinden yararlanmak üzere birkaç kişi toplanıp bir konuyu görüşmek, bir konuda dertleşmek. <b>Bu sorunu ancak baş başa vermekle çözebiliriz. </b>'),
(375,	'Baş belâsı',	'Sürekli rahatsız eden, yük olan, bir kimseye musallat olup sıkıntı veren ve uzaklaştırılamayan kişi ya da şey. <b>Şu baş belâsı adamı uzaklaştırırsanız sevindirirsiniz beni. </b>'),
(376,	'Baş çekmek',	'Ön ayak olmak, öncülük etmek. <b>Hayatı boyunca baş çeken bir adam olarak yaşadı. </b>'),
(377,	'Baş edememek',	'Gücü yetmemek, başarı kazanamamak, bir işi başarmakta zorluk çekmek. <b>Şu uysal insanlarla baş edemezsen kiminle edeceksin!</b>'),
(378,	'Baş eğmek',	'Direnmekte vazgeçip güçlünün buyruğuna girmek, teslim olmak. <b>Türk milletine baş eğdiremezsin. </b>'),
(379,	'Baş göstermek',	'Ortaya çıkmak, belirmek, vuku bulmak. <b>Milletimiz baş gösteren bu yeni fikri kısa zamanda benimseyecektir. </b>'),
(380,	'Baş göz etmek',	'Evlendirmek. <b>Şu kızı da bir baş göz edersem gözüm arkada kalmayacak. </b>'),
(381,	'Başı ağrımak',	'Bir işten dolayı sorumlu duruma düşmek, kaygu çekmek. <b>Sana güveniyorum, başımı ağrıtmayacağına eminim, haydi güle güle git. </b>'),
(382,	'Başı altından çıkmak',	'Kötü bir şey, kötü bir durum, birinin gizli düzeni ve tertibiyle meydana gelmek. <b>Böyle şeyler bilirim ki senin başının altından çıkar, şimdi bana doğruyu söyle, kim kırdı vazoyu. </b>'),
(383,	'Başı bağlı olmak',	'1. Evli ya da nişanlı olmak. <br />2. Serbest, özgür olmayan, bir yere bağımlı olan. <b>Nihayet oğlanın da başını bağladık. </b>'),
(384,	'Başı boş bırakmak',	'Bir kimsenin üzerindeki denetimi ve gözetimi kaldırmak, kendi bildiğine bırakmak. <b>Çocuk dediğin başı boş bırakılmaya gelmez. </b>'),
(385,	'Başı darda kalmak (başı dara düşmek)',	'Çok sıkıntılı, çaresiz bir durumda olmak; parasızlıktan dolayı güç bir durumda kalmak. <b>Başı darda kalan insanlara yardım etmek insanlık borcudur. </b>'),
(386,	'Başı derde girmek',	'Can sıkıcı, üzücü, istemediği bir duruma düşmek. <b>Şu kendini bilmez adamla başım derde girsin istemiyorum. </b>'),
(387,	'Başı dik gezmek',	'Utanılacak bir durumu olmadan, onurlu şekilde toplumda yer almak. <b>Başı dik gezen insanları sevmemek elde değil. </b>'),
(388,	'Başı dönmek',	'1. Bir şey karşısında şaşırmak. <br />2. Sıkıntı meydana getiren bir durum karşısında bunalmak. <br />3. Dengesini yitirmek, gözleri kararmak; çevresi kararıyor, dönüyor, kayıyor duygusu içinde sarsılmak. <b>Arabayı durdurun, başım dönmeye başladı. </b>'),
(389,	'Başı göğe ermek',	'Beklenmeyen, umulmayan bir mutluluğa, sevince ulaşmak. <b>Üç kuruş zam yapıldı diye maaşına, başı göğe erdi sanıyor; bilmiyor ki enflasyon bir ay sonra alacak o zammı elinden. </b>'),
(390,	'Başı kalabalık (olmak)',	'Bir iş dolayısıyla yanında çok fazla kişi olmak. <b>Kusura bakma, başım kalabalıktı bugün, seni arayamadım. </b>'),
(391,	'Başına belâyı satın almak',	'Sıkıntı, üzüntü ve tedirginlik verici olduğunu sonradan anladığı bir işe kendi isteği ile girmiş bulunmak. <b>Nereden girdim bu inşaat işine, durup dururken başıma belâyı satın aldım. </b>'),
(392,	'Başına bir hâl gelmek',	'Büyük, içinden çıkılması zor güçlüklerle karşılaşmak; kötü duruma düşmek. <b>Gece gitme, başına bir hâl gelir diye korkuyorum. </b>'),
(393,	'Başına buyruk',	'Dilediğini izin almaksızın yapan, istediği gibi davranan. <b>Sizin çocuk da amma başına buyruk bir çocuk olmuş. </b>'),
(394,	'Başına çalmak',	'Bir şeyi sert, öfkeli ve kızgın bir davranış içinde vermek. <b>Al da başına çal bu sapı kırık küreği. </b>'),
(395,	'Başına çorap örmek',	'Bir kimseye, haberi olmadan, kötü duruma sokucu davranışta bulunmak, alt etmek için gizlice plân kurmak. <b>Onun başına bir çorap örecekler diye korkuyorum. </b>'),
(396,	'Başına çökmek',	'1. İştahla sofraya oturmak. <br />2. Bir işi çabuk bitirmek üzere oturup ele almak. <br />3. Birini altına alıp dövmek. <b>Birkaç kişi utanmadan zavallı adamın başına çöktüler. </b>'),
(397,	'Başına devlet kuşu konmak',	'Ummadığı, beklemediği bir nimete ya da varlığa kavuşmak. <b>Nasıl aldı bu köşkü? Başına devlet kuşu mu kondu dersin?</b>'),
(398,	'Başına dolamak',	'İçinden çıkılması zor bir işi birine musallat etmek. <b>Bu işi benim başıma dolayanlar, dilerim hiçbir zaman onmazlar!</b>'),
(399,	'Başına iş açmak',	'Uğraştırıcı ve üzücü bir işin çıkmasına yol açmak. <b>Bırak o bıçağı elinden, hiç yoktan başına iş açacaksın. </b>'),
(400,	'Başında kavak yeli esmek',	'1. Sorumluluk duygusundan uzak, zevk ve eğlence peşinde koşmak (genç için). <br />2. Gerçekleşmeyecek şeyler düşünerek vakit geçirmek. <b>Bu çocuk da büyümedi bir türlü, hâlâ başında kavak yelleri esiyor. </b>'),
(401,	'Başından atmak',	'1. Gereksiz görülen bir bağlılığa, bir ilişkiye son vermemek; bir istekte bulunan kişiyi yanından uzaklaştırmak. <br />2. Yapılması zor bir işi yapmaktan kendini kurtarmak ya da o işi bir başkasına yüklemek. <b>Sonunda o işi başından atmasını becerdi.</b>'),
(402,	'Başından aşağı kaynar sular dökülmek',	'Çok kötü, üzücü, sıkıntı verici ya da utandırıcı bir olay karşısında vücudunu ter basmak, ürpermek. <b>Babasını karşısında görünce başından aşağı kaynar sular döküldü. </b>'),
(403,	'Başından büyük işlere girişmek (veya kalkışmak)',	'Gücünün üstünde olan işleri yapmaya kalkışmak. <b>Çekil lütfen, başından büyük işlere kalkışıp da kendini rezil etme bari. </b>'),
(404,	'Başından korkmak',	'Hayatından kaygı duymak, cezalandırılmaktan korkmak. <b>Düşman topraklarına girince başından korkmaya başladı. </b>'),
(405,	'Başını ağrıtmak',	'1. Gereksiz sözlerle birini bunaltmak. <br />2. Bir iş için birini uğraştırmak, sıkmak. <b>Yeter artık, bu iş için başımı ağrıtıp durma. </b>'),
(406,	'Başını alıp gitmek',	'Nereye gideceğini bildirmeden, izin almadan gitmek. <b>İçine düştüğü sıkıntıdan kurtulamayan adam başını alıp gitti. </b>'),
(407,	'Başını bağlamak',	'Evlendirmek. <b>Askerliği biten Ali`nin başını bağlamayı düşünen annesi kolları hemen sıvadı. </b>'),
(408,	'Başını belâya sokmak',	'Bir kimseyi, zarar göreceği, kötü sonuçlarla karşılaşacağı bir işe sokmak. <b>Oğlanın da başını belâya sokacaklar diye ödüm kopuyor. </b>'),
(409,	'Başını bir yere bağlamak',	'Bir işe yerleştirmek, işsizlikten kurtarmak. <b>Çok geçmeden oğlunun da başını bir yere bağlamayı becerdi. </b>'),
(410,	'Başını boş bırakmak',	'Denetimsiz, yalnız ve serbest bırakmak. <b>Bu çocuğun başını boş bırakma, yoksa başı belâya girecek. </b>'),
(411,	'Başını derde sokmak',	'Sıkıcı, yorucu, üzücü bir işe girmek veya getirilmek. <b>Tanımadığı adamlarla işe girişince başını derde soktu. </b>'),
(412,	'Başını dinlemek',	'Sessiz, sakin bir ortama çekilmek; kalabalıktan ve gürültüden uzaklaşmak. <b>Emekli olur olmaz başımı dinleyecek bir köşe arayacağım</b>'),
(413,	'Başını ezmek',	'Birini hareket edemez, kötülük yapamaz ya da başını kaldırıp bir işi göremez duruma getirmek. <b>Zalimlerin başını ezecek adamlara bugün ne kadar ihtiyaç var!</b>'),
(414,	'Başını kaşımaya (kaşıyacak) vakti olmamak',	'Çok meşgul olmak, başka bir işi yapmaya hiç vakti olmamak. <b>Bana yükleme o işi, çünkü başımı kaşıyacak vaktim yok. </b>'),
(415,	'Başının çaresine bakmak',	'Kimsenin yardımı olmadan kendi işini kendi yapmak, kendini zor durumdan kurtarmak. <b>Benden sana fayda yok, başının çaresine baksan iyi olacak. </b>'),
(416,	'Başının derdine düşmek',	'Başka bir şeyle ilgilenemeyecek kadar sıkıntılı, üzücü ve tehlikeli bir duruma çare bulmaya çalışmak. <b>Adamın bize aldıracağı yok, baksana başının derdine düşmüş. </b>'),
(417,	'Başının etini yemek',	'Sürekli olarak, bıktırıncaya kadar, ısrarla birinden bir şey istemek; bu sebeple onu rahatsız edip üzmek. <b>Tamam kızım, alacağız o oyuncağı, yeter başımın etini yediğin!</b>'),
(418,	'Başını taştan taşa vurmak',	'Fırsatı kaçırdığı için çok pişman olmak, çaresiz kalarak kahırlanmak. <b>Zamanında eve gidip hasta çocuğu doktora götürmediği için başını taştan taşa vuruyordu. </b>'),
(419,	'Başını vermek',	'Bir ideal uğrunda kendini feda etmek, canını vermek. <b>Yiğitler başını vermesiydi bu ülke düşmanlardan kurtulur muydu?</b>'),
(420,	'Başını yemek',	'Bir kimsenin büyük zarar görmesine ya da ölmesine yol açmak. <b>Ruhsuz herifler adamın başını yemek için yarışa giriştiler. </b>'),
(421,	'Başı sıkışmak (sıkılmak)',	'Herhangi bir güçlük karşısında kalmak, bunalmak. <b>Onun görevi, başı sıkışan insanlara yardım etmektir. </b>'),
(422,	'Başı tutmak',	'1. Önde olmak. <br />2. Gürültüden, üzüntüden ve çok konuşmadan başı ağrımak. <b>Kesin artık şu dedikoduyu, yoksa başım tutacak!</b>'),
(423,	'Baş koymak',	'Bir şey uğruna ölümü göze almak. <b>Çekil önümden ben bu yola baş koydum. </b>'),
(424,	'Baş köşe',	'Saygı duyulan, önder sayılan büyüklerin oturması için ayrılan yer. <b>Baş köşeye oturmak onun her zaman hakkıdır. </b>'),
(425,	'Baş sallamak',	'Anlasa da anlamasa da karşısındakinin her sözünü uygun bulur görünmek. <b>Her şeye baş sallayan insanlardan hiç hoşlanmam. </b>'),
(426,	'Baş tacı etmek',	'Değer vermek, çok üstün tutmak, çok sevmek. <b>Babalarını baş tacı ettiler, toz kondurmuyorlar adama. </b>'),
(427,	'Baştan aşağı',	'Tamamiyle, hepsi, bütünüyle. <b>Evi baştan aşağı boyadılar. </b>'),
(428,	'Baştan kara gitmek',	'Sonunu düşünmeyerek, hatta sonucun kötü olduğunu bildiği hâlde hesapsız, batarcasına bir yol tutmak; felâkete doğru gitmek. <b>Bu baştan kara gittiğin hayata artık bir son vermelisin. </b>'),
(429,	'Baştan savma',	'Üstün körü, özen gösterilmeden, gelişi güzel. <b>Yaptığın işin tamamen baştan savma olduğu ne kadar açık. </b>'),
(430,	'Baş üstünde yeri var',	'<b>Sevgi, ilgi ve saygı ile karşılanıp ağırlanır. </b> anlamında kullanılır. <b>Durmasın gelsin, baş üstünde yeri var. </b>'),
(431,	'Baş vermek',	'1. İnandığı bir şey uğrunda ölmek, canını vermek. <br />2. Belirmek, kimi bitkilerin başak tutmaya başlaması. <b>Ektiğimiz buğdaylar baş vermeye başladı. </b>'),
(432,	'Baş vurmak',	'1. Müracaat etmek, bir işin yapılmasını bir kimse veya kuruluştan istemek. <br />2. Bilgi edinmek üzere bir kaynağa bakmak, bir kimseye danışmak. <b>Vakit geçirmeden ansiklopediye bakalım da öğrenelim. </b>'),
(433,	'Baş yemek',	'1. Sofrada en önemli yemek. <br />2. Birinin ölümüne sebep olmak. <br />3. Birinin herhangi bir işte güç durumda kalmasına yol açmak. <b>Adamın başını sebepsiz yere yediler, şimdi çoluk çocuk aç kalacak. </b>'),
(434,	'Battı balık yan gider',	'<b>İşlerin kötü gittiğine, düzelmeyeceğine, bu konuda da umut kalmadığına göre artık istenildiği gibi davranılabilir, ne olursa olsun</b> anlamında kullanılır. <b>Aldırma, üzülme artık, battı balık yan gider. </b>'),
(435,	'Bayrak açmak',	'1. Bir dava yolunda toplanmaya çağırmak. <br />2. Gönüllü asker toplamaya girişmek. <b>Düşmana karşı yurdun dört bir yanında bayrak açan yurtseverler sonunda amaçlarına ulaştılar. </b>'),
(436,	'Bayram etmek',	'Çok sevinmek. <b>Oyuncakları görünce çocuklar bayram etti. </b>'),
(437,	'Belâ aramak',	'Kavga çıkararak, önüne gelene çatarak ya da başka sebeplerle kendisi için tehlikeli bir durum oluşmasına yol açmak. <b>Bırak sövmeyi, belâ mı arıyorsun başına?</b>'),
(438,	'Belâsını bulmak',	'Kendi yol açtığı tehlikeli bir durumun içine düşmek, hak ettiği cezayı görmek. <b>Adam nihayet belâsını buldu. </b>'),
(439,	'Belâyı satın almak',	'Kendi davranışları yüzünden tehlikeyi üstüne çekmek. <b>Köylülerle biraz daha uğraşırsak belâyı satın alacağız, haydi gidelim buradan. </b>'),
(440,	'Bel bağlamak',	'Güvenmek, birisinin kendisine yardım edeceğine inanmak, inanıp arkasından gitmek. <b>İnsanoğluna bel bağlanılmaz. </b>'),
(441,	'Beli bükülmek',	'1. Yaşlılık yüzünden güçsüz kalmak, bir iş yapamaz duruma gelmek. <br />2. Üzüntü ve kederden ruhsal bir çöküntüye düşmek. <b>İflas eden şu genç adamın bir yılda beli büküldü. </b>'),
(442,	'Belini doğrultmak',	'Kötüye giden durumunu yeniden düzeltmek, güçlenmek, kaybettiği itibarını ve ekonomik gücünü yeniden kazanmak. <b>Adam kısa zamanda belini doğrulttu. </b>'),
(443,	'Belini kırmak',	'1. Birini bir şey yapamaz duruma getirmek. <br />2. Bir işin en güç tarafını yapmak. <b>Tarlanın ortasından şu tümseği de kaldırdık mı işin belini kırmış sayılırız, artık gerisi kolay olacaktır. </b>'),
(444,	'Bel vermek',	'(Dik şeylerin) dışarıya doğru, (yatay şeylerin de) aşağıya doğru kamburlaşmak. <b>Yeni ördüğümüz duvar bel verdi. </b>'),
(445,	'Ben hancı, sen yolcu (oldukça)',	'<b>Özel ilişkilerimiz sürüp gittikçe senin bana işin düşer</b> ya da <b>Nasıl olsa yine karşılaşacağız</b> anlamında kullanılır. <b>Demek şu küçük paketi götürmüyorsun, öyle olsun, ben hancı sen yolcu, bugünün yarını da vardır. </b>'),
(446,	'Benlik dâvası',	'Önde görünmek, her şeyde söz sahibi olmak, her şeyi kendi düşüncesine uydurmak, hep dediğini yaptırmak çabası ve tutkusu. <b>Benlik dâvası güden insanlar bir yere varamazlar. </b>'),
(447,	'Benzi atmak',	'Bir sebepten ötürü ansızın yüzünün rengi sararmak, solmak. <b>Askerleri karşısında görünce benzi attı. </b>'),
(448,	'Bereket versin',	'1. <b>Allah size bol kazanç versin</b> anlamında iyi dilek sözü. <br />2. Çok şükür ki iyi ki (hoşnutluk anlatır). <b>Bereket versin ki ona bir şey olmamış. </b>'),
(449,	'Beş aşağı beş yukarı',	'Çok az fark olarak, kararlaştırılmak istenen sayıdan, ölçüden bir miktar az veya çok olarak. <b>Beş aşağı beş yukarı bir kg. çeker bu tavuk. </b>'),
(450,	'Bet (i) bereket (i) kalmamak',	'Bolluğun, verimliliğin kalmaması, sona ermesi. <b>Yanımıza geldiği günden beri evin beti bereketi kalmadı. </b>'),
(451,	'Betine gitmek',	'Ayıp saymak, kötü karşılamak, kendisine yedirememek. <b>Senin yaptığın iş adamın çok betine gitti. </b>'),
(452,	'Beyin yıkamak',	'Bir insanı, kendine özgü düşünce ve dünya görüşüne yabancılaştırmak, başka yönlerde düşünür ve davranır duruma getirmek. <b>Batılılar ülke insanımızın beynini yıkamaya devam ediyorlar. </b>'),
(453,	'Beylik söz',	'Etkisi kalmamış, herkesin kullanageldiği söz. <b>Bırak artık şu beylik sözleri, kimseyi etkileyemiyorsun. </b>'),
(454,	'Beyni bulanmak',	'1. Sersemlemek, sağlıklı düşünemez olmak. <br />2. Kötü bir şey olacağını sezinleyip huzuru kaçmak. <b>Adamların suratlarını hiç beğenmedim, beynim bulandı, haydi gidelim buradan. </b>'),
(455,	'Beyninden vurulmuşa dönmek',	'Umulmadık, beklenmedik bir olay karşısında şaşkınlığa düşmek, düşünce yeteneğini yitirir gibi olmak. <b>Adamı karşısında görünce beyninden vurulmuşa döndü. </b>'),
(456,	'Beynine girmek',	'1. Akla uygun gelmek. <br />2. Bir kimseyi türlü yollara baş vurarak bir şey yapmaya inandırmak, kandırmak. <br />3. Ezberlemek, aklında tutmak. <b>Ne kadar okursam okuyayım beynime girmiyor. </b>'),
(457,	'Bıçak kemiğe dayanmak',	'Çekilen sıkıntı artık katlanamayacak bir hâl almak. <b>Bıçak kemiğe dayandı, artık bu yerde duramam. </b>'),
(458,	'Bıyığı terlemek',	'Bıyığı yeni yeni çıkmaya başlamak. <b>Bıyığı terlemiş gençlerin eline bakamam gayri. </b>'),
(459,	'Bıyık altından gülmek',	'Birinin içine düştüğü duruma belli etmeden gülmek, sevindiğini belli etmeyerek onunla eğlenmek, içinden onunla alay etmek. <b>Ayşe`nin kırdığı pot karşısında bıyık altından gülmeye başladı. </b>'),
(460,	'Bildiğini okumak',	'Kim ne derse desin, istediği gibi davranmak. <b>Bildiğini okumaya devam edersen, sonunda zarar görmen muhakkak olacak. </b>'),
(461,	'Bile bile lâdes',	'Bile bile aldınmış görünme, öyle gerektiği için kötü bir durumu kabullenme. <b>Ağaçları kesmesine bile bile lâdes dedim. </b>'),
(462,	'Bin dereden su getirmek',	'Birini kandırmak için dil dökmek, birçok sebep ileri sürmek, aldatıcı sözler sarf etmek. <b>O evi almamam için bin dereden su getirdiler. </b>'),
(463,	'Bindiği dalı kesmek',	'Kendisi için gerekli ve yararlı olan şeyi kendi eliyle yok etmek. <b>Geçimini sağladığın o tarlayı sakın satma, yoksa bindiğin dalı kesmiş olursun. </b>'),
(464,	'Bir atımlık barutu olmak (veya kalmak)',	'1. Bir konuda yapacağı çok az şeyi olmak. <br />2. Dayanacak pek az gücü kalmak. <b>Bir atımlık barutu kalmış, hâlâ ben yaparım o işi diyor. </b>'),
(465,	'Bir ayağı çukurda olmak',	'Çok yaşlanmış olmak, yaşayacak çok az zamanı kalmış olmak. <b>Dedemin bir ayağı çukurda, onu üzmeyin artık. </b>'),
(466,	'Bir ayak önce (evvel)',	'Çok çabuk, bir an önce, ivedi olarak. <b>Bu iş, bir ayak önce yapılacak bir iştir. </b>'),
(467,	'Bir baltaya sap olmak',	'Belirli bir sanat ya da iş sahibi olmak. <b>Şu yaşa geldin ama bir baltaya sap olamadın gitti. </b>'),
(468,	'Bir bardak suda fırtına koparmak',	'Çok basit, küçük, önemsiz bir şeyi büyütüp içinden zor çıkılır bir olay hâline getirmek. <b>Bir bardak suda fırtına koparmayı bırak artık, mendilini yaktıysa evi de yakmadı ya!</b>'),
(469,	'Birbirine düşmek',	'Aralarında anlaşmazlık çıkıp birbirlerine kötü bakmaya başlamak. <b>Çocukların kavgası yüzünden birbirlerine düştüler. </b>'),
(470,	'Birbirine girmek',	'1. Aralarında çıkan anlaşmazlık kavgaya dönüşmek, çarpışmak, saldırmak. <br />2. Bir kaza sonucu araçların birbirine çarpması. <b>Su yüzünden sokak sakinleri birbirine girdi. </b>'),
(471,	'Bir çuval inciri berbat etmek',	'İyi olan, yolunda giden bir durumu yanlış davranışlarla bozmak, olumsuz bir gidişe sokmak. <b>Eline çekici alır almaz çiviye vurdu, çivi tahtayı yarıp geçti, bir çuval inciri berbat ettiğini o zaman anladı. </b>'),
(472,	'Bir dalda durmamak',	'Sık sık düşünce, iş ya da tutum değiştirmek. <b>Bir dalda dursaydı başına bu iş gelmeyecekti. </b>'),
(473,	'Bir damla',	'1. Çok az, pek az (sıvı şeyler için söylenir). <br />2. Çok küçük (çocuklar için söylenir). <b>Bir damla su kaldı, ne yapacağız su gelmezse. </b>'),
(474,	'Bir dediği iki olmamak',	'Her istediği hemen yapılmak, yerine getirilmek. <b>O, bir dediği iki olsun istemiyordu. </b>'),
(475,	'Bir deri bir kemik kalmak',	'Çok zayıflamak, kilo kaybına uğramak. <b>Zavallı çocuk, bu illete yakalanalı beri bir deri bir kemik kaldı. </b>'),
(476,	'Bir dikili ağacı olmamak',	'Malı, mülkü veya evi olmamak. <b>Şu dünyada bir dikili ağacımız olmayacak bu gidişle. </b>'),
(477,	'Bire bin katmak',	'Olduğundan çok göstermek, abartmak. <b>Bire bin katarak anlatmaya bayılır. </b>'),
(478,	'Bire bir gelmek',	'Etkisini hemen ve kesin olarak göstermek. <b>Verdiğin ilaç diş ağrıma bire bir geldi. </b>'),
(479,	'Bir eli yağda, bir eli balda (olmak)',	'Bolluk, varlık, rahat ve huzur içinde olmak. <b>Bir eli yağda, bir eli balda, daha ne istiyor ki?</b>'),
(480,	'Bir elle verdiğini öbür elle almak',	'Bir kimseye yaptığı iyiliği, yararı, başka bir yola baş vurarak sağladığı çıkarla ödetmek. <b>Bir eliyle verip öbür eliyle aldığını çok zaman sonra anladım. </b>'),
(481,	'Bir gömlek aşağı',	'Bir derece daha düşük. <b>Sizin ürettiğiniz fındık, bizimkinden bir gömlek daha aşağıdadır. </b>'),
(482,	'Bir hâl olmak',	'1. Bir şeyi çok yapa yapa usanmak, yorulmak, fenalık gelmek, bezmek. <br />2. Daha önce görülmeyen davranışlar içinde olmak, huyu değişmek. <br />3. Kazaya uğramış olmak. <b>Gecikti, başına bir hâl mi geldi acaba?</b>'),
(483,	'Bir hoşluğu olmak',	'Rahatsız, neşesiz olmak. <b>O şiddetli kazayı görünce bir hoş oldum. </b>'),
(484,	'Bir kalemde',	'Birden ve toptan, bir işlem ile. <b>Bir kalemde öde de kapat şu hesabı. </b>'),
(485,	'Bir kapıya çıkmak',	'Aynı sonuca varmak, aynı neticeyi vermek. <b>Ha sen söylemişsin ha ben, bir kapıya çıkmaz mı?</b>'),
(486,	'Bir kaşık suda boğmak',	'Bir kişiye çok fazla kızmak, elinden gelse öldürecek ölçüde sinirlenmek. <b>Şu yalancı herifi her söz söyleyişinde bir kaşık suda boğasım geliyor!</b>'),
(487,	'Bir kıyamettir gitmek (kopmak)',	'Çok fazla gürültü, patırtı, telâş olmak. <b>Alevler bacayı sarınca bir kıyamettir koptu sokakta. </b>'),
(488,	'Bir Köroğlu bir Ayvaz',	'Bir karı kocanın çocuğunun olmaması yahut yakınlarının yanlarında bulunmaması. <b>Bir Köroğlu bir Ayvaz olmasak bu maaşın bize yeteceği yok. </b>'),
(489,	'Bir kulağından girip öbür kulağından çıkmak',	'Söylenen söze önem vermemek, kulak asmamak, umursamamak. <b>Söylediğim söz bir kulağından girip öbür kulağından çıkarsa anlamazsın elbet!</b>'),
(490,	'Bir pula satmak',	'Bir kimseyi bir çıkar uğruna harcamak. <b>Parayı görünce adam bizi bir pula satıverdi. </b>'),
(491,	'Bir sözünü iki etmemek',	'Birinin her istediğini hemen yerine getirmek. <b>Ah benim tatlı çocuğum, bir sözümü iki etmez, hemen yapıverir. </b>'),
(492,	'Bir şeye benzememek',	'İşe yarar durumda olmamak, istenilen biçimde bulunmamak. <b>Bu kadar emekten sonra bari bir şeye benzemiş olsaydı şu kapı. </b>'),
(493,	'Bir taşla iki kuş vurmak',	'Bir davranışla iki veya birden çok yararlı sonuç elde etmek, bir girişimle iki iş yapmak. <b>Anladım amacını, bir taşla iki kuş vurmak. </b>'),
(494,	'Bir tutmak',	'Eşit görmek, eşit saymak, farklı muamelede bulunmamak. <b>Öğretmen, sınıftaki öğrencilerin hepsini bir tutmalıdır. </b>'),
(495,	'Bir yastığa baş koymak',	'Evli bulunmak, acı ve tatlı günlerde birbirini desteklemiş olmak. <b>Biz kırk yıl bir yastığa baş koyduk, nasıl unuturum onu?</b>'),
(496,	'Bir yastıkta kocamak',	'Karı ve koca birlikte uzun bir ömür sürmek. <b>Bir yastıkta kocarsınız inşaallah. </b>'),
(497,	'Bir yaşına daha girmek',	'Şaşılacak bir durumla, yeni bir şeyle karşılaşmak. <b>Aman yarabbim, onu o kılıkta görünce bir yaşıma daha girdim. </b>'),
(498,	'Bit yeniği',	'Kuşkulu bir nokta, işin gizli kalmış, kötü ve aksak yönü. <b>Bir bit yeniği var gibime geliyor bu işte, haydi hayırlısı. </b>'),
(499,	'Bize de mi lolo!',	'<b>Senin ne mal olduğunu biliyoruz, bize yutturamazsın ya; seni yeterince tanıyoruz, herkesi aldatabilirsin ama bizi asla</b> anlamında kullanılır.'),
(500,	'Boğaz boğaza gelmek',	'Zorlu bir kavgaya tutuşmak, ya da kavga edecek hâle gelmek. <b>Senin o dilin yüzünden adamla boğaz boğaza geldik. </b>'),
(501,	'Boğaz derdi',	'1. Yemek pişirme, hazırlama sıkıntıları. <br />2. Geçim için uğraşma, kazanç sağlama kaygısı. <b>Boğaz derdi, bence dertlerin en büyüğüdür. </b>'),
(502,	'Boğaz kavgası',	'Yaşamak için, geçinebilmek için yapılan didinme, uğraş. <b>Hemen bütün insanlar boğaz kavgasının içinde kaybolmuş durumdalar. </b>'),
(503,	'Boğazı kurumak',	'Çok susamak, çok konuşmaktan ve bağırmaktan ötürü sesi çıkmaz olmak. <b>Boğazım kurudu, bir şeyler içelim de öyle gidelim. </b>'),
(504,	'Boğazına dizilmek',	'Bir üzüntüden dolayı iştahı kesilmek, isteksiz ve zorla yemek. <b>Annemin o hasta hâli gözümün önüne geldikçe lokmalar boğazıma diziliyor. </b>'),
(505,	'Boğuntuya getirmek',	'Birini bunaltıp şaşırtma yolu ile kendisinden bir iş veya mal karşılığı olarak çok miktarda para çekmek.'),
(506,	'Bohçasını koltuğuna vermek',	'İşine son vermek, kovmak, başından defetmek. <b>Hiç sebepsiz yere bohçasını koltuğuna verip fabrikadan uzaklaştırdılar onu. </b>'),
(507,	'Bol keseden',	'Ölçüsüz, çok fazla, bol bol. <b>Bol keseden atıp tutmaya bayılır bizim çocuk. </b>'),
(508,	'Borç harç',	'Borç alarak ya da benzer yollara başvurarak (bir şeyi sağlamak). <b>Borç harç nihayet yaptırdık evin çatısını. </b>'),
(509,	'Borusunu çalmak',	'Çıkar sağladığı kimsenin davasını gütmek. <b>O, yıllardan beri Tophane kabadayılarının borusunu çalar. </b>'),
(510,	'Borusu ötmek',	'Sözü geçer olmak, dinlenilir olmak. <b>Bizim sokakta Hasan amcanın borusu öter. </b>'),
(511,	'Bostan korkuluğu',	'1. Kuşları ve diğer yabani hayvanları ürkütmek için tarlalara dikilen kukla, insan benzeri nesne. <br />2. Kendisinden beklenileni yapmayan, kendisinden çekinilmeyen, göstermelik kimse. <b>Müdür tam bir bostan korkuluğu, memurlar ne iş yapıyor ne güç.</b>'),
(512,	'Boşa çıkmak',	'Umulan gerçekleşmemek, sonuç vermemek, elde edilememek. <b>Bütün emeklerimiz boşa çıktı desenize. </b>'),
(513,	'Boş atıp dolu tutmak',	'Umutsuz olarak girişilen bir iş, iyi sonuç vermek; doğruluğuna inanmadan söylediği söz gerçek çıkmak. <b>Hayatımızın boş atıp dolu tutmak diye bir ilkesi olamaz. </b>'),
(514,	'Boş bulunmak',	'1. Dalgın ve dikkatsiz bulunmak. <br />2. Söylenmemesi gereken, sakıncalı bir sözü, işin sonunu düşünmeden söyleyivermek. <b>Boş bulunup da sakın söz verme, biliyorsun onlara gitmemiz mümkün değil. </b>'),
(515,	'Boş gezenin boş kalfası',	'İşsiz güçsüz, aylak, boş gezip dolaşan kimse. <b>Adam boş gezenin boş kalfası, bir de işsizlikten yakınıyor. </b>'),
(516,	'Boş vermek',	'Önem vermemek, aldırmamak, ilgisiz davranmak. <b>Boş ver, bu hayat böyle gelmiş, böyle gider. </b>'),
(517,	'Boy atmak',	'Boyu uzamak, gelişmek, boylanmak. <b>Çok çabuk boy attı sizin çocuk; maşallah, delikanlı gibi olmuş. </b>'),
(518,	'Boy göstermek',	'1. Görünmek, belirmek. <br />2. Gösteriş yapmak. <b>Onun gelip gitmesinin ardından olaylar boy gösterdi. </b>'),
(519,	'Boy ölçüşmek',	'Yarışmak, değer yarışına girmek. <b>Benimle boy ölçüşecek adam daha anasından doğmadı. </b>'),
(520,	'Boynu bükük',	'Yardım bekleyen; acınacak, kimsesiz, güçsüz, öksüz durumda olan. <b>Nerede bir boynu bükük görsem içim yanar. </b>'),
(521,	'Boynu eğri',	'Herhangi bir nedenle, kendisini bir kimsenin dediklerini yapmaya borçlu sayan. <b>O adamdan borç para aldığı için boynu eğri, bu yüzden yaptığı kötülüklere ses çıkaramıyor. </b>'),
(522,	'Boynu kıldan ince olmak',	'Adaletli yargı karşısında verilecek her cezaya razı olmak. <b>Gerçek adaletin karşısında boynum kıldan incedir. </b>'),
(523,	'Boynunun borcu',	'Yapılması gerekli olan ödev. <b>Seni sevindirmek boynumun borcu oldu artık. </b>'),
(524,	'Boynunu vurmak',	'Başını keserek öldürmek. <b>Boynunun vurulmasına ramak kala hakkındaki hükmün kaldırıldığını öğrendi ve yer gök onun oldu sanki</b>'),
(525,	'Boyunduruk altına girmek',	'Başkasının egemenliği altına girmek, tutsak olmak, emir ve baskı altında yaşamak. <b>Türk milleti için boyunduruk altına girmek, ölüm demektir. </b>'),
(526,	'Boyunun ölçüsünü almak',	'1. İddia üzerine giriştiği bir işi başaramayıp yetersizliğini anlamak. <br />2. Biri tarafından haddi bildirilmek. <br />3. Beklediği yakınlığı görememek. <b>Boynunun ölçüsünü aldı, böyle bir işe bir daha giremez. </b>'),
(527,	'Bozuk çalmak',	'Bir şey yüzünden canı sıkılmış, yüzü asılmış olmak, sinirli davranışlarda bulunmak. <b>Biraz hasta oldu diye sağa sola bozuk çalıp duruyor. </b>'),
(528,	'Bozuk düzen',	'1. Düzensiz, düzeni bozuk olan. <br />2. Toplumun yönetiminde uygulanan yanlış kurallar dizgesi. <b>Bu bozuk düzenden hangi görüş ve anlayış biçimi kurtaracak milleti, onu öğrenmeye çalışıyorum. </b>'),
(529,	'Bozum etmek',	'Bir kimseyi bekmediği bir davranış karşısında bırakarak utandırmak, mahçup etmek. <b>Adamı bozum etmeye bayılır bu ihtiyar, ona karşı dikkatli ol. </b>'),
(530,	'Bozum olmak',	'Bir sözü ya da davranışı iyi karşılanmadığı için utanmak, utanacak duruma düşmek. <b>Onun düşüncesinin hiç de doğru olmadığını söylediğim zaman amma da bozum oldu kadın. </b>'),
(531,	'Bozuntuya vermemek',	'Hataya düştüğünü anladığında veya hoşlanmadığı bir durumla karşılaştığında farketmemiş gibi davranmak, oralı olmamak. <b>Hiç bozuntuya vermeden misafirlere hoş geldin demeye devam etti. </b>'),
(532,	'Bulanık suda balık avlamak',	'Karışık durumlardan yararlanarak kendi çıkarını sağlamak. <b>Bulanık suda balık avlamayı kural hâline getirmiş. </b>'),
(533,	'Buldukça bunamak',	'Bulduğundan daha çoğunu isteyip şükretmemek, daha iyisini istemek. <b>Buldukça bunuyorsun, milletin aç sefil gezdiğini görmez misin sen?</b>'),
(534,	'Buluttan nem kapmak',	'Çok alıngan olmak, en küçük şeylerden bile alınmak. <b>Seninle konuşmak imkânsız, buluttan nem kapıyorsun çünkü. </b>'),
(535,	'Bunda bir iş var',	'<b>Bir olayın şimdilik bilinmeyen bir yönünün bulunması, anlaşılamayan bir sebebin aranması</b> durumunu anlatmak için kullanılır. <b>Polis, bunda bir iş var diyerek olayın üzerine tekrar gitti. </b>'),
(536,	'Bundan iyisi can sağlığı',	'<b>Bundan daha iyisi, en iyisi olamaz</b> anlamında kullanılır. <b>Bundan iyisi can sağlığı, haydi oturun bakalım sofraya. </b>'),
(537,	'Bu ne perhiz, bu ne lâhana turşusu',	'Bir ilke benimsediği hâlde, benimsediği bu ilkenin tersine davranışlarda bulunanlar için söylenir.'),
(538,	'Burnu bile kanamamak',	'Tehlikeli bir durumdan yara bere almadan kurtulmak. <b>On takla atan arabadan, burnu bile kanamadan çıktı, şaşılacak şey doğrusu. </b>'),
(539,	'Burnu büyümek',	'Kibirlenmek, böbürlenmek, büyüklenmek. <b>Adam milletvekili seçilir seçilmez bizimle konuşmaz oldu, burnu büyüdü birden. </b>'),
(540,	'Burnu havada (olmak)',	'Kendini çok beğenmiş, kibirli (olmak). <b>Burnu havada gezenlerden hiç hoşlanmam. </b>'),
(541,	'Burnu Kaf dağında (olmak)',	'Çok fazla kibirli, herkese yukarıdan bakar (olmak). <b>İyi ki bir araba aldı, burnu Kaf dağında bir adam olup çıktı. </b>'),
(542,	'Burnundan (fitil fitil) gelmek',	'Hoş bir durum, elde ettiği güzel bir şey, sonra gelen üzüntüler üzerine kendisine zehir olmak. <b>Yediğimiz yemeği burnumuzdan getirmek mi istiyorsun? Sus artık!</b>'),
(543,	'Burnundan düşen bin parça (olmak)',	'Suratı çok asık (olmak). <b>Ne olmuş bir cam kırılmışsa, iki gündür burnundan düşen bin parça. </b>'),
(544,	'Burnundan kıl aldırmamak',	'Oldukça huysuz olmak, kendisine hiç söz söyletmemek, kendisinin eleştirilmesine fırsat tanımamak, en küçük yergiye tahammül göstermemek. <b>Amma da burnundan kıl aldırmaz bir adammışsın; söylesene, nasıl konuşacağız seninle?</b>'),
(545,	'Burnundan solumak',	'İşi başından aşkın olduğu için gözü hiçbir şey görmemek, çok öfkelenmiş olmak. <b>Adam burnundan soluyor, sakın üstüne gitme, yoksa konuştuğuna pişman olursun. </b>'),
(546,	'Burnunu çekmek',	'1. Nefesini kullanarak sümüğünü burnunun yukarısına, geri çekmek. <br />2. Yoksun kalmak, umduğunu bulamamak, istediğini elde edememek, gayesine ulaşamamak. <b>Müdürün yanına alınmayınca burnunu çekip gitti. </b>'),
(547,	'Burnunun dikine gitmek',	'Kendisine verilen öğütlere kulak asmayıp kendi bildiği gibi davranmak, istediğini yapmak. <b>Burnunun dikine gidersen, işte böyle eline yüzüne bulaştırırsın işi. </b>'),
(548,	'Burnunun direği sızlamak',	'1. Çok acı duymak (maddî). <br />2. Çok üzülmek. <b>Soğuktan burnumun direği sızladı. </b>'),
(549,	'Burnunun ucunu görmemek',	'1. İleriyi görememek, meydana geleceği açık olanı görememek. <br />2. Çok sarhoş olmak. <br />3. Çok dikkatsiz ve dalgın olmak. <b>Sen ki burnunun ucunu göremeyen bir adamsın, seninle nasıl iş yapabilirim ben. </b>'),
(550,	'Burnunu sokmak',	'Üzerine vazife olmadığı, gerekmediği hâlde her işe karışmak. <b>Sen de her işe burnunu sokmaktan geri durmazsın!</b>'),
(551,	'Burnu sürtülmek',	'Ilımlı bir yol seçip gururundan vazgeçmek, sıkıntı çektikten sonra daha önce beğenmediği bir durumu kabul etmek. <b>Onun da burnunun sürtülmesine az kaldı, kısa zamanda dikbaşlılığı bırakacak. </b>'),
(552,	'Burun buruna gelmek',	'1. Ansızın karşılaşmak, karşı karşıya gelmek. <br />2. Birbirine çok yaklaşmak, birine çok sokulmak. <b>Kapıdan çıkar çıkmaz öğretmenimle burun buruna geldim. </b>'),
(553,	'Burun kıvırmak',	'Önem ve değer vermemek, küçümsemek, beğenmemek. <b>Önüne konan yemeklere burun kıvırıp sofradan kalktı. </b>'),
(554,	'Buyur etmek',	'Misafiri karşılayarak içeri almak, <b>buyurun</b> diyerek saygı ile yer göstermek ya da sofraya çağırmak. <b>Misafirleri büyük bir şevkle buyur etti. </b>'),
(555,	'Buyurun cenaze namazına',	'Hiç beklemedik kötü bir durum karşısında şaka yollu üzüntü belirtmek için <b>ne yazık ki</b> anlamında kullanılır. <b>Şunun yaptığına bakın, buyurun cenaze namazına!</b>'),
(556,	'Buz kesilmek',	'1. Çok üşümek, donmak. <br />2. Buz gibi soğumak, buz durumuna gelmek. <br />3. Endişe, korku ve üzüntü veren bir durum karşısında donakalmak. <b>Öldürdüğünü sandığı adamı karşısında görünce buz kesildi. </b>'),
(557,	'Buzlar çözülmek',	'1. Buzların erimeye ve kırılmaya, su hâline gelmeye başlaması. <br />2. Kişiler arasındaki dargınlığın, soğukluğun, kırgınlığın ve gerginliğin ortadan kalkmaya başlaması. <b>İki kardeşin arasındaki buzlar çözülmeye başlayınca aileye neşe geldi. </b>'),
(558,	'Buz tutmak',	'Üstünde buz meydana gelmek, buzla kaplanmak. <b>Göl buz tuttu. </b>'),
(559,	'Buz üstüne yazı yazmak',	'1. Birine etkisi olmayan sözler söylemek. <br />2. Etkisi ve süresi çok kısa olan bir iş yapmak. <b>Evet çocuklar, beni buz üstüne yazı yazan bir adam konumuna getirmeyin!</b>'),
(560,	'Büyük oynamak',	'1. Büyük bir tehlikeyi göze alarak bir işe girişmek. <br />2. Çok fazla para koyarak kumar oynamak. <b>Büyük oynadım, ya kaybedeceğim, ya da kazanacağım. </b>'),
(561,	'Büyük (söz) söylemek',	'Başkasının düştüğü kötü duruma düşmeyeceğini söyleyerek övünmek. <b>Ne demiş atalarımız, büyük lokma ye, büyük söz söyleme. </b>'),
(562,	'Büyük sözüme tövbe!',	'Bir konuda kesin konuşulduğunda ya da bir başkasının düştüğü kötü dur ama düşmeme iddiasında bulunulduğunda Cenab-ı Allah`tan böyle bir duruma düşürmemesini dileme. <b>Ne ettim de o sözü söyledim, büyük sözüme tövbe!</b>'),
(563,	'Büyüklük göstermek',	'Elinde her imkân varken kötülük yapmamak, affetmek, iyi davranmak. <b>İstese büyüklük göstermeyip onu buraya bir daha sokmazdı, erkek adammış. </b>'),
(564,	'Büyümüş de küçülmüş',	'Davranışları, konuşması yaşının üstünde olan, büyükler gibi hareketler yapan çocuk. <b>Aman yarabbim, şunun söylediği sözlere bakın hele, büyümüş de küçülmüş sanki!</b>'),
(565,	'Cadı kazanı',	'Fesadın ve dedikodunun çok olduğu, herkesin birbirine düştüğü, türlü düşmanlıkların kaynaştığı, hile ve düzenlerin kurulduğu yer. <b>Mahalle bir anda cadı kazanı gibi kaynamaya başladı. </b>'),
(566,	'Caka satmak',	'Çalım satmak, gösteriş yapmak. <b>Caka satmayı bırak da işine bak. </b>'),
(567,	'Cambul cumbul',	'Pek sulu, suyu bol (yemek için). <b>Yemek cambul cumbuldu ama lezzetli olmuştu. </b>'),
(568,	'Cana can katmak',	'İnsanda yaşama sevincini artırmak; insana neşe, heves ve iç gücü vermek. <b>Ah o cana can katan yaylaya bir daha çıkabilsem. </b>'),
(569,	'Can alacak yer (nokta)',	'Bir şeyin en önemli yeri, en temelli noktası. <b>Meselenin can alıcı noktasına bir türlü ulaşamadık. </b>'),
(570,	'Cana minnet (bilmek)',	'İhtiyacı olduğu hâlde arayıp da bulamadığı şeylerden saymak. <b>Yalnızca su mu? Canıma minnet, çabuk ver. </b>'),
(571,	'Can atmak',	'Herhangi bir şeye sahip olmayı, ya da herhangi bir şeye erişmeyi çok istemek. <b>Top oynamaya can atıyordu. </b>'),
(572,	'Can borcunu ödemek',	'Ölmek. <b>Beni korkutamazsın, bir can borcum var, onu da öder kurtulurum. </b>'),
(573,	'Cana yakın',	'Sevimli, sokulgan, insana pek sıcak davranan. <b>Ne cana yakın bir insanmış meğer. </b>'),
(574,	'Can baş üstüne',	'İstenilen, arzu edilen şeyin büyük bir memnunlukla yapılacağını anlatır. <b>Can baş üstüne efendim, kasabaya varınca onu hemen göreceğim. </b>'),
(575,	'Can çekişmek',	'Ölmek üzere bulunmak. <b>Yanına vardığımızda hayvan can çekişiyordu. </b>'),
(576,	'Can damarı',	'Bir şeyin en önemli noktası, en mühim unsuru; bir şeyin yaşaması için en önemli araç. <b>Babam evin can damarıdır. </b>'),
(577,	'Can damarına basmak',	'Bir işin en önemli noktası üzerinde durmak, ya da bir şeyin en duyarlı noktasını açığa çıkarmak. <b>Adamın en sonunda can damarına bastılar, zararı da kendileri gördüler. </b>'),
(578,	'Can dayanmamak',	'Bir acı, üzüntü, sıkıntı ve istek karşısında direnme gücü kalmamak; dayanıklılığı yitirmek. <b>Yıllarca uğraşıp didinip yaptığı ev bir anda kül oldu, buna can mı dayanırdı?</b>'),
(579,	'Can düşmanı',	'Öldürmeyi bile düşünen, aşırı kin ve düşmanlık besleyen, dost olmayan. <b>Can düşmanları etrafında cirit atıyorlardı. </b>'),
(580,	'Can evi',	'1. Yürek. <br />2. En duyarlı bölge. <b>Onları can evlerinden vurmaya yemin etti. </b>'),
(581,	'Can evinden vurmak',	'En etkileyici, en can alıcı yönden saldırmak; bir daha yaşama imkânı kalmayacak şekilde vurmak. <b>Onları can evinden vurmalıyız ki bir daha bellerini doğrultamasınlar. </b>'),
(582,	'Can havli ile',	'Ölüm korkusundan kaynaklanan güçlü bir tepkiyle (bir eylem yapmak). <b>Silâh sesini duyunca can havli ile yerinden fırladı. </b>'),
(583,	'Canı burnuna gelmek',	'Bir şey yaparken çok zorluk çekmek, bunalmak. <b>Kömürü taşıdım ama canım da burnuma geldi. </b>'),
(584,	'Canı (gönlü) çekmek',	'Bir şeyi istemek, istek duymak, çok arzulamak. <b>Şimdi o yeşil eriklerden olsa da yesek, öyle de canım çekti ki. </b>'),
(585,	'Canı çıkmak',	'1. Ölmek. <br />2. Çok yorulmak. <br />3. Çok yıpranmak. <b>Onu razı edinceye kadar canım çıktı. </b>'),
(586,	'Canı gitmek',	'Önem ve değer verdiği, beğendiği bir şeye zarar gelecek diye çok korkmak, kaygılanmak. <b>Araba çizilecek diye canı gidiyor. </b>'),
(587,	'Canına değmek',	'1. Çok hoşlanmak, yararına yapılan işten ötürü çok sevinmek. <br />2. Ruhu şad olmak. <b>Büyükannenin canına değsin, ikramın bizi oldukça sevindirdi</b>'),
(588,	'Canına kıymak',	'1. İntihar etmek, kendini öldürmek. <br />2. Acımadan öldürmek. <br />3. Kendini yoracak, yıpratacak kadar iş görmek. <b>Komşunun kızı canına kıymış. </b>'),
(589,	'Canına okumak',	'1. Bir kimseye büyük bir zarar vermek, kötülük etmek. <br />2. İyi bir şeyi kötü hâle getirmek, heder etmek, harcamak. <b>Yeni aldığım oyuncağın canına okudu bir günde. </b>'),
(590,	'Canına tak demek',	'Sabrı kalmamak, bir sıkıntıya dayanamaz duruma gelmek. <b>Canıma tak dedi artık, ya yaptıklarına son verirsin ya da burayı terkedersin!</b>'),
(591,	'Canına yandığım (yandığımın)',	'Kimi zaman sevgi ve hayranlık, kimi zaman da kızgınlık ve öfke gibi duyguları anlatmak için kullanılır. <b>Canına yandığımın adamı, bizi saatlerce bekletti bu soğukta. </b>'),
(592,	'Canına yetmek',	'Bezmek, bıkmak, bir zorluğa dayanamayacak duruma gelmek. <b>Canıma yetti artık bu işi yapmayacağım. </b>'),
(593,	'Canından bezmek',	'Çektiği sıkıntılar yüzünden içinde olduğu hayatı artık istemeyecek bir duruma gelmek. <b>Ne yapayım böyle hayatı, beni canımdan bezdirdi!</b>'),
(594,	'Canını almak',	'Öldürmek. <b>Allah canını alsın da kurtulalım senden!</b>'),
(595,	'Canını bağışlamak',	'Öldürebileceği bir kişiyi öldürmekten vazgeçmek. <b>Ona kıyamadı ve canını bağışladı. </b>'),
(596,	'Canını dişine takmak',	'Büyük sıkıntıları, tehlikeleri göze alarak bir işi başarmaya çalışmak. <b>Canını dişine takıp koca kayayı parçalamaya devam etti. </b>'),
(597,	'Canını sokakta bulmak',	'Sağlığını koruması, kendini yıpratmaması ve tedbir alması gerektiğini anlatmak için kullanılır. <b>Biraz soluk almama izin ver. Ben canımı sokakta bulmadım. </b>'),
(598,	'Canının içine sokacağı gelmek',	'Birine karşı büyük ölçüde sevgi duymak, birinden çok hoşlanmak. <b>Öyle ki o yavrucağı canımın içine sokacağım geliyor!</b>'),
(599,	'Canını vermek',	'1. Hiçbir şey esirgememek. <br />2. Bir şey uğrunda en değerli varlığını feda etmeye, hatta ölmeye hazır olmak. <br />3. Bir şeye aşırı ölçüde düşkün olmak. <b>Vatan uğruna kim can vermez ki?</b>'),
(600,	'Canını yakmak',	'1. Fizikî acı vermek. <br />2. Bir kimseyi zarara ya da sıkıntıya sokmak; üzmek, kaygılandırmak. <b>Lütfen canını yakma çocuğun. </b>'),
(601,	'Canı tatlı',	'Acıya, üzüntüye ve sıkıntıya katlanmayan. <b>Öyle de canı tatlı ki ne zaman bir şey taşınacak olsa bir bahane bulup ortadan kayboluyor. </b>'),
(602,	'Canı tez',	'Sabırsız, beklemeye tahammülü olmayan, ivecen. <b>Bekle de gör, ne canı tez adamsın sen öyle!</b>'),
(603,	'Canı yanmak',	'1. Fizikî bir acı duymak. <br />2. Bir işte zarar görmek, manevî bir üzüntü duymak. <b>Canını yakmadan ver o elindekini bana!</b>'),
(604,	'Can kalmamak',	'Gücü, kuvveti kesilmek; bitkin bir duruma düşmek. <b>Daha fazla yürüyemeyeceğim, can kalmadı bende, siz gidedurun. </b>'),
(605,	'Can kaygısına düşmek',	'Her şeyi bırakıp, içine düştüğü tehlikeden varlığını kurtarma ve koruma çabasında olmak. <b>Ortalık birbirine girip silâhlar patlamaya başlayınca can kaygısına düştü zavallı kadın. </b>'),
(606,	'Can kulağıyla dinlemek',	'Kendini vererek, büyük bir dikkatle dinlemek. <b>Babasının söylediklerini can kulağıyla dinlemeye başladı. </b>'),
(607,	'Canla başla',	'Seve seve, her türlü zorluğa göğüs gererek, var gücüyle, hiçbir fedakârlıktan kaçınmayarak. <b>Hepsi canla başla çalıştı. </b>'),
(608,	'Canlı cenaze',	'Çok zayıf, güçsüz, zayıflıktan kemikleri çıkmış kimse. <b>Adam canlı cenaze gibiydi. </b>'),
(609,	'Canlı yayın',	'Kişilerin ses ve davranışlarını o anda ve doğrudan doğruya veren radyo ve televizyon yayını. <b>Parti temsilcileri bu akşam televizyonda canlı yayında tartışacaklar. </b>'),
(610,	'Can pazarı',	'Herkesin kendi canının kaygusuna düştüğü ve kendi canını kurtarmaya çalıştığı tehlikeli bir durum, yer. <b>Ortalık toz dumandı; haykırışlar, inlemeler ortalığı çınlatıyordu; insanlar can pazarının tam ortasındaydılar. </b>'),
(611,	'Can sağlığı',	'Esenlik, kişinin sağlıklı olması. <b>Ne demeli canım kardeşim, inan bundan ötesi can sağlığı. </b>'),
(612,	'Can sıkıntısı',	'Yapılacak iş ve bir şeyle oyalanma imkânı bulamamaktan duyulan tedirginlik, içine düşülen bunalım. <b>Bütün gün evde oturuyor, can sıkıntısından ne yapacağımı bilemiyordum. </b>'),
(613,	'Can vermek',	'1. Ölmek. <br />2. Ruha güç vermek, yaşar duruma getirmek. <br />3. Bir şeyi çok ister olmak. <b>Adam bir kurşunda can verdi. </b>'),
(614,	'Can yakmak',	'1. Üzmek, acı vermek. <br />2. Zulmetmek, eziyet etmek. <br />3. Bir kimseyi büyük zarar ve ziyana sokmak. <b>Şu hareketlerinle canımı yakıyorsun. </b>'),
(615,	'Can yoldaşı',	'Yalnızlıktan kurtulmak için birlikte yaşanılan kimse. <b>Her insanın bir can yoldaşına ihtiyacı vardır. </b>'),
(616,	'Cart curt etmek',	'Göz dağı vermek ya da övünmek amacıyla abartılı konuşmak. <b>Karşımda cart curt edip durma. </b>'),
(617,	'Cart kaba kâğıt',	'Yüksekten atan, yapamayacağı şeyleri yapar gibi konuşan, çalım satan kimselere karşı söylenen küçümseme ünlemi.'),
(618,	'Cebi delik',	'Parasız, cebinde para tutmasını bilmeyen. <b>Daha ne kadar cebi delik dolaşacaksın. </b>'),
(619,	'Cebini doldurmak',	'Karşılaştığı fırsatları değerlendirerek bol para kazanmak. <b>Cebini doldurmaktan başka bir düşüncesi yok adamın. </b>'),
(620,	'Cehennem azabı',	'1. Çok büyük sıkıntı, eziyet. <br />2. İman etmeyenlerin, kâfirlerin, günahkârların cehennemde çekecekleri ceza. <b>Allah bizi cehennem azabından korusun. </b>'),
(621,	'Cehennem olmak',	'Defolup gitmek. <b>Çabuk cehennem ol yanımdan. </b>'),
(622,	'Cemaziyülevvelini bilmek',	'Bir kimsenin herkesçe bilinmeyen, geçmişteki kötü bir yönünü veya kötü durumunu bilmek. <b>Sakın güvenme ona, ben onun cemaziyülevvelini bilirim. </b>'),
(623,	'Cendereye sokmak',	'Çok sıkıştırmak, manevî baskı altına almak. <b>Adamı cendereye almayı iyi beceriyorsun. </b>'),
(624,	'Cevabı yapıştırmak',	'Karşısındakinin, beklemediği, ters, güç duruma düşürücü bir cevap vermek. <b>Öyle bir cevap yapıştırdı ki hasmı donakaldı. </b>'),
(625,	'Ciğeri beş para etmemek',	'Değersiz, kendisine güvenilmez, korkak, aşağılık (bir kimse olmak). <b>Bırak, ondan söz etme bana, ciğeri beş para etmez adamlarla işim yok. </b>'),
(626,	'Ciğerimin köşesi',	'1. Çok sevdiğim. <br />2. Sevgili evlâdım. <b>O, hâlâ benim ciğerimin köşesidir. </b>'),
(627,	'Ciğerini okumak',	'Karşısındakinin gizli düşüncelerini bilmek, aklından geçenleri anlamak. <b>Bizimi düşünüyormuş? Ben onun ciğerini okurum; o kendinden başkasını düşünmez. </b>'),
(628,	'Ciğerini sökmek',	'Bir kimseyi büyük ölçüde zarar ve ziyana uğratmak. <b>Söyle ona, beni oraya getirtmesin, gelirsem ciğerini sökerim onun. </b>'),
(629,	'Cin çarpmışa dönmek',	'Neye uğradığını anlayamayacak kadar kötü duruma düşmek. <b>Bir tokatta cin çarpmışa döndürdü adamı. </b>'),
(630,	'Cin fikirli',	'Zeki, çok kurnaz, her zaman kendi çıkarını kollayan, çok anlayışlı. <b>Endişelenmeyin; o cin fikirli, o işin de üstesinden gelecektir. </b>'),
(631,	'Cinler cirit (top) oynamak',	'Bir yerin ıssız, ürküntü verir olduğunu anlatmak için kullanılır.'),
(632,	'Cinleri başına toplamak',	'Öfkelenmek, kızmak, çok sinirlenmek. <b>Zorla cinleri başıma topladınız. </b>'),
(633,	'Curcunaya çevirmek (veya döndürmek)',	'Bir yeri kargaşa, şamata, gürültü patırtı ile doldurup kimsenin ne dediğini anlamayacak hâle getirmek. <b>Çocuklar bir dakikada ortalığı curcunaya çevirdiler. </b>'),
(634,	'Cümbür cemaat',	'Topluca, hep birden. <b>Halamlara cümbür cemaat gitmeye karar verdik. </b>'),
(635,	'Cümle kapısı',	'Konak, saray gibi büyük binaların ana giriş kapısı. <b>Devletin ileri gelenleri konağın cümle kapısı önünde toplandılar. </b>'),
(636,	'Cüret etmek',	'Ataklık etmek, yüreklilikle davranmak. <b>O, hemen herkesin yanında söz söylemeye cüret eden bir yapıya sahipti. </b>'),
(637,	'Cürmü meşhut hâlinde yakalamak',	'Bir kimseyi suçu işlerken şahitlerle birlikte yakalamak.'),
(638,	'Çaba göstermek',	'Bir işi başarmak için uğraşmak, kuvvet harcamak. <b>Çaba göstermeden amacına ulaşamazsın. </b>'),
(639,	'Çabalama kaptan ben gidemem',	'<b>Zorlamanın hiç faydası yok, ben bu işi yapacak güçte değilim; boşuna uğraşıyorsun, yapamam, gitmem, </b> anlamında kullanılır.'),
(640,	'Çağ açmak',	'Yeni bir gidişin, tutumun öncüsü olmak; evrensel bir gidişe yol açmak. <b>İstanbul` un fethiyle yeni bir çağ açıldı. </b>'),
(641,	'Çakar almaz',	'İşe yarar gibi görünse de aslında yararsız, bozuk olan. <b>Çakar almaz bir tabancayla bizi korkutacağını sanmıştı. </b>'),
(642,	'Çakı gibi',	'Canlı ve atik, çevik. <b>Çakı gibi delikanlı olmuş. </b>'),
(643,	'Çalımından geçilmemek',	'Çok kibirli, kurumlu olmak; büyüklük taslamak, gösteriş yapmak. <b>Adamın çalımından geçilmiyor, ona laf anlatmak çok zor. </b>'),
(644,	'Çalım satmak (caka satmak)',	'Büyüklük taslamak, kurularak davranmak.'),
(645,	'Çalıp çırpmak',	'Eline ne geçerse (az ve çok) çalmak, bu yolla kazanç sağlamak. <b>Yoksul kalınca çalıp çırpmaya başladı. </b>'),
(646,	'Çam devirmek',	'Farkında olmadan karşısındakini kıracak ya da kötü bir sonuca yol açacak söz söylemek, davranışta bulunmak. <b>Onun da çam devirmede üstüne yok hani. </b>'),
(647,	'Çam yarması',	'İri gövdeli insan.'),
(648,	'Çanak tutmak (açmak)',	'1. Söz ve davranışlarıyla kavgaya, kargaşaya yol açmak. <br />2. Dilenmek. <b>Onun bu işe çanak tutmasına fırsat vermeyeceğim. </b>'),
(649,	'Çanak yalayıcı',	'Dalkavuk, çıkarı için dalkavukluk eden. <b>Çanak yalayıcılar gün geçtikçe artıyor. </b>'),
(650,	'Çan çan etmek',	'Gerekli gereksiz sürekli konuşmak, yüksek sesle devamlı gevezelik etmek. <b>Başımda ne çan çan edip duruyorsun, kes artık şu sesini. </b>'),
(651,	'Çanına ot tıkamak',	'Bir daha sesini çıkaramayacak, kötülük edemeyecek bir duruma sokmak. <b>Elbet sizin de çanınıza ot tıkayacağım gün gelecek. </b>'),
(652,	'Çantada (torbada) keklik',	'<b>Ele geçirilmesi o kadar kesin ki elde edilmiş sayılır</b> anlamında kullanılır. <b>Beni çantada keklik sanıyor ama yanılıyor. </b>'),
(653,	'Çaptan düşmek',	'Önceleri iyi olan durumu sonradan bozulmuş olmak; çalışma gücü, verimi tükenmiş olmak. <b>Adamın bir ayda çaptan düşeceğini sandılar. </b>'),
(654,	'Çar çur etmek',	'Gereksiz, lüzumsuz yere harcayıp tüketmek. <b>Paranı sakın çarçur edeyim deme. </b>'),
(655,	'Çarıklı erkânıharp',	'Daha ziyade öğrenimi olmayan ama kafası çalışan, kurnaz ve uyanık köylüler için şaka yollu kullanılır.'),
(656,	'Çark etmek',	'Dönmek, geri dönmek. <b>Birkaç adım sonra çark ediniz. </b>'),
(657,	'Çarkına okumak',	'Bozmak, çalışamaz hâle getirmek, zarar vermek; birine büyük kötülük yapmak. <b>Eline alır almaz saatin çarkına okudu. </b>'),
(658,	'Çarşamba pazarı',	'Her şeyi açıkta olan, karmakarışık yer. <b>Etrafı çarşamba pazarı gibi yapmış çocuklar. </b>'),
(659,	'Çarçaf gibi',	'Dalgasız, dümdüz ve durgun. <b>Deniz çarşaf gibiydi. </b>'),
(660,	'Çat kapı',	'Aniden, beklenmedik bir anda. <b>Oturuyorduk, çat kapı çıkageldiler. </b>'),
(661,	'Çat pat',	'1. Ara sıra. <br />2. Yarım yamalak, biraz. <br />3. Vakitli vakitsiz, uygunsuz zamanlarda. <b>Çat pat okuması var diye mektubu ona uzattılar. </b>'),
(662,	'Çayı görmeden paçaları sıvamak',	'Ham hayaller kurmak; henüz zamanı gelmediği hâlde yapılacak bir iş, meydana gelebilecek bir olay için hazırlıklara girişmek. <b>Durun bakalım hele, çayı görmeden paçaları sıvamayın, bir haber ulaşsın önce. </b>'),
(663,	'Çehre züğürdü',	'Çirkin, suratsız, yüzü yakışıksız. <b>Oğlanı çehre züğürdü bir kızla evlenmek zorunda bıraktılar. </b>'),
(664,	'Çekeceği olmak',	'Çok acı çekeceği, sıkıntıya gireceği bir iş ya da durumla karşılaşacağı sezilir olmak. <b>Öyle anlaşılıyor ki bu çavuştan çekeceğimiz var. </b>'),
(665,	'Çekidüzen vermek',	'Karışıklığı, dağınıklığı, başıbozukluğu gidermek. <b>Kendine bir çeki düzen vermelisin artık. </b>'),
(666,	'Çekip çevirmek',	'Yönetmek, düzene sokmak, hâle yola koymak, çalışmasını sağlamak. <b>Tek başıma bu işi çekip çeviremem ki!</b>'),
(667,	'Çekip gitmek',	'Savuşmak, bırakıp gitmek, kimseye danışmadan ayrılmak. <b>Aradığını bulamayınca çekip gitti. </b>'),
(668,	'Çekirdekten yetişme',	'Bir işi küçük yaştan, çıraklıktan başlayarak öğrenme ve o işte ustalaşma. <b>Ali, çekirdekten yetişmiş bir marangozdu. </b>'),
(669,	'Çekişe çekişe pazarlık (etmek)',	'Bir malı ucuza almak, ya da pahalıya satmak için titizce uzun süre yapılan pazarlık. <b>Babam çok istediği atı alabilmek için, atın sahibiyle çekişe çekişe pazarlık etmeye başladı. </b>'),
(670,	'Çelme takmak',	'1. Ayağını bacağına geçirerek yıkmaya çalışmak. <br />2. Bir işin gelişmesini engellemek veya bir kimsenin iyi yürüyen işini bozmak. <b>Sakin sakin giden arkadaşını çelmek takarak yere düşürdü. </b>'),
(671,	'Çene çalmak',	'Gevezelik ederek, çok konuşarak vakit geçirmek. <b>Komşu kadınları çene çalmaya bayılırlar. </b>'),
(672,	'Çenesi düşük',	'Geveze, çok konuşan, gereksiz şeyler söyleyen. <b>Senin kadar çenesi düşük bir adam daha görmedim. </b>'),
(673,	'Çenesi kuvvetli',	'Söylemekten yorulmayan, söylediği sözlerle kendisini dinletmesini bilen. <b>İyi hatip, acaba çenesi kuvvetli hatip midir?</b>'),
(674,	'Çene yarıştırmak',	'Karşılıklı gevezelik etmek, boş konuşmak. <b>Sizinle çene yarıştırılmaz doğrusu. </b>'),
(675,	'Çetele tutmak',	'Hesap tutmak amacı ile bir yere çizgiler çekmek. <b>Ahmet amca, veresiye verdiği mallar için çetele tutmaktan usanmıştı. </b>'),
(676,	'Çetin ceviz',	'1. Kırılması zor, kabuğu sert ceviz cinsi. <br />2. Yola getirilmesi, yenilmesi zor rakip; başarılması güç iş. <b>Şimdi anlıyordu rakibinin ne deneli çetin ceviz olduğunu. </b>'),
(677,	'Çevir kaz (ı) yanmasın',	'Karşısındakini kıracak bir söz söylediğini fark edip de çevirmeye kalkışanlara şaka yollu söylenir.'),
(678,	'Çıban başı',	'1. Çıbanın patlamak üzere olan tepe noktası. <br />2. Kötü sonuçların, uygunsuzlukların ana sebebi. <b>Bu işte çıban başı mı olmak istersin?</b>'),
(679,	'Çıfıt çarşısı',	'Türlü kötülüklerin, hile ve düzenlerin karmakarışık bir durumda bulunduğu yer. <b>Daireyi çıfıt çarşısına çevirenler tek tek bulunmalıdır. </b>'),
(680,	'Çığır açmak',	'Bir alanda yeni bir yol açmak; yeni bir tutum, izlenecek yöntem bulmak. <b>Bilim adamları kanserle mücadelede çığır açmak için kolları sıvadılar. </b>'),
(681,	'Çığırından çıkmak',	'Yoldan sapmak, doğru ve uygun gidişten ayrılmak, artık düzelemez hâle gelmek. <b>İşler çığırından çıkmadan önlem almalıyız. </b>'),
(682,	'Çıkar yol',	'Çare, en tutarlı çözüm yolu. <b>Sınıf geçebilmek için tek çıkar yol ders çalışmaktır. </b>'),
(683,	'Çıkış yapmak',	'Bir tartışma esnasında etkili söz ve sert davranışlarla düşüncelerini belirtmek. <b>Ani bir çıkış yaparak herkesi şaşırttı. </b>'),
(684,	'Çıkmaza girmek',	'Çözümlenemeyecek, içinden çıkılamayacak bir duruma düşmek. <b>İşler, hiç ummadıkları bir anda çıkmaza girdi. </b>'),
(685,	'Çıngar çıkarmak',	'Gürültü patırtı, karışıklık ve kavga çıkarmak. <b>Çıngar çıkarmadan oturtun şu kadını. </b>'),
(686,	'Çıt çıkarmamak',	'Çok sessiz olmak, hiç ses çıkarmamak, gürültü yapmamak. <b>Çocuklar korkudan çıt çıkarmıyorlardı. </b>'),
(687,	'Çiçeği burnunda',	'Çok taze, yeni koparılmış. <b>Çiçeği burnunda bir haber getirmek için yarışa girdi muhabirler. </b>'),
(688,	'Çifte kumrular',	'Birbirini çok seven ve birbirinden ayrılmayan kimseler. <b>İşte çifte kumrular geliyorlar. </b>'),
(689,	'Çiğlik etmek',	'İnsana yakışmayan; olgunluğa, yaşa uygun düşmeyen yersiz ve kaba davranışlarda bulunmak. <b>Bir çiğlik edip de toplantıyı berbat edecek diye ödüm kopuyor. </b>'),
(690,	'Çiğ süt etmiş olmak',	'Soysuz ve namussuz olmak. <b>Bu yürek yakıcı işi yapmak için çiğ süt emmiş olmak gerek. </b>'),
(691,	'Çiğ yemedim ki karnım ağrısın',	'<b>Herhangi bir suç işlemedim ki korku duyayım, işi eksik yapmadım ki olumsuz sonuçtan kaygılanayım</b> anlamında kullanılır.'),
(692,	'Çile çekmek',	'Üzüntü, eziyet, acı ve sıkıntı içinde yaşamak. <b>Annen seni büyütünceye kadar ne çileler çekti biliyor musun?</b>'),
(693,	'Çile çıkarmak',	'1. Sıkıntılı bir işin veya durumun sona ermesini beklemek. <br />2. Tasavvufta bir müridin belli bir eğitim safhasından geçmesi. <b>Çile çıkarmayan mürit olgunlaşamaz. </b>'),
(694,	'Çileden çıkmak',	'1. Çok öfkelenmek, olan bitenler karşısında dayanıklılığı kalmayıp taşkınlık göstermek. <br />2. Çile süresini bitirmek. <b>Ben çileden çıkmadan çabuk terk edin burayı. </b>'),
(695,	'Çil yavrusu gibi dağılmak',	'Toplu hâlde bulunan insanların her biri, herhangi bir sebeple bir yana dağılmak. <b>Silâh sesini duyunca çil yavrusu gibi dağılmaya başladılar. </b>'),
(696,	'Çirkefe taş atmak',	'Edepsiz, geçimsiz, kaba saba kimsenin tepkisine yol açacak davranışlarda bulunmak. <b>Şu çirkefe taş atıp da başını belâya sokmadan gir içeri!</b>'),
(697,	'Çivi kesmek',	'Çok üşümek, donmak. <b>Çocuklar soğuktan çivi kesmişlerdi. </b>'),
(698,	'Çizmeden yukarı çıkmak',	'Bilmediği, aklının kesmediği, yetkisinin dışında bir işe kalkışmak; haddini bilmemek. <b>Kes artık, çizmeden yukarı çıkmaya başladın. </b>'),
(699,	'Çocuk oyuncağı',	'Önem verilecek değerde olmayan, kolay iş. <b>Dereyi geçmek mi? Çocuk oyuncağı benim için. </b>'),
(700,	'Çocuk oyuncağı hâline getirmek',	'Bir işi sık sık değiştirip verilmesi gereken önemde ele almamak, küçümsenir duruma getirip değerinden düşürmek. <b>Ne biçim adamlarsınız siz, bu güzel işi çocuk oyuncağı hâline getirdiniz!</b>'),
(701,	'Çoğu gitti azı kaldı',	'İşin en güç, en önemli, en büyük kısmı bitti, kalanı önemsizdir. <b>Ha gayret çocuklar, çoğu gitti azı kaldı. </b>'),
(702,	'Çok görmek',	'1. Esirgemek, bir kimseyi o şeye değer bulmamak. <br />2. Bir kimsenin yaptığını, davranışını yadırgamak. <b>Gel, çok görme bana bu işi. </b>'),
(703,	'Çoluk çocuk elinde kalmak',	'Genç, tecrübesiz, çocuk denecek kişilerin yönetimi altında yaşar durumda olmak. <b>Ülke çoluk çocuk elinde mi kalacak? Allah korusun!</b>'),
(704,	'Çoluk çocuğa karışmak',	'Evlenip, çocukları dünyaya gelip, onlarla uğraşır olmak. <b>Vay canına! Daha dünkü çocuktu, bugün çoluk çocuğa karışmış! Zaman ne çabuk da geçiyor. </b>'),
(705,	'Çorap söküğü gibi gitmek',	'Başlayan bir işin birbirine bağlı diğer bölümlerinin kolaylıkla halledilmesi. <b>Hele bir başla sen, bak nasıl çorap söküğü gibi gidecek iş. </b>'),
(706,	'Çorbada tuzu bulunmak',	'Yapılan bir iş ya da hizmette az da olsa çabası, emeği bulunmak. <b>Haydi durmayın, çorbada sizin de tuzunuz bulunsun!</b>'),
(707,	'Çömlek hesabı',	'Güvenilmez, yanlış hesap. <b>Senin yaptığın çömlek hesabı, bir muhasebeciye havale et işi. </b>'),
(708,	'Çuval gibi',	'Kaba ve seyrek, bol ve ütüsüz. <b>Pantolonun çuval gibi olmuş. </b>'),
(709,	'Çürüğe çıkmak',	'1. İşe yaramaz olduğu, sağlam olmadığı anlaşılarak bir yana atılmak. <br />2. Sağlığı el vermediği için askerlik görevine alınmamak. <b>Çürüğe çıkmak için can atanlar da yok değil bugün. </b>'),
(710,	'Çürük tahtaya basmak',	'Tedbirsiz hareket edip, kötü sonuçlanacak bir işe girişmek. <b>Allah kimseyi çürük tahtaya bastırmasın. </b>'),
(711,	'Dağa çıkmak',	'Hükümete, kanunlara karşı gelerek dağlara çekilmek, buralarda eşkıyalık etmek. <b>Düğünü basanlar dağa çıkmışlar. </b>'),
(712,	'Dağa kaldırmak',	'Herhangi bir sebepten ötürü birini zorla dağa veya ıssız bir yere götürüp orada alıkoymak. <b>Eşkıyalar, karakol komutanının oğlunu dağa kaldırmışlar; ne istedikleri henüz belli değil. </b>'),
(713,	'Dağarcığına atmak',	'Yeni bilgilerini, eski bilgilerine katmak; yeni bilgileri zihnine yerleştirmek. <b>Öğrendiği her yeni bilgiyi dağarcığına atmayı ihmal etmedi. </b>'),
(714,	'Dağdan gelip bağdakini kovmak',	'Daha sonradan geldiği bir yere ya da karıştığı bir işte eskiden beri bulunan bir kişinin yerini almaya çalışmak. <b>Şu densize bak hele, dağdan gelip bağdakini kovuyor!</b>'),
(715,	'Dağ doğura doğura fare doğurdu',	'Önemli gibi görünen şeylerden önemsiz bir sonuç çıkması durumunda söylenir.'),
(716,	'Dağlara düşmek',	'Sıkıntı, üzüntü sebebiyle insanlardan kaçıp ıssız yerlerde yaşar olmak. <b>Annesinin ölümünden sonra dağlara düştü. </b>'),
(717,	'Dağları devirmek',	'Çok büyük güçlüklerin altından kalkmak, ağır işleri başarmak. <b>O, dağları devirir bir adamdır. </b>'),
(718,	'Dalavere çevirmek',	'Yalan, dolan ve hile ile kötü bir iş yapmak; düzen kurarak gizlice başkasını aldatmak. <b>Yine bir dalavere çevirmesin bu adam!</b>'),
(719,	'Dal budak salmak',	'1. Karmaşık biçimde yayılıp genişlemek. <br />2. Soy ya da dostluk yönünden genişleyip yayılmak. <b>Bu mesele daha fazla dal budak salmadan hemen halledilmeli. </b>'),
(720,	'Daldan dala konmak',	'Çok sık, düşünce ya da konu değiştirmek. <b>Daldan dala konmayı bırak da bir işe sarıl artık. </b>'),
(721,	'Dalına basmak',	'Hiç hoşlanmadığı şeyleri yaparak birisini öfkelendirmek. <b>Dalıma basıp da beni çileden çıkarma lütfen!</b>'),
(722,	'Dallanıp budaklanmak',	'Genişleyip yayılmak, gittikçe büyüyerek karışık bir durum almak. <b>İşi dallandırıp budaklandırmada üstüne yok hani!</b>'),
(723,	'Damdan düşer gibi',	'Aniden, yersiz olarak (söz söylemek). <b>Damdan düşer gibi söz söyleyince ortalık birbirine girdi. </b>'),
(724,	'Damgasını vurmak',	'Biri hakkında kötü bir yargıya varmak. <b>Allah`tan korkmazsan ona hırsızlık damgasını vur da rezil olsun. </b>'),
(725,	'Damokles`in kılıcı',	'Kişiyi korku ve baskı altında tutan büyük ceza tehdidi. <b>Damokles`in kılıcı gibi başımda dikilip durma öyle!</b>'),
(726,	'Dananın kuyruğu kopmak',	'Olay patlak vermek, beklenen ve korkulan sonucun gerçekleşmesi. <b>Dananın kuyruğu bu gece kopacak, inşallah hayır demezler. </b>'),
(727,	'Danışıklı dövüş',	'Şike; önceden aralarında bir anlaşma olduğu hâlde, sanki böyle bir anlaşma yokmuş gibi davranarak başkalarını aldatmak. <b>Danışıklı dövüş insanların mertlik anlayışını tamamen öldürdü. </b>'),
(728,	'Dara düşmek',	'1. Paraca sıkıntıya uğramak. <br />2. Sıkıntılı, tehlikeli bir durumla karşılaşmak. <b>İyice dara düştük, geçinmekte güçlük çekiyoruz. </b>'),
(729,	'Dara getirmek',	'Aceleye getirmek, gerektiği gibi zaman ayıramamak. <b>Biraz erken kalkalım da dara getirmeden yapalım işi, güzel olsun. </b>'),
(730,	'Dar boğaz',	'Sıkıntılar ve güçlükler içinde geçirilen, geçici kabul edilip sonunda ferahlık umulan durum. <b>Evel Allah bu dar boğazı da aşacağız. </b>'),
(731,	'Dar hayat',	'Sıkıntılar, güçlükler, zorluklar içinde sürdürülen hayat.'),
(732,	'Darda kalmak',	'1. Zor duruma düşmek. <br />2. Paraca sıkıntı çekmek. <b>Öğretmeninin karşısında darda kalmak istemeyen Ahmet, ödevini yapmayı hiç ihmal etmezdi. </b>'),
(733,	'Dar gelirli',	'Geçim sıkıntısı çeken, kazancı normal olarak geçimini sağlamaya yetmeyen. <b>Dar gelirli ailelerin çocuklarının çoğu okulu yarıda bırakmak zorunda kalıyorlar. </b>'),
(734,	'Darısı (dostlar) başına',	'<b>Kavuştuğum başarı ve mutluluğa tüm dostlarımın da kavuşmasını isterim</b> anlamında kullanılır.'),
(735,	'Dar kafalı',	'Anlayışı, kavrayışı az; yeniliklere açık olmayan. <b>Dar kafalı insanlarla anlaşmak oldukça zordur. </b>'),
(736,	'Davul çalmak',	'Bir şeyi herkesin duyabileceği biçimde ortalığa yaymak. <b>Davul çalıp bizi elâleme rezil etti. </b>'),
(737,	'Defe (tefe) koymak',	'Dedikodusunu yapmak, kınayan bir dille başkalarına anlatmak, alaya almak. <b>Sakın söyleme, yoksa bizi defe koyarlar. </b>'),
(738,	'Defterden silmek',	'İlişkisini kesmek, yok saymak, adını anmaz olmak, unutmak. <b>Ali`yi defterden iyice sildim. </b>'),
(739,	'Defteri dürülmek',	'1. İşine son verilerek bir yerden uzaklaştırılmak. <br />2. Ölmek ya da öldürülmek. <b>Onun da defterini dürecekler yakında. </b>'),
(740,	'Defteri kapamak',	'İlgiyi kesmek, uğraşmaz olmak, söz konusu işi yapmaz olmak. <b>O defteri kapadık biz, artık soru sormayın. </b>'),
(741,	'Deli divane olmak',	'Bir şeyi, bir kimseyi aşırı derecede sevmek, ona tutkun olmak. <b>Delikanlı o kız için deli divane oluyordu. </b>'),
(742,	'Deli fişek',	'Atak, delişmen, delice işler yapan, şımarık. <b>Bırak artık şu deli fişek adamla arkadaşlık etmeyi. </b>'),
(743,	'Deliksiz uyku',	'Hiç uyanmadan, çok rahat, uzun süre uyunulan uyku. <b>Bu gece deliksiz bir uyku çekip yorgunluğumu atmak istiyorum. </b>'),
(744,	'Demir atmak',	'1. Çapasını denize atmak. <br />2. Bir yerde uzun süre kalmak. <b>Gemiler fırtına başlayınca koya girip demir attılar. </b>'),
(745,	'Dem tutmak',	'Bir çalgıya, bir başka çalgı veya sesle eşlik etmek.'),
(746,	'Denizden çıkmış balığa dönmek',	'Yeni bir işe, ortama, duruma alışmakta zorluk çekmek. <b>Eski işinden ayrılıp, yeni işine başlayınca denizden çıkmış balığa dönmüştü. </b>'),
(747,	'Derdine düşmek',	'Yapılması gereken bir şeyi gerçekleştirmenin yollarını aramak. <b>Sana ne ki o işin derdine düştün?</b>'),
(748,	'Dert ortağı',	'1. Aynı derdin, sıkıntının içinde bulunanlardan her biri. <br />2. Bir kimsenin derdini paylaştığı, anlattığı yakın dostu. <b>Onlar yıllar yılı birbirlerinin dert ortağı olarak yaşamışlardı. </b>'),
(749,	'Destan olmak',	'Yaptığı (kötü) bir işten dolayı şöhreti yayılmak. <b>Karısına bağırdı diye annesini kapıya attı, bütün civar köylere destan oldu. </b>'),
(750,	'Devede kulak',	'Bütüne göre çok ufak bir parça. <b>Onun yaptığı iş devede kulak kalır. </b>'),
(751,	'Deve kini',	'Bitmeyen, geçmeyen, unutulmayan büyük kin. <b>Tam anlamıyla bir deve kini besliyordu komşusuna karşı. </b>'),
(752,	'Deveye hendek atlatmak',	'Birisine yapılması çok zor, hemen hemen yapamayacağı bir işi yaptırmaya çalışmak. <b>Senin yaptığın deveye hendek atlatmak, bırak şu garibin yakasını. </b>'),
(753,	'Devlet kuşu',	'Umulmadık, iyi talih; zenginlik, mutluluk getiren talih.'),
(754,	'Dışı eli (seni) yakar, içi beni',	'<b>Dıştan görünüşü, herkesi imrendirecek kadar güzel ama içyüzü elverişsiz, kötü, sahibini üzücü</b> anlamında kullanılır. <b>Ah bir bilseler işin iç yüzünü, dışı eli yakar, içi beni. </b>'),
(755,	'Diken üstünde oturmak',	'Bir yerde tedirginlik duymak, her an kalkmak durumunu belirtir olmak, huzursuz olmak. <b>İnan, diken üstünde oturuyorum şurada. </b>'),
(756,	'Dikine gitmek',	'İnatçılık etmek, bildiğini yapmaya çalışmak, kimsenin uyarısına kulak asmamak. <b>Biraz daha dikine giderse başına büyük bir belâ gelecek bu çocuğun. </b>'),
(757,	'Dikiş tutturamamak',	'Bir yerde, bir işte bir sebepten ötürü başarı sağlayamayıp uzun süre kalmamak. <b>Bir şeyde dikiş tutturamadı, şimdi boşta gezip duruyor. </b>'),
(758,	'Dikiz etmek',	'Bir yeri, olayı, birinin hareketlerini gizlice ve gözünü ayırmadan dikkatlice izlemek.'),
(759,	'Dilden dile dolaşmak',	'Her yerde, pek çok kimse tarafından bahis konusu olmak. <b>Ata sözleri dilden dile dolaşarak günümüze kadar geldi. </b>'),
(760,	'Dil dökmek',	'Kandırmak, inandırmak ya da yararlanmak için tatlı sözler söylemek. <b>Peşine düşen çocuğu ne kadar dil döktüyse de evde kalmaya razı edemedi. </b>'),
(761,	'Dil ebesi',	'Çok fazla ve esprili konuşan. <b>Dil ebesi bir adam o, sen onunla başa çıkamazsın. </b>'),
(762,	'Dile (dillere) düşmek',	'Hakkında dedikodu yapılmak. <b>Allah kimseyi dile düşürmesin, kadıncağız sokağa çıkamaz oldu. </b>'),
(763,	'Dile gelmek',	'1. Konuşma yeteneği yokken konuşmak, dillenmek. <br />2. Dile düşmek. <b>Dile geldi dağlar, avuttu onu!</b>'),
(764,	'Dile getirmek',	'1. Bir meseleyi belirtmek, ortaya atmak, anlatmak, açıklamak. <br />2. Birini konuşturmak. <b>Hiç umulmadık bir anda konuyu dile getirdi, hepimizin anlamasını sağladı. </b>'),
(765,	'Dile kolay',	'Söylenmesi kolay ama yapılması ortaya konması ya da katlanılması çok güç. <b>Evet, dile kolay, haydi yap da görelim. </b>'),
(766,	'Dili açılmak',	'Herhangi bir sebepten dolayı konuşamayan kimse, birden konuşmaya başlamış olmak. <b>Dili açıldı çok şükür!</b>'),
(767,	'Dili dolaşmak',	'Heyecan, korku ya da bir hastalık sebebiyle söyleyeceğini şaşırmak, karıştırmak, açık olarak ifade edememek. <b>Babasını aniden karşısında görünce dili dolaştı, kekelemeye başladı. </b>'),
(768,	'Dili dönmemek',	'1. Bir sözü doğru ve düzgün söylemeyi becerememek, yanlışsız konuşamamak. <br />2. Amacını iyi anlatamamak. <b>İnşaallah dilim dönmeden meseleyi anlatır da kurtulurum ondan. </b>'),
(769,	'Dilinden kurtulamamak',	'Yaptığı bir kabahatten ötürü sürekli olarak, bir kimsenin sitem, eleştiri ve sataşmalarına uğramak. <b>Ne yapmalıyım da dilinden kurtulmalıyım onun?</b>'),
(770,	'Dilinde tüy bitmek',	'Sık sık söylemekten bıkmak, usanmak. <b>Size söyleye söyleye dilimde tüy bitti. </b>'),
(771,	'Diline dolamak',	'1. Bir kimsenin dedikodusunu yapmak, kötü tarafını her yerde söylemek. <br />2. Bir şeyi her fırsatta söyler olmak.'),
(772,	'Dilinin altında bir şey olmak',	'Bir kimsenin sözlerinden açıkça söylemediği bir şeyler olduğu anlaşılmak. <b>Dilinin altında bir şey olduğunu biliyorum ama bir türlü söyletemiyorum. </b>'),
(773,	'Dilinin ucuna gelmek',	'1. Tam söyleyecekken vazgeçip söylememek. <br />2. Hatırladığı şeyi söyleyecekken yine unutuvermek. <b>Dilinin ucuna geldi ama utandığı için söyleyemedi. </b>'),
(774,	'Dilini tutmak',	'Sonunu düşünerek gelişigüzel konuşmaktan sakınmak, ölçülü konuşmak, rast gele konuşmamak. <b>Dilini tutmasını bilmeyenlerin başına neler geldiğini sana söylemediler mi?</b>'),
(775,	'Dilini yutmak',	'Büyük bir korku, şaşkınlık ya da sevinç karşısında konuşamaz hâle gelmek. <b>Korkudan neredeyse dilini yutacaktı. </b>'),
(776,	'Dilin kemiği yok ya!',	'1. Önceden söylediği sözü başka biçimlere sokarak inkâr etmek. <br />2. İnsan konuşurken bazı hatalar yapabilir, doğru ve yanlış herşeyi söyleyebilir.'),
(777,	'Dili olsa da söylese',	'<b>Cansız nesneler, hayvanlar konuşabilseler, bazı olaylara tanıklık edebilseler ne iyi olurdu</b> anlamında kullanılır.'),
(778,	'Dili tutulmak',	'Herhangi bir sebepten ötürü söz söyleyemez duruma gelmek. <b>Sevinçten dili tutuldu bizim kızın. </b>'),
(779,	'Dili uzun',	'İncitici, kırıcı sözler söyleyen, saygısız kimse. <b>O uzun dilini bana kestirmeden çek içeri!</b>'),
(780,	'Dili varmamak',	'Bir sözü söylemeye gönlü razı olmamak. <b>Sana git demeye dilim varır mı sanıyorsun?</b>'),
(781,	'Dillerde dolaşmak',	'Her yerde kendisinden, ondan söz edilmek. <b>Cephede gösterdiği yararlılıklardan sonra adı dillerde dolaşır oldu. </b>'),
(782,	'Dillere destan olmak',	'Bir olay veya nitelik halk arasında yayılmak. <b>Ona öyle bir oyun oynayacağım ki dillere destan olacak!</b>'),
(783,	'Diline pelesenk etmek',	'Bir sözü her zaman, yerli yersiz tekrarlamak. <b>Şey sözünü diline pelesenk etmişsin, her cümlenin başında kullanıyorsun. </b>'),
(784,	'Dil uzatmak',	'Bir kimse veya bir şey için kötü söz söylemek. <b>Ben öğretmenime dil uzattıracak adam değilim. </b>'),
(785,	'Dil yarası',	'Acı, ağır ve kötü sözün gönülde bıraktığı kırgınlık. <b>Bıçak yarası geçer, dil yarası geçmez demişler. </b>'),
(786,	'Dimyat`a pirince giderken evdeki bulgurdan olmak',	'Daha iyisini elde etmek uğruna çalışırken elindekilerini de yitirmek. <b>Gel şu işten vazgeç, Dimyat`a pirince giderken evdeki bulgurdan da olma. </b>'),
(787,	'Dinden imandan çıkmak',	'Çok sinirlenmek, öfkelenmek, kızgınlık duymak. <b>İnsanı dinden imandan çıkarıyorsun, yapma şu hareketleri!</b>'),
(788,	'Dinden imandan olmak',	'Dinî inancını yitirmek, mürtet olmak.'),
(789,	'Dini bir uğruna',	'Müslümanlık davası yoluna (iş yapmak).'),
(790,	'Dini bütün',	'Dinin emirlerini eksiksiz yerine getirmeye çalışan, inancı sağlam olan, dinine çok bağlı. <b>Her Müslüman dini bütün olmak zorundadır. </b>'),
(791,	'Dipsiz kile boş ambar',	'Para, mal tutamayanın durumunu ya da verimsiz, sonuçsuz bir işi anlatmak için kullanılır. <b>Memurların işi tam anlamıyla dipsiz kile boş ambar, sıfıra sıfır elde var sıfır. </b>'),
(792,	'Dirlik düzenlik',	'Bir arada yaşayan, çalışan kimseler arasında iyi geçim, güven, sevgi ve anlaşma hâli. <b>Bir aileye önce dirlik ve düzenlik gereklidir. </b>'),
(793,	'Dirsek çevirmek',	'Daha önce birlikte iş yaptığı, anlaştığı kimseden, artık ihtiyaç duymadığı için yüz çevirmek; bir kimseyi kendinden uzaklaştıracak davranışlarda bulunmak. <b>Onun da dirsek çevireceğini hiç beklemezdim. </b>'),
(794,	'Dirsek çürütmek',	'Okumak, öğrenim görmek için uzun yıllar çalışmak. <b>Desene boşuna dirsek çürütmüşsün. </b>'),
(795,	'Diş bilemek',	'Öç almak, kötülük yapmak için fırsat kollamak; öfkesini gösterir durum almak. <b>Bana diş bilediği bakışlarından belli. </b>'),
(796,	'Dişe dokunur',	'Hatırı sayılır, işe yarar, belirtilmeye değer, önemli. <b>Dişe dokunur bir iş yapmışsın, aferin çocuğum. </b>'),
(797,	'Diş geçirememek',	'Etkisiz kalmak, güç yetirememek, hükmünü yürütüp sözünü dinletememek. <b>Bir çocuğa diş geçiremiyorsun, ne biçim annesin sen!</b>'),
(798,	'Diş gıcırdatmak',	'Kızgınlığını, öfkesini kimi davranışlarıyla belli etmek. <b>Dediğini yaptıramayınca dişlerini gıcırdatmaya başladı. </b>'),
(799,	'Diş göstermek',	'Güçlü olduğunu, kendine güvendiğini, saldırabileceğini davranışlarıyla belli etmek; tehdit etmek. <b>Biraz diş göstersen hemen yola geleceklerdir. </b>'),
(800,	'Dişinden tırnağından artırmak',	'Yiyeceğinden, içeceğinden vb. ihtiyaçlarından keserek zorla biriktirmek. <b>Seni, dişimden tırnağımdan artırdığım parayla okuttum!</b>'),
(801,	'Dişine göre',	'Yapabileceği, gücünün yeteceği, becerebileceği, uygun bir durumda. <b>Tam da dişime göre, onu yenebilirim. </b>'),
(802,	'Dişini sıkmak',	'Darlığa, sıkıntıya dayanmak; her türlü zorluğa katlanmak. <b>Biraz daha dişini sıkmalısın, inşallah yakında rahata kavuşacağız. </b>'),
(803,	'Dişini tırnağına takmak',	'Çok büyük zorluklara, sıkıntılara, darlıklara katlanarak bütün gücünü kullanıp çalışmak. <b>Biz bu evi dişimizi tırnağımıza takarak yaptık, yıkmalarına izin vermeyeceğim!</b>'),
(804,	'Diş kirası',	'1. Eskiden sarayda ya da konaklarda zenginlerin iftara çağırdıkları yoksullara verdikleri armağan veya para. <br />2. Harcadığı emek dışında bir kimsenin fazladan sağladığı çıkar.'),
(805,	'Dişinin kovuğuna bile gitmemek',	'Çok az gelmek (yiyecekler için). <b>Açlıktan kırılıyorduk, önümüzdeki yiyecekler dişimizin kovuğuna bile gitmeyecek kadardı. </b>'),
(806,	'Diz boyu',	'Dize kadar (yükseklik veya alçaklık için). <b>Çukuru diz boyu kazmışlardı. </b>'),
(807,	'Diz çökmek',	'1. Dizini yere koyarak oturmak. <br />2. Teslim olmak. <b>Düşman askerleri önümüzde diz çökmüşlerdi. </b>'),
(808,	'Dize gelmek',	'Teslim olmak, boyun eğmek, yenilmek, güçlünün buyruğunu kabullenmek. <b>Bizim kitabımızda dize gelmek yoktur!</b>'),
(809,	'Dize getirmek',	'Kendisine karşı geleni alt ederek buyruğunu dinler duruma getirmek, boyun eğdirmek. <b>İki saatte düşmanı dize getirebiliriz. </b>'),
(810,	'Dizgini (dizginleri) ele almak',	'Yönetimi ele geçirmek, işi kendisi yönetmeye başlamak. <b>Dizginleri ele almazsak fabrika kargaşa içinde boğulup kalacak, üretim yapılamayacak. </b>'),
(811,	'Dizginleri salıvermek',	'Başıboş bırakmak, sıkı tuttuğu yönetimi gevşetmek. <b>Yönetim, dizginleri salıverince insanlar rahat bir nefes aldılar. </b>'),
(812,	'Dizini dövmek',	'Çok pişman olmak. <b>Çocuklarını küçük yaşta eğitmezsen sonradan dizini döversin. </b>'),
(813,	'Dizinin (dizlerinin) bağı çözülmek',	'Korkudan, heyecandan, yorgunluktan ayakta duramayacak hâle gelmek. <b>Yokuşu çıktım ama dizlerimin de bağı çözüldü. </b>'),
(814,	'Dizlerine kapanmak',	'Yalvarmak, kendini küçük düşürecek kadar çok yalvarmak, başını dizlerinin üzerine koymak. <b>Göreceksin, günün birinde dizlerine kapanacak babasının. </b>'),
(815,	'Dobra dobra söylemek',	'Hiçbir şeyden çekinmeden, sözü eğip bükmeden, dosdoğru, açık açık konuşmak. <b>Dobra dobra konuşan insanları severim. </b>'),
(816,	'Doğmamış çocuğa don biçmek',	'Henüz ele geçmemiş bir şey, gerçekleşmesi kesin olarak bilinmeyen bir durum için hazırlık yapmak.'),
(817,	'Dokuz doğurmak',	'1. Bir işi güçlükle ve sıkıntı içinde sonuca ulaştırmak. <br />2. Merakla, heyecanla, sabırsızlıkla, sıkıntı çekerek beklemek. <b>İşe geç kalmıştı, yeni araba gelinceye kadar dokuz doğurdu. </b>'),
(818,	'Dokuz köyden kovulmuş',	'Geçimsizliği, hatalı davranışları yüzünden birçok yerden atılmış kimse.'),
(819,	'Dolap çevirmek',	'Hile, düzen ve dalavere ile iş yapmak. <b>Yine ne dolap çeviriyor acaba?</b>'),
(820,	'Dolma yutmak',	'Kanıp aldanmak. <b>Ona dolma yutturacağını hiç sanmam!</b>'),
(821,	'Dolu dizgin',	'1. Son hızla (süvari ve at arabası için). <br />2. Önüne geçilemeyecek biçimde, çok fazla olarak. <b>Kinlerimizi dolu dizgin salıverdik düşmanın üstüne. </b>'),
(822,	'Doluya koydum almadı, boşa koydum dolmadı',	'İçinden çıkılamayan güç bir durum karşısında söylenir. <b>Her yolu denedim, çözüm yolu bulamadım</b> anlamına gelir.'),
(823,	'Domuzdan kıl çekmek',	'Sevilmeyen, eli sıkı olan, cimri bir kimseden bir şey alabilmek. <b>Domuzdan bir kıl koparmak kârdır. </b>'),
(824,	'Don gömlek',	'Çıplak, üzerinde sadece don ve gömlek var denilecek kadar soyunmuş hâlde. <b>Adamı, don gömlek kalacak kadar soydular. </b>'),
(825,	'Dostlar alışverişte görsün',	'Gösteriş olsun; amaç iş yapıyor görünmek, iş yapmak değil. <b>Güya çalışıyor, dostlar alışverişte görsün!</b>'),
(826,	'Dökülüp saçılmak',	'1. Bir şey uğruna fazla para harcamak, masraf etmek. <br />2. Soyunmak, çok açık giyinmek. <b>Düğün yapıyorum diye sakın dökülüp saçılma, yoksa kendini toplayamazsın. </b>'),
(827,	'Dört ayak üstüne düşmek',	'Tehlikeli bir durumdan hiç zarar görmeden kurtulmak. <b>Nasıl oluyor da, bu adam hep dört ayak üstüne düşüyor?</b>'),
(828,	'Dört başı mamur',	'Her yanı bakımlı, elverişli, güzel, tam istenildiği gibi. <b>Alırsam dört başı mamur bir ev alacağım. </b>'),
(829,	'Dört dönmek',	'Bir işi yapmak için korku, heyecan, telâş, şaşkınlık içinde sağa sola koşmak, çare aramak. <b>Kadıncağız haberi alır almaz odanın içinde dört dönmeye başladı. </b>'),
(830,	'Dört elle sarılmak',	'Yapacağı işe büyük bir önem verip özen göstererek girişmek. <b>Başarılı olmak mı istiyorsun, dört elle sarıl işine!</b>'),
(831,	'Dört gözle beklemek',	'Özleyerek, çok isteyerek, büyük bir sabırsızlıkla beklemek. <b>Annemin yolunu dört gözle beklemeye başladım. </b>'),
(832,	'Dudak bükmek',	'Umursamamak, beğenmemek, küçümsemek. <b>Yeni alınan elbiseye şöyle bir dudak büküp geçti. </b>'),
(833,	'Dudak ısırmak',	'Hayret etmek, şaşırmak. <b>Beni karşısında görünce dudağını ısıracak eminim. </b>'),
(834,	'Dudak ısırtmak',	'1. Hayran bırakmak. <br />2. Şaşkınlığa, hayrete düşürmek. <b>Yazdığı son kitabıyla dudak ısırttı herkese. </b>'),
(835,	'Duman attırmak',	'Geride bırakmak, zor duruma düşürmek, birini yıldırmak. <b>Silâhını çeken komutan etrafa duman attırmaya başladı. </b>'),
(836,	'Duman etmek',	'Bozmak, ortalığı dağıtmak, yok etmek; yenmek, birine karşı başarı sağlamak. <b>Askerler ortalığı toz duman ettiler. </b>'),
(837,	'Dumanı üstünde',	'1. Çok taze (sebze ve meyve için). <br />2. Çok yeni, üzerinden zaman geçmemiş. <b>Şu elmalara bak, daha dumanı üstünde bunların. </b>'),
(838,	'Duman olmak',	'1. Ortadan kaybolmak. <br />2. Durumu, düzeni, işi bozulmak. Kötü olmak. <b>Çabuk duman ol buradan, gözüm görmesin seni!</b>'),
(839,	'Durduğu yerde',	'1. Hiç gereği yokken. <br />2. Kolaylıkla, hiç emek ve çaba harcamadan. <b>Adam durduğu yerde para kazanıyor, anlamadım bu işi!</b>'),
(840,	'Durup dinlenmeden',	'Sürekli olarak, ara vermeden, arka arkaya. <b>Yıllar yılı durup dinlenmeden çalıştım sizin için. </b>'),
(841,	'Durup dururken',	'1. Birden bire, ansızın. <br />2. Hiç gereği veya sebebi yokken. <b>Durup dururken bir tokat attı arkadaşına. </b>'),
(842,	'Dut yemiş bülbüle dönmek',	'Susmak; konuşkanlığını, sevincini, neşesini yitirmek; sesi çıkmaz olmak. <b>Onu dut yemiş bülbüle döndürmezsem bana da Hasan demesinler!</b>'),
(843,	'Düğüm noktası',	'Bir meselenin sonuçlandırılması için çözülmesi, açıklığa kavuşturulması gereken en güç yanı. <b>Biz işin daha düğüm noktasını tespit etmiş değiliz ki!</b>'),
(844,	'Düğün bayram etmek',	'Çok sevinç duymak, topluca neşeli bir duruma kavuşmak. <b>Ağabeyim savaştan sağ salim dönünce ailece bayram ettik. </b>'),
(845,	'Düğün evi gibi',	'Çok kalabalık ve telâşlı görülen yer. <b>Hayrola, dün akşam sizin sokak düğün evi gibiymiş!</b>'),
(846,	'Dümen çevirmek',	'Düzen kurup, hileli iş yapmak. <b>Yine ne dümen çeviriyorsunuz siz?</b>'),
(847,	'Dümen kırmak',	'Yön değiştirmek.'),
(848,	'Dümen suyunda gitmek',	'Birine bağımlı olmak, birinin tuttuğu yolu izlemek, hemen her şeyde ona uyarak onun istediğini yapmak. <b>Başkasının dümen suyundan gidenler kişiliklerini bulamazlar. </b>'),
(849,	'Dünkü çocuk',	'Deneyimi az, toy acemi. <b>Dünkü çocukların aklına ihtiyacım yok benim. </b>'),
(850,	'Dünya başına yıkılmak',	'Dara düşmek, felâkete uğramak, umutlarını yitirmek, çok üzülüp acı çekmek. <b>Trafik kazasında kocasını ve iki çocuğunu kaybeden kadının dünyası başına yıkılmıştı. </b>'),
(851,	'Dünya bir araya gelse',	'<b>Bütün insanlar engel olmaya kalksa bile, asla, hiçbir zaman, kim ne derse desin</b> anlamında, yine bildiğini yapma durumu için kullanılır. <b>Dünya bir araya gelse de ben o adamla barışmam. </b>'),
(852,	'Dünyadan elini eteğini çekmek',	'Bir kenara çekilip toplum ile ilişkisini kesmek, toplumun yaşayışına karışmaz olmak, daha çok ibadetle meşgul olmak ve dünya işleriyle ilgilenmez olmak. <b>Bizim komşu her nedense dünyadan elini eteğini çekti, görünmez oldu sanki. </b>'),
(853,	'Dünyadan haberi olmamak',	'Çevresinden, çağından ve çağının getirdiklerinden, zamanında yaşanan hayattan haberli olmamak. <b>Sen dünyadan haberi olmayan bir adamsın, ne anlarsın bu işten, lütfen karışma!</b>'),
(854,	'Dünya gözü ile',	'Ölmeden önce, yaşarken. <b>Dünya gözü ile Almanya`daki kardeşimi bir daha görsem. </b>'),
(855,	'Dünyalar onun olmak',	'Oldukça çok sevinmek. <b>Babası istediği oyuncağı getirince dünyalar onun oldu sanki. </b>'),
(856,	'Dünyanın kaç bucak olduğunu anlamak',	'Dünyada insanın başına neler gelebileceğini öğrenmek, zorluklarla karşılaşmak, tecrübe kazanmak. <b>Elbet sen de bir gün dünyanın kaç bucak olduğunu anlayacaksın. </b>'),
(857,	'Dünyanın öbür ucu',	'Çok uzak yer. <b>Ali de dünyanın öbür ucunda oturuyor. </b>'),
(858,	'Dünya yıkılsa umurunda değil',	'Hiçbir şeyle ilgilenmemek, umursuz olmak, sorumluluk duymamak. <b>Sakın `dünya yıkılsa umurumda değil` deme bana. </b>'),
(859,	'Dünyayı toz pembe görmek',	'İyimser olmak, üzücü durumlara bile iyi gözle bakmak. <b>Bırak artık şu dünyayı toz pembe görmeyi, aç gözlerini!</b>'),
(860,	'Düşe kalka',	'1. İşi kimi zaman iyi, kimi zaman kötü olarak güçlükle, uğraşa uğraşa (yapmak). <br />2. Biriyle yakın ilişki kurarak. <b>Sokak serserileriyle düşe kalka iyice bozuldu, sapıttı. </b>'),
(861,	'Düşeş atmak',	'Umulmadık bir başarı kazanmak. <b>Düşeş attı bizim oğlan, şimdi yanına da yaklaştırmaz kimseyi. </b>'),
(862,	'Düşman çatlatmak',	'Nisbet yapmak, iyi durum ve başarılarıyla düşmanı kızdırmak ve kıskandırmak. <b>Düşman çatlatmakta da üstüne yok senin!</b>'),
(863,	'Düşman kesilmek',	'Düşman olmak, düşman gibi görünüp tavır almak. <b>Yalnız benim değil, bütün ailenin düşmanı kesilmişti. </b>'),
(864,	'Düşünüp taşınmak',	'Bir meseleyi enine boyuna tartmak, konuyu bütün yönleriyle incelemek, iyice düşünüp ona göre davranmak. <b>Acele etme, düşünüp taşın öyle karar ver. </b>'),
(865,	'Düşüp kalkmak',	'1. Yakın arkadaşlık etmek. <br />2. Yasa ve gelenek dışı kadın ve erkekle birlikte yaşamak veya sık sık bir araya gelmek. <b>Seni bu hâle getirenler düşüp kalktığın arkadaşlarındır. Hâlâ anlamadın mı?</b>'),
(866,	'Düttürü Leylâ',	'Gülünç, tuhaf, daracık ve kısacık giyinmiş kadın. <b>Sana hiç yakışmamış, düttürü Leylâ gibi olmuşsun. </b>'),
(867,	'Ecel aman verirse',	'Ölmezsem, ömür yeterse. <b>Ecel aman verirse torunumu da görürüm. </b>'),
(868,	'Ecel teri dökmek',	'Çok korkmak, heyecan içinde bulunup terlemek, korku ve bunalım içinde olmak. <b>Köprüden geçerken ecel terleri döktüler. </b>'),
(869,	'Eceli gelmek',	'Ölmek, sonu gelmek, yok oluş vakti gelmek. <b>Herkesin eceli gelecek ve bu dünyadan göçecek. </b>'),
(870,	'Eceline susamak',	'Ölümüne yol açacak kadar tehlikeli işlere girişmek. <b>Bırak o silâhı elinden, eceline mi susadın sen?</b>'),
(871,	'Eciş bücüş',	'Çarpuk çurpuk, eğri büğrü, düzgün yanı olmayan, çirkin bir biçim almış bulunan. <b>Eciş bücüş bir yazıyla karşılaşınca şaşırdı. </b>'),
(872,	'Edebiyat yapmak',	'Bir işe yaramayan, konuyu açıklamaya yetmeyen, gerçeği yansıtmayan süslü, parlak ve gereksiz sözler söylemek. <b>Edebiyat yapmaya amma da meraklı bir insanmış. </b>'),
(873,	'Efkâr dağıtmak',	'Sıkıntıyı gidermek, üzüntüyü yok etmeye çalışmak. <b>Sahile efkâr dağıtmak için inmiş olmalı. </b>'),
(874,	'Eğri (gözle) bakmak',	'Kötü düşünce besleyerek bakmak. <b>O, hiç kimseye eğri gözle bakmazdı. </b>'),
(875,	'Ekmeğinden etmek',	'İşinden çıkarmak veya atmak. <b>Adamı durup dururken ekmeğinden ettiler. </b>'),
(876,	'Ekmeğine yağ sürmek',	'Birinin yararına göre eylemde bulunmak, istemese de birinin işine yarayacak biçimde hareket etmek. <b>O işi bana vermemekle yabancıların ekmeğine yağ sürdün sen. </b>'),
(877,	'Ekmeğini kazanmak',	'Geçimini temin edecek, ihtiyaçlarını karşılayacak parayı kazanmak. <b>Kaygılanma, ekmeğini kazanmasını bilir o. </b>'),
(878,	'Ekmeğini taştan çıkarmak',	'En zor işleri bile yapıp geçimini sağlayacak beceriklikte olmak, her türlü işi yapmak. <b>Ekmeğini taştan çıkaran insanların arasına katılmakta gecikmedi. </b>'),
(879,	'Ekmek elden su gölden',	'Kendisi kazanmayıp başkalarının kazancı ile geçinen kimselerin durumunu anlatmak için kullanılır.'),
(880,	'Ekmek kapısı',	'Çalışıp para kazanılan, geçim sağlayan iş yeri. <b>O dükkân benim ekmek kapım, asla satmam, satamam onu!</b>'),
(881,	'Ekmek parası',	'Kazanç, geçinmek için kazanılan para. <b>Ekmek parası kolay kolay kazanılmıyor. </b>'),
(882,	'Eksik gedik',	'Ufak tefek ihtiyaçlar. <b>İkramiye ile eksiği gediği kapadılar. </b>'),
(883,	'Ekşi yüz',	'Somurtkan, asık yüz. <b>Onun ekşi yüz göstermeye hakkı yoktu. </b>'),
(884,	'El açmak',	'1. Dilenmek. <br />2. Başkasının yardımını almak için yalvarmak. <b>İhtiyarlayıp da el açacağı hiç aklına gelmemişti. </b>'),
(885,	'El altından',	'Kimsenin haberi olmadan, gizlice. <b>Parayı el altından verdi. </b>'),
(886,	'El atmak',	'1. Bir işe girişmek. <br />2. Birisinin işine karışmak. <b>Üstüne vazife olmayan işe el atma sakın!. . </b>'),
(887,	'El ayak çekilmek',	'Ortalıkta kimse kalmamak, ıssızlaşıp sessizleşmek. <b>Bu iş ancak el ayak çekildikten sonra yapılır. </b>'),
(888,	'El basmak',	'Yemin etmek, kutsal bir şey üzerine el koyarak ant içmek. <b>Kur`ân`a el basarım ki bu işi ben yapmadım. </b>'),
(889,	'El çabukluğu',	'1. Bir işi çok çabuk yapabilme ustalığı. <br />2. Hilesini kimseye sezdirmeyecek biçimde yapabilme. <b>Adamın cebinden el çabukluğu ile cüzdanı çekiverdi. </b>'),
(890,	'Elde avuçta bir şey kalmamak',	'Parasını, malını, tüm varlığını harcayıp bitirmiş olmak. <b>Elde avuçta bir şey kalmayınca ne yapacağını şaşırdı. </b>'),
(891,	'Elde etmek',	'1. Bir şeye sahip olmak. <br />2. Bir kimseyi kendi yanına çekmek. <b>Onun gibi dürüstleri elde edemezsin, boşuna uğraşma. </b>'),
(892,	'Elde kalmak',	'1. Bir malın satılmayıp geride kalan kısmı. <br />2. Harcanandan arta kalmış olmak. <b>Şu kasadaki üzümler elde kaldı. </b>'),
(893,	'Elden ayaktan düşmek (veya kesilmek)',	'Yaşlılık, hastalık sebebiyle iş yapamaz, yürüyemez, kendi işini göremez duruma gelmek. <b>Allah kimseyi elden ayaktan düşürmesin. </b>'),
(894,	'Elden çıkmak',	'Malı olmaktan çıkmak. <b>O arsa elden çıktığı için üzüldüm. </b>'),
(895,	'Elden düşme',	'Az kullanılmış. <b>Elden düşme bir araba aldı. </b>'),
(896,	'Elden ele dolaşmak',	'Pek çok kişi tarafından kullanılmak, bir çok sahip eline geçmek. <b>Elden ele dolaşan atı nihayet geri almayı başardı. </b>'),
(897,	'Elden geçirmek',	'Eksiklikleri düzeltmek, onarmak; denetlemek için pek çok şeyi ele alıp yoklamak, gözden geçirmek. <b>Yaptığın işi bir daha elden geçir. </b>'),
(898,	'Elden gitmek',	'Bir şeyi yitirmek, ondan yoksun kalmak. <b>Bütün mal mülk bir hiç uğruna elden gitti. </b>'),
(899,	'Ele almak',	'1. Bir şey üzerinde çalışmaya başlamış olmak. <br />2. İncelemek, araştırmak veya tenkit etmek. <b>Konuyu yeni baştan bir daha ele alalım. </b>'),
(900,	'Ele avuca sığmamak',	'1. Şımarık davranmak. <br />2. Söz dinlememek, kural tanımamak, zapt edilememek. <b>Sen ne ele avuca sığmaz bir çocukmuşsun meğer. </b>'),
(901,	'Ele geçirmek',	'Sahip olmak, kaçan bir kimseyi yakalamak. <b>Şu toprak parçasını da ele geçirdik mi işimiz tamam demektir. </b>'),
(902,	'El elde baş başta',	'1. Masrafla para birbirine denk geldi. <br />2. Yapılan işin sonunda ne kâr ne de zarar edildi. <b>Alışverişten el elde baş başta döndü. </b>'),
(903,	'Elekten geçirmek',	'Titizlikle seçmek; iyiyi kötüyü, doğruyu yanlışı birbirinden ayırmak. <b>Şu dosyayı bir daha elekten geçirin. </b>'),
(904,	'El ele vermek',	'Güçleri birleştirip işbirliği yapmak, yardımlaşmak. <b>Bu yolu ancak el ele verirsek yapabiliriz. </b>'),
(905,	'El emeği',	'1. Elle yapılan işe harcanan emek. <br />2. Elle yapılan çalışmanın karşılığı. <b>El emeğinin karşılığı değildir bu para. </b>'),
(906,	'Ele vermek',	'Bulunduğu yeri haber vererek suçluyu yakalatmak. <b>Katili ele vermeyi kafasına koyarak sokağa çıktı. </b>'),
(907,	'Eli açık',	'Cömert, çok para harcayan, sakınmadan para verebilen. <b>Eli açık olan insanları severim. </b>'),
(908,	'Eli ağır',	'1. Oldukça yavaş iş yapan. <br />2. Vurunca çok acıtan. <b>Eli o kadar ağırmış ki enseme gülle düştü sandım. </b>'),
(909,	'Eli altında olmak',	'1. İstediği anda ele alıp kullanabileceği bir yerde bulunmak. <br />2. Buyruğunda olmak. <b>İyi bir usta, araç ve gereçlerinin elinin altında olmasını ister. </b>'),
(910,	'Eli ayağı buz kesilmek',	'1. Korku, heyecan ve üzüntüden ne yapacağını bilemez duruma gelmek, donup kalmak. <br />2. Çok üşümek. <b>Haydi elimiz ayağımız buz kesmeden girelim içeri. </b>'),
(911,	'Eli ayağı tutmak',	'İş yapabilecek güçte olmak, bedenî gücü var olmak. <b>Çok şükür şimdilik elimiz ayağımız tutuyor. </b>'),
(912,	'Eli bayraklı',	'Kavgacı, şirret, edepsiz. <b>Onun eli bayraklı bir kadın olduğunu daha yeni anladınız. </b>'),
(913,	'Eli bol',	'Cömert, esirgemeyen, çok para ve eşyası olan. <b>Duyduğumuza göre Hasan Çavuş eli bol bir insanmış. </b>'),
(914,	'Eli boş dönmek',	'Umduğunu alamadan geri dönmek. <b>Eli boş döneceği hiç aklıma gelmezdi. </b>'),
(915,	'Eli böğründe kalmak',	'Çaresiz kalmak, bir şey yapamaz duruma gelmek, başarısızlığa uğramak. <b>Tek hayvanın öldüğünü görünce eli böğründe kaldı. </b>'),
(916,	'Eli cebine gitmemek (veya varmamak)',	'Cimri olmak, para harcamaya kıyamamak. <b>Ondan da yardım istediler, ancak eli cebine bir türlü gitmedi, arkasını dönüp uzaklaştı. </b>'),
(917,	'Eli çabuk',	'Süratli iş gören. <b>Eli çabuk adamlara ihtiyacımız var. </b>'),
(918,	'Eli darda',	'Geçimi için para sıkıntısı çeken. <b>Eli darda insanlara yardım etmek insanlık borcudur. </b>'),
(919,	'Eli değmemek',	'Bir işi yapmaya zaman bulamamak. <b>Odanı temizlemeye elim değmiyor. </b>'),
(920,	'Elifi görse mertek sanır',	'Cahil, okuması yazması yoktur. <b>Ona mı akıl danışıyorsun, elifi görse mertek sanır o. </b>'),
(921,	'Eli hafif',	'İncitmeden, can yakmadan iş gören. <b>İğneyi Hatice hemşireye vurdurun eli hafiftir onun. </b>'),
(922,	'Eli kalem tutmak',	'1. Yazı yazmayı bilmek. <br />2. Düşüncelerini derli toplu güzel bir ifade ile yazabilmek. <b>Elin kalem tutmaz mı senin?</b>'),
(923,	'Elinden iş çıkmamak',	'Çabuk iş yapamamak. <b>Bırakın onu, elinden iş çıkmaz birine ihtiyacımız yok. </b>'),
(924,	'Elinden tutmak',	'1. Destek olmak, ilerlemesi için yardımda bulunmak. <br />2. Yürümesine, kalkmasına, inmesine, çıkmasına yardım etmek. <b>Hayatım boyunca elimden tutan olmadı. </b>'),
(925,	'Eline düşmek',	'1. Birine muhtaç olmak. <br />2. Yakalanmak. <br />3. Düşmanın ya da kendisine hıncı bulunan birinin hâkimiyetinde kalmak. <b>Düşmanın eline düşmemek için bir yol bulmalıyız. </b>'),
(926,	'Eline su dökemez',	'Sözü edilen kişi, değerce ondan çok geride. <b>Sen hamur açmakta Fatma`nın eline su dökemezsin. </b>'),
(927,	'Elini çabuk tutmak',	'Hızlı davranmak, acele etmek. <b>Elimizi çabuk tutup şu kömürü yağmura yakalanmadan taşıyalım. </b>'),
(928,	'Elini kana bulamak',	'Birini öldürmek veya yaralamak. <b>Zavallı çocuk, boş yere elini kana buladı. </b>'),
(929,	'Elini kolunu sallaya sallaya gelmek',	'Bir işten sonuç almaksızın dönmek, gelirken hiçbir armağan getirmemek.'),
(930,	'Elini kolunu sallaya sallaya gezmek',	'Pervasızca, çekinmeden, kimseden korkmadan dolaşmak. <b>Bunca ağır suç işlemesine rağmen elini kolunu sallaya sallaya gezmesi şaşılacak şey doğrusu. </b>'),
(931,	'Elinin hamuruyla erkek işine karışmak',	'Anlamadığı, bilmediği, beceremediği işleri yapmaya kalkışmak (kadınlar için).'),
(932,	'Elini sıcak sudan soğuk suya sokmamak',	'Çok nazlı olmak, evde hiçbir iş yapmamak, zor işlerden kaçınmak. <b>Ne kadınmış o da, elini sıcak sudan soğuk suya soktuğunu görmedim daha!</b>'),
(933,	'Eli sıkı',	'Kolay para harcamayan, cimri, çok tutumlu. <b>Bu kadar eli sıkı bir adam olmak zorunda değilsin. </b>'),
(934,	'Eli uzun',	'Hırsız, fırsat buldukça bir şeyler aşırmaktan geri kalmayan.'),
(935,	'Eli varmamak',	'Bir işi yapmaya gönlü razı olmamak. <b>Bulaşıkları yıkamaya bir türlü elim varmıyor. </b>'),
(936,	'Eli yatmak',	'Bir işe eli alışkın olmak, bir işi yapabilecek el becerisi bulunmak.'),
(937,	'Eliyle koymuş gibi bulmak',	'Aradığı şeyi söylenen yerde çok kolay bulmak. <b>Onca şeyin arasında küçücük düğmeyi eliyle koymuş gibi buluverdi. </b>'),
(938,	'El kadar',	'Küçük, küçücük. <b>El kadar çocuk işime karışamaz benim. </b>'),
(939,	'El kaldırmak',	'1. Kendisinden büyüğe vurmak için elini kaldırmak. <br />2. Bir şey söylemek istediğini, oy verdiğini elini kaldırarak belirtmek. <b>Sen ne cüretle babana el kaldırırsın!</b>'),
(940,	'El kapısı',	'1. Bir kızın gelin gittiği ev. <br />2. Yabancıların memleketi, evi, yurdu. <b>Yıllarca el kapılarında çalıştım durdum. </b>'),
(941,	'El koymak',	'1. Bir meselenin yetkili organlarca incelenmeye başlaması. <br />2. Buyruğu altına almak, hükümetçe uygun görülen mal, arazi ve kuruluşa hâkim olmak. <b>Hükümetin el koyduğu arazi burdan başlıyor. </b>'),
(942,	'Elle tutulur gözle görülür',	'Çok açık, gizli bir tarafı yok. <b>Şu zamana kadar elle tutulur gözle görülür bir iş yaptın mı sen?</b>'),
(943,	'El oğlu',	'1. Yabancı. <br />2. Damat. <b>El oğluna güvenme sakın!</b>'),
(944,	'El sürmemek',	'1. Dokunmamak, hiç değmemek. <br />2. Yapımına başlamamak. <b>İşe el sürmeye vakit bulamadım daha. </b>'),
(945,	'El uzatmak',	'1. Birine yardım etmek. <br />2. Dokunmaya, almaya çalışmak. <b>O bizim bir yakınımız, ona elimizi uzatmalıyız hemen. </b>'),
(946,	'El üstünde tutulmak',	'Çok değer verilip sevilmek, kendisine büyük ölçüde saygı gösterilmek. <b>Dedem ailemizde el üstünde tutulurdu. </b>'),
(947,	'El yordamıyla',	'Tahminlerine, sezgilerine dayanıp elle yoklayarak. <b>El yordamıyla kibrit kutusunu buldum. </b>'),
(948,	'Emeği geçmek',	'Bir şeyin yapılmasında kendisinin de katkısı bulunmak. <b>Şu caminin yapımında kimlerin emeği geçmedi ki. </b>'),
(949,	'Emek vermek',	'Bir şeyin meydana gelmesi için özenle ve çok çalışmak. <b>İyi bir sonuç mu almak istiyorsun? Emek ver, gayret et. </b>'),
(950,	'Emir kulu',	'Kendisine emredilen işi yapmak zorunda olan kimse. <b>Emir kulu olmak o kadar da kolay değil. </b>'),
(951,	'Eninde sonunda',	'Nihayet, en sonunda. <b>Eninde sonunda onu bulacağım. </b>'),
(952,	'Enine boyuna',	'1. Her yönü ile, eksiksiz, bütün ihtimalleri göz önünde tutarak. <br />2. İri yarı, gösterişli (adam). <b>Şu meseleyi enine boyuna bir kez daha düşünelim. </b>'),
(953,	'Ensesi kalın',	'Parası çok, varlıklı, sözü geçer, ödeme gücü yüksek (kimse). <b>Neden şu ensesi kalın adamlardan yardım istemiyorsunuz. </b>'),
(954,	'Ensesinde boza pişirmek',	'Sıkıştırıp tedirgin etmek, eziyet etmek. <b>İşlerin yavaş gittiğini gören patron işçilerin ensesinde boza pişirmeye başladı. </b>'),
(955,	'Ensesine yapışmak',	'Yakalamak. <b>Bir hamlede ensesine yapıştı çocuğun. </b>'),
(956,	'Ense yapmak',	'Yemek, içmek ve keyfine bakmak, hiç iş yapmamak. <b>Ense yapmayı bırak da biraz işle ilgilen. </b>'),
(957,	'Er geç',	'Ne zaman olsa, mutlaka. <b>Er geç onu bulacağım. </b>'),
(958,	'Esamisi okunmamak',	'Adı anılmamak, değer verilmemek. <b>Onun buralarda hiç esamisi okunmaz. </b>'),
(959,	'Es geçmek',	'Dikkate almamak, sözleri arasında o konuya dokunmamak. <b>Borç meselesini es geçmesine fırsat vermeyin. </b>'),
(960,	'Esip savurmak',	'Bağırıp çağırmak, öfke ile atıp tutmak. <b>Davet edilmediğini öğrenince esip savurmaya başladı. </b>'),
(961,	'Eski çamlar bardak oldu',	'Devir değişti, eski durumların, tutumların bir önemi kalmadı.'),
(962,	'Eski defterleri karıştırmak',	'Eski olayları, işleri bir çıkar umuduyla tekrar ele almak, yeniden gündeme getirmek. <b>Eski defterleri karıştırmayı bırak artık</b>.'),
(963,	'Eski hamam eski tas',	'Hiçbir şey değişmemiş, eski durumda kalmış. <b>Köy aynı, insanlar aynı, eski hamam eski tas. </b>'),
(964,	'Eski kafalı',	'Yeniliğe açık olmayan, yaşayış ve düşünce itibariyle eskiye bağlı. <b>Eski kafalı insanlar gittikçe azalıyor mu ne?</b>'),
(965,	'Eski kurt',	'Tecrübeli, görmüş ve geçirmiş, mesleğini iyi bilen, hileyi ve düzeni deneyimi sayesinde hemen anlayan. <b>O da eski kurtlardandır. </b>'),
(966,	'Eski toprak',	'Yaşlılığına rağmen dinçliğini, dayanıklılığını hâlâ sürdüren, gücünü kaybetmemiş kimse. <b>Sen eski topraksın, bizim gibi birkaç genci daha cebinden çıkartırsın. </b>'),
(967,	'Eşeğini sağlam kazığa bağlamak',	'İşini güvenli kılacak önlemler almak. <b>Ne demişler; Eşeğini sağlam kazığa bağla, sonra Allah`a ısmarla. </b>'),
(968,	'Eşek kadar',	'Büyük, iri; aşırı derecede gelişmiş. <b>Eşek kadar oldu ama hiç söz dinlemiyor. </b>'),
(969,	'Eşek sudan gelinceye kadar dövmek',	'Adamakıllı, çok ve iyi dövmek. <b>Eğer aklını başına toplamazsan seni eşek sudan gelinceye kadar döveceğim, anladın mı?</b>'),
(970,	'Eşek şakası',	'Ağır, hoşa gitmeyen, incitici, kaba şaka. <b>Ben eşek şakasından hiç hoşlanmam. </b>'),
(971,	'Eşiğine yüz sürmek',	'Bir isteğinin yerine getirilmesi için bir kimseye yalvarmak, önünde eğilmek. <b>İnsanların eşiğine yüz sürülmemesi gerekir. </b>'),
(972,	'Eşiğini aşındırmak',	'Bir işi yaptırmak, gördürmek için bir yere çok gidip gelmek. <b>Şu köy yolu için hükümet eşiğini aşındırıp durduk. </b>'),
(973,	'Eşref saat',	'1. İş görecek kimsenin uysal davranacağı, aksilik çıkarmayacağı zaman. <br />2. Bir işin olumlu yola girmesi için en uygun zaman. <b>İzin alabilmek için müdür beyin eşref saatini kollamaya başladı. </b>'),
(974,	'Eteği ayağına dolaşmak',	'Telâş, korku ve heyecandan yürüyüşünü ve yapacağı işi şaşırmak.'),
(975,	'Eteğine yapışmak',	'1. Bir kimsenin manevî desteğini istemek. <br />2. Varlıklı, sözü geçer bir kimseden yardım ve himaye istemek. <b>Korkudan annesinin eteğine yapıştı. </b>'),
(976,	'Etekleri tutuşmak',	'Çok telâşlanmak, heyecanlanmak. <b>Babasını parkta göremeyince etekleri tutuşmaya başladı, yoksa gelmeyecek miydi?</b>'),
(977,	'Etekleri zil çalmak',	'Çok sevinmek, işler yolunda olmak. <b>Yazılı sınavı umduğundan iyi geçen Halit`in etekleri zil çalıyordu. </b>'),
(978,	'Etek öpmek',	'Yaltaklanmak, dalkavukluk etmek; birine yaranmak için katına çıkıp o kimsenin eteğini öpme davranışı içinde olmak. <b>Bu makama etek öpe öpe çıktı soysuz herif. </b>'),
(979,	'Eti ne butu ne?',	'1. İmkânları, parası az. <br />2. Çelimsiz, zayıf, küçük. <b>Ona baskı yapma, zavallının eti ne butu ne?</b>'),
(980,	'Eti senin kemiği benim',	'Çocuk velilerinin öğretmene ya da ustaya çocuğun eğitiminde kendine tam yetki verdiğini anlatmak için söylenir.'),
(981,	'Et kafalı',	'Akılsız, anlayışı az, kavrayışı kıt olan.'),
(982,	'Etliye sütlüye karışmamak',	'Kendini alâkadar etmeyen meselelerden, toplumu derinden etkileyen olaylardan uzak durmak, kaçınmak ve hiçbiriyle ilgilenmemek. <b>Kendine sahip çık, sakın etliye sütlüye karışayım deme oğlum. </b>'),
(983,	'Etrafında dört dönmek',	'İstediğini elde etmek amacıyla bir kimsenin, bir şeyin yanından ayrılmamak, ona aşırı ilgi göstermek. <b>Çocuklar Nasreddin Hoca`nın etrafında dört dönmeye başladılar. </b>'),
(984,	'Et tırnak olmak',	'Sıkı bir ilişkiye girmek, birbirinden kopmamak.'),
(985,	'Ettiğini bulmak',	'Yaptığı bir kötülüğün cezasını görmek.'),
(986,	'Ev açmak',	'Ayrı bir eve çıkmak, yerleşmek. <b>Evlendikleri günün ertesinde ev açmaya karar verdiler. </b>'),
(987,	'Evde kalmak',	'Yaşı ilerleyen kızın evlenememesi. <b>Evde kalmak korkusu zavallı kızı yiyip bitiriyordu. </b>'),
(988,	'Evdeki hesap çarşıya uymamak',	'Önceden tasarlanan, düşünülen bir iş umulduğu gibi gitmemek, başka bir yönde gelişmek. <b>O kadar uğraştık ama evdeki hesap çarşıya uymadı, bu paraya istediğimiz gibi bir ev bulamadık. </b>'),
(989,	'Evlât acısı gibi içine çökmek',	'Kaybettiği bir şey için çok üzülmek. <b>Bahçeye diktiği güllerinin dipten sökülüp atılması evlât acısı gibi içine çökmüştü. </b>'),
(990,	'Eyere de gelir semere de',	'Her işe uyar, her işe yarar, ince işler için de kaba işler için de kullanılabilir.'),
(991,	'Eyüp sabrı',	'Peygamber Hz. Eyyub` un başına gelen hastalığa sabredip, bundan dolayı şikâyet etmemesi; güçlük ve üzüntülere, hastalığa karşı sabretmesinden hareketle, en ağır ve sürekli üzüntülerden bile yakınmayanın büyük ve uzun sabrını anlatmak için kullanılır.'),
(992,	'Eyvallah demek',	'1. Razı olmak, kabul etmek. <br />2. Ayrılırken <b>Allah`a ısmarladık</b> anlamında kullanılır.'),
(993,	'Eyvallah etmemek',	'Minnet altına girip boyun eğmemek. <b>Aç kaldı, susuz kaldı ama kimseye eyvallah etmedi. </b>'),
(994,	'Ezbere iş görmek',	'İncelemeden, özenmeden, gerekli olan bilgiyi almadan, gelişi güzel iş yapmak. <b>Ben sana ezbere iş görme demedim mi?</b>'),
(995,	'Ezilip büzülmek',	'Güç bir duruma düştüğünü, utandığını, sıkıldığını davranışlarıyla belli etmek. <b>Hiçbir insanın karşımda ezilip büzülmesine tahammülüm yoktur. </b>'),
(996,	'Faka basmak',	'Tuzağa düşmek, aldatılmak. <b>Beni nasıl faka bastırdılar anlayamadım bir türlü!</b>'),
(997,	'Fareler cirit oynamak',	'Bir yer ıssız olmak, kimseler bulunmamak. <b>Koca köyde fareler cirit atıyordu. </b>'),
(998,	'Farkına varmak',	'Gözüne çarpmak, orada bulunduğunu anlamak, fark etmek. <b>O kalabalıkta senin farkına varacaklarını sanmıyorum. </b>'),
(999,	'Felce uğramak',	'1. Bir işin tamamen bozulması, durup ilerleyemez olması. <br />2. Hastalık sebebiyle organlarının bir kısmı çalışamaz duruma gelmek, kötürüm olmak. <b>Yaptığımız işin felce uğramasından korkuyorum. </b>'),
(1000,	'Feleğin çemberinden geçmek',	'Hayatta çok günler görmüş, acı tatlı olaylar yaşayıp tecrübe kazanmış, olgunlaşmış. <b>O ihtiyar mı? Feleğin çemberinden geçmiş biridir o. </b>'),
(1001,	'Fellik fellik aramak',	'Telâşla, hemen her köşeye bakarak heyecanla aramak. <b>Bütün her yeri fellik fellik aradım ama bıçağı bulamadım. </b>'),
(1002,	'Felsefe yapmak',	'Olayların sebep ve sonuçları üzerine kendince birtakım soyut düşünceler ileri sürmek.'),
(1003,	'Fena etmek',	'Kötü duruma düşürmek, işini bozmak, zor durumda bırakmak, dövmek. <b>Biraz daha konuşursan seni fena edeceğim. </b>'),
(1004,	'Fener alayı',	'Bayram gecelerinde kalabalık halk topluluklarının, ellerinde fener veya meşalelerle şehri dolaşarak yaptıkları gösteri.'),
(1005,	'Feragat sahibi',	'Gönlü tok, özveri gösterebilen, fedakârlık yapabilen.'),
(1006,	'Fermanlı deli',	'Deli olduğu herkesçe bilinen, zır deli. <b>Halk bu ülkeyi fermanlı delilerin eline bırakmayacaktır. </b>'),
(1007,	'Ferman dinlememek',	'Kural, yasa, söz dinlememek; hiçbir yerden buyruk almamak. <b>Âşığın gönlü ferman dinlemez oldu. </b>'),
(1008,	'Fesat kumkuması',	'Tamamiyle kötülük düşünen, insanları birbirine düşürecek işler yapan, ortalığı karıştıran.'),
(1009,	'Fırıldak çevirmek',	'Düzen kurmak, hileli iş görmek. <b>Yine ne fırıldak çeviriyorsun sen?</b>'),
(1010,	'Fırsat düşkünü',	'Çıkar sağlamak, kötülük yapmak için fırsat kollayan kimse. <b>Fırsat düşkünü insanlardan nefret ederim. </b>'),
(1011,	'Fikir almak',	'Birinin düşüncesinden yararlanmak. <b>Fikir alınacak insanlar konularında ehil kişiler olmalı. </b>'),
(1012,	'Fikir vermek',	'1. Bir konuda düşüncesini bildirmek. <br />2. Bir konuda yol gösterici bilgi edinmek. <b>Nasıl yapmalıyım? Bana biraz fikir versenize. </b>'),
(1013,	'Fikir yürütmek',	'Bir konu üzerinde kendi düşüncesini söylemek, tahminlerde bulunmak. <b>Bu konuda fikir yürütmek işime gelmiyor. </b>'),
(1014,	'Fincancı katırlarını ürkütmek',	'Zararı dokunacak bir kimsenin hoşuna gitmeyen bir davranışta bulunmak. <b>Kaymakamla konuşurken dikkatli ol, fincancı katırlarını ürkütme sakın!</b>'),
(1015,	'Fink atmak',	'Hiçbir şeye aldırmadan gönlünce gezip eğlenmek, şurada burada oynayıp zıplamak.'),
(1016,	'Fiskos etmek',	'Birilerinin bulunduğu bir yerde birkaç kişi gizlice ve alçak sesle konuşmak. <b>Utanmıyor musunuz bu kadar kişi içinde fiskos etmeye?</b>'),
(1017,	'Fitil olmak',	'1. Çok içip sarhoş olmak. <br />2. Aşırı ölçüde kızmak. <b>Fitil oluyorum şu adamın hareketlerine!</b>'),
(1018,	'Fitne sokmak',	'İnsanları birbirine düşürecek, aralarını bozacak davranışta bulunmak, sözler sarf etmek.'),
(1019,	'Fiyat biçmek',	'Bir şeyin değerini belirlemek, para karşılığını tespit etmek. <b>Bu malın fiyatını biçmek o kadar kolay değil. </b>'),
(1020,	'Fiyatı dondurmak',	'Fiyatın yükselmesini önlemek, fiyatların olduğu gibi kalmasını sağlamak. <b>Belediye et fiyatlarını dondurmaya yanaşmıyor. </b>'),
(1021,	'Fiyat kırmak',	'Fiyatı birilerinin verdiğinden az vermek, fiyatı düşürmek. <b>Müteahhitlerden ikisi anlaşarak ihalede fiyat kırma yoluna gittiler. </b>'),
(1022,	'Fol yok yumurta yok',	'Ortada (bir konu ile ilgili) hiçbir belirti olmadığı hâlde varmış gibi bir kuşkuya düşmek. <b>Henüz ortada fol yok yumurta yok, sen adama para ödemeye kalkışıyorsun. </b>'),
(1023,	'Fora etmek',	'Açmak, çözmek. <b>Bütün yelkenleri fora ettik. </b>'),
(1024,	'Formül bulmak',	'Bir çözüm, işi çözümleyecek çıkar yol bulmak. <b>Sabahtan beri bir formül bulmaya çalışıyorum, sense yatıyorsun!</b>'),
(1025,	'Forsu kalmamak',	'Sözü geçmez olmak; bir konuda saygınlığı, gücü kalmamak. <b>Adamları arasında da forsu kalmayacak onun. </b>'),
(1026,	'Foyası meydana çıkmak',	'Yalanı, dolanı, hilesi, kötü niteliği, kusuru ortaya çıkmak. <b>Yakında onun da diğerleri gibi foyası meydana çıkacak. </b>'),
(1027,	'Fukara babası',	'Yoksulları koruyup gözeten, onlara yardım elini uzatan, elden geldiğince yardım etmeyi seven kimse.'),
(1028,	'Funda demir etmek',	'Demir atma komutu vermek. <b>Körfeze iyice girince kaptan funda demir edin dedi. </b>'),
(1029,	'Fütur getirmemek',	'Bezginlik getirmemek, umutsuzluğa düşmemek. <b>Sakın fütur getirme, göreceksin başaracağız. </b>'),
(1030,	'Gafil avlanmak',	'Hiç beklenmedik bir sırada yakalanmak, habersiz ve hazırlıksız olduğu sırada zor duruma düşürülmek. <b>Ben gafil avlanacak bir insan değildim ama oldu bir kere. </b>'),
(1031,	'Gaflet basmak',	'Uykusu gelmek. <b>Siz konuşurken beni bir gaflet bastı ki hiç sorma, sizin konuştuklarınızı anladım diyemem. </b>'),
(1032,	'Gam yememek',	'Kaygılanmamak, tasa etmemek, üzülmemek. <b>Seni bir kez daha gördüm ya, artık gam yemem. </b>'),
(1033,	'Gani gönüllü',	'Cömert, eli bol, vermekten kaçınmayan. <b>Gani gönüllü insanlara artık günümüzde pek rastlanmıyor. </b>'),
(1034,	'Gâvur etmek',	'Boşuna harcamak, işe yaramaz duruma getirmek, yerinde harcamamak. <b>Onca parayı bu eve verip gâvur etti. </b>'),
(1035,	'Gâvur inadı',	'Yok edilemeyen, önüne geçilemeyen, yumuşatılamayan inat. <b>Adamın yine gâvur inadı tuttu, gelmem deyip duruyor. </b>'),
(1036,	'Gazel okumak',	'1. Gazel söylemek. <br />2. Kandırmak ve oyalamak için boş sözler söylemek. <b>Boşuna gazel okuma, kandıramazsın beni!</b>'),
(1037,	'Gece kuşu',	'Geceleri gezip dolaşan, bunu huy edinen kimse. <b>Bizim oğlan iyice gece kuşu oldu. </b>'),
(1038,	'Geceyi gündüze katmak',	'Ara vermeden, devamlı çalışmak; büyük çaba göstermek. <b>Geceyi gündüze katıp çalıştık ve bu evi yaptık. </b>'),
(1039,	'Geçer akçe',	'Herkesçe aranılan, beğenilen, değerli (şey). <b>Elimizdeki tek geçer akçemiz şu arabadır. </b>'),
(1040,	'Geçimini sağlamak',	'Yaşamak için gerekli olanı elde etmek. <b>Geçimini sağlamak için hemen her yola başvurdu. </b>'),
(1041,	'Geçmişini karıştırmak',	'Birinin ölmüşlerini yermek veya onlara sövmek.'),
(1042,	'Geçti Bor`un pazarı (sür eşeğini Niğde`ye)',	'<b>İş işten geçti artık, fırsatı kaçırdın</b> anlamında kullanılır.'),
(1043,	'Gel gelelim',	'<b>Fakat, ama, ancak</b> ve <b>Ne çare ki. . </b> anlamlarında kullanılır. <b>Gel gelelim onlara, daha teklifimizi kabul etmediler. </b>'),
(1044,	'Gelip çatmak',	'Vakti gelmek, kaçınılmaz olmak, çok yakında olmak. <b>Ödeme gününün gelip çatacağını hiç düşünmedin mi?</b>'),
(1045,	'Gel keyfim gel',	'Bir durumdan duyulan memnunluk, işlerin yolunda gitmesi anlatılır.'),
(1046,	'Gel zaman git zaman',	'Aradan epeyce bir zaman geçtikten sonra. <b>Gel zaman git zaman bu ikisi beraberce yaptılar bu evi. </b>'),
(1047,	'Gemi azıya almak',	'1. Söz dinlemez olmak. <br />2. At, gemi azıları arasına alıp etkisiz bırakarak süvarisinin yönetiminden çıkmak ve kendi istediğince koşmak.'),
(1048,	'Geniş gönüllü',	'Heyecan ve telâş göstermeyen, merak etmeyen, olayları hoş karşılayan. <b>Geniş gönüllü olmak benim için o kadar kolay değil. </b>'),
(1049,	'Geri basmak',	'Geri geri gitmek. <b>Heyecanlanınca geri basmaya başladı. </b>'),
(1050,	'Geri çekilmek',	'1. Kaçmak, bulunduğu yerden arka arkaya doğru gitmek. <br />2. Karıştığı bir işi sürdürmekten ya da sürdürenler arasında bulunmaktan vazgeçmek. <b>Düşmanın çokluğu karşısında geri çekilmekten başka çaremiz kalmamıştı. </b>'),
(1051,	'Geri çevirmek',	'İade etmek, geldiği yere göndermek, kabul etmemek. <b>Ona aldığım hediyeyi rüşvettir diye geri çevirdi. </b>'),
(1052,	'Geri durmamak',	'Bir işe girmekten kaçınmamak, o işe girişmek. <b>Ona bu işi yapmaktan geri durmamasını söyle, sonunda başaracaktır. </b>'),
(1053,	'Geri hizmet',	'1. Ordunun çeşitli gereksinimleri ile ilgili işlerin tümü. <br />2. Etkinliği ikinci dereceden sayılan, kolay görev. <b>Senin bu savaşta, geri hizmette bulunacağını söylediler bana. </b>'),
(1054,	'Geri kafalı',	'Yenilikleri kabul etmeyen, bağnaz, kafası hurafelerle dolu.'),
(1055,	'Gıcık tutmak',	'Bir süre boğaz gıcıklanmasına yakalanmak, konuşamamak. <b>Gıcık tuttuğu için konuşmasını yarıda kesmek zorunda kaldı. </b>'),
(1056,	'Gıcık vermek',	'1. Birini kızdırıp sinirlendirmek. <br />2. Boğazı yakıp kaşındırarak öksürmeye yol açmak. <b>Gıcık veren bu tatlıyı yiyemiyorum. </b>'),
(1057,	'Gık dememek',	'Hiç sesini çıkarmamak, yakınmamak, karşı çıkmamak. <b>Bütün hepsi üzerine yürüdü ama o gık demedi. </b>'),
(1058,	'Gına gelmek',	'Usanmak, bıkmak. <b>Bu işten gına geldi artık. </b>'),
(1059,	'Gırla gitmek',	'1. Bol bol ortaya dökülüp harcanmak. <br />2. Uzun sürmek.'),
(1060,	'Gırtlağına kadar borca girmek',	'Pek çok, ödenmesi zor olacak şekilde borçlanmak. <b>Nasıl gülerim, gırtlağıma kadar borca girdim. </b>'),
(1061,	'Gırtlak gırtlağa gelmek',	'Kıyasıya dövüşmek ya da dövecek hâle gelmek. <b>Komşumla gırtlak gırtlağa gelecektik az kalsın. </b>'),
(1062,	'Gidiş o gidiş',	'<b>Gitti ve kendisinden bir daha haber alınamadı</b> anlamında kullanılır.'),
(1063,	'Göbeği çatlamak',	'Birçok güçlükleri yenmek için çok uğraşmak, pek çok çaba sarf etmek. <b>Onu razı edeceğim diye göbeğim çatladı. </b>'),
(1064,	'Göbek adı',	'Yeni doğan çocuğun göbeği kesilirken konulan ad. <b>Senin göbek adın nedir?</b>'),
(1065,	'Göğsü kabarmak',	'İftihar etmek, övünç duymak. <b>Senin başarılarınla göğsüm kabarıyor oğlum. </b>'),
(1066,	'Göğüs geçirmek',	'Üzüntülü bir şekilde soluk almak, içini çekmek. <b>Eski hatıraları gözünde canlanınca derin derin göğüs geçirdi. </b>'),
(1067,	'Göğüs germek',	'Bir zorluğa dayanmak, karşı koymak. <b>Bu güne birçok zorluklara göğüs gererek geldik. </b>'),
(1068,	'Göklere çıkarmak',	'Aşırı ölçüde övmek. <b>Adamı bu basit iş için göklere çıkartıp şımarttıkça şımarttılar. </b>'),
(1069,	'Gökten zembille mi indi?',	'<b>Ona niçin ayrıcalık gösteriliyor?</b>, <b>Onun ne özelliği var ki ona özel imkânlar tanınıyor?</b> anlamında kullanılır.'),
(1070,	'Gölge düşürmek',	'Bir şeyin önemini ve değerini azaltacak, ününü düşürecek işler yapmak.'),
(1071,	'Gölge etmek',	'1. Işığa engel olmak. <br />2. Bir işin yapılmasına engel olmaya çalışmak. <b>Gölge etme de şu işi zamanında yapayım. </b>'),
(1072,	'Gölgesinden korkmak',	'Çok korkak olmak, en basit işlere bile girmekten korkar olmak. <b>Gölgesinden korkan adamlarla hiçbir işe girilmez. </b>'),
(1073,	'Gönlü bol',	'Yeterli imkânlardan mahrum olmasına rağmen eli açık davranan, cömert.'),
(1074,	'Gönlü kalmak',	'1. Gücenmek. <br />2. İstediği hâlde elde edemediği şey üzerinde isteği devam etmek. <b>Gönlüm o vitrindeki elbisede kaldı. </b>'),
(1075,	'Gönlü kara',	'Başkaları hakkında kötü düşünen, onların iyiliğini istemeyen.'),
(1076,	'Gönülden geçirmek',	'Bir şeyi yapmayı düşünmek, olmasını istemek, o şeyi düşünür olmak. <b>Ben de o işi yapmayı gönlümden geçirmiştim. </b>'),
(1077,	'Gönlünden kopmak',	'Birine iyilik yapma ya da bir şeyi verme isteği, içinde aniden doğuvermek. <b>Gönlünden kopanı vermek kadar güzel bir şey olamaz. </b>'),
(1078,	'Gönlüne göre',	'İsteğine uygun olarak, dilediğine göre. <b>Allah gönlüne göre verir inşallah. </b>'),
(1079,	'Gönlü tok',	'Fazla para ve mal istemeyen, zorunlu ihtiyacı kadarı ile yetinen, imkânları az da olsa bunu hissettirmeyen, bu durumda dahi cömert olan. <b>Onun kadar gönlü tok bir adam görmedim. </b>'),
(1080,	'Gönül almak',	'1. Sevindirmek, hoşnut ettirmek. <br />2. Kırılan, gücenen bir kimseyi güzel söz ve davranışlarla yeniden hoşnut etmek. <b>Daha fazla uzatmadan o çocukların gönlünü almalısın. </b>'),
(1081,	'Gönülden çıkarmak',	'Anmaz ve sevmez olmak. <b>Onu gönlünden çıkarmışsın anlaşılan. </b>'),
(1082,	'Gönül eri',	'Açık yürekli, güvenilir, hoşgörüsü geniş, ehli dil (kimse). <b>O ihtiyar adam tam bir gönül eriydi. </b>'),
(1083,	'Gönül kırmak (yıkmak)',	'Birini çok üzecek, gücendirecek davranışta bulunmak. <b>Gönül kırmakta üstüne yoktur onun. </b>'),
(1084,	'Gönüllü gönülsüz',	'Pek de istekli olmayarak.'),
(1085,	'Gönül okşamak',	'Birini hoş bir davranış ve sözle sevindirmek. <b>Gönlünü okşamak mı istiyorsun, bir gül uzat ona. </b>'),
(1086,	'Gönül yapmak',	'Hoşa giden davranışlarla veya sözle birinin kırgınlığını gidermek.'),
(1087,	'Görüş açısı',	'Bir soruna yaklaşma, onu ele alma biçimi. <b>Dar bir görüş açısı ile sorunlar çözümlenemez. </b>'),
(1088,	'Gövde gösterisi',	'Belli bir amaç için güçlerini birleştiren kalabalıkların yaptıkları gösteri. <b>. . . partisi büyük bir gövde gösterisi yaptı. </b>'),
(1089,	'Göz açamamak',	'İşlerinin yoğun oluşu sebebiyle başka bir şeyle ilgilenme imkânı bulamamak. <b>Şu büronun işleri yüzünden göz açamıyorum. </b>'),
(1090,	'Göz açıp kapayıncaya kadar',	'Çok çabuk, kısa bir zamanda. <b>O işi göz açıp kapayıncaya kadar yaparız. </b>'),
(1091,	'Göz açtırmamak',	'Baskı altında bulundurarak başka bir şeyle uğraşmasına fırsat vermemek. <b>Çalışan işçilere hiç göz açtırmadı. </b>'),
(1092,	'Göz alıcı',	'Alımlı; şekli, rengi ve güzelliği ile dikkat çekici. <b>Oldukça göz alıcı bir elbise. </b>'),
(1093,	'Göz atmak',	'Kısaca, dikkatli değil de şöyle bir bakıvermek; üzerinde fazla durmadan elden geçirmek. <b>Kütüphaneye şöyle bir göz atıp gitti. </b>'),
(1094,	'Göz boyamak',	'Gösterişle aldatmak, bir şeyi iyi gibi göstermek, kandırmak, yanıltmak.'),
(1095,	'Göz bebeği',	'Pek değerli, sevgili, çok önem verilen (kimse). <b>Babam benim göz bebeğimdir. </b>'),
(1096,	'Gözdağı vermek',	'Korkutmak, tehdit etmek, istediğini yaptırmak için yıldırmak. <b>Ona öyle bir gözdağı verin ki bir daha buralara ayak basmasın!</b>'),
(1097,	'Gözden çıkarmak',	'Bir malın elinden çıkmasına katlanmak, bir şeyden vazgeçmek ve yokluğuna razı olmak. <b>Evi ister istemez gözden çıkardılar. </b>'),
(1098,	'Gözden düşmek',	'Kendisine daha önce duyulan sevgi ve ilgiyi kaybetmek. <b>Eskisi gibi top oynayamayan Ali bir senede gözden düştü. </b>'),
(1099,	'Gözden geçirmek',	'1. Okumak. <br />2. Durumu incelemek. <br />3. Niteliğini anlamak için bir şeyin her yanına bakmak. <b>Yapılan işleri gözden geçirdiniz mi?</b>'),
(1100,	'Gözden kaybolmak',	'Ortadan çekilmek, görünmez olmak. <b>Adam biraz önce buradaydı ama gözden kayboldu. </b>'),
(1101,	'Gözden ırak olan gönülden de ırak olur',	'<b>Ayrı düşenlerin arasındaki sevgi de zamanla azalır</b> anlamında kullanılır.'),
(1102,	'Gözden kaçmak',	'Farkına varılmamak, ortadan çekilmek, görülmemek. <b>Nasıl oldu da gözden kaçırdık onu. </b>'),
(1103,	'Gözde tütmek',	'Çok özlemek, hasret çekmek. <b>Yıllardan beri gözümde tüten köyüme yarın kavuşuyorum!</b>'),
(1104,	'Göz dikmek',	'Bir şeyi ele geçirmek isteğinde olmak. <b>Komşusunun tarlasına göz dikti. </b>'),
(1105,	'Göz doldurmak',	'Hâli, tavrı ve görünüşü ile beklenenden çok etkilemek. <b>Vitrine konan elbiseler göz dolduruyor. </b>'),
(1106,	'Göze almak',	'Bir iş nedeniyle karşılaşabileceği her türlü zararı ve tehlikeyi önceden kabullenmek. <b>Vatan için kim ölümü göze almaz ki?</b>'),
(1107,	'Göze batmak',	'1. Başkalarını aşırı söz ve davranışlarıyla tedirgin etmek. <br />2. Kıskançlığa, çekememezliğe yol açmak. <b>Her davranışınla gözüme batıyorsun. Kendine bir çeki düzen ver. </b>'),
(1108,	'Göze çarpmak',	'Görünüşü ile dikkati üzerine çekmek. <b>O uzun boyuyla hemen göze çarpıyordu. </b>'),
(1109,	'Göze girmek',	'Yetenekleri ve davranışları ile çevresinde, bulunduğu yerde sevgi ve güven kazanmak. <b>Kısa zamanda göze girmeyi başardı. </b>'),
(1110,	'Göze göz, dişe diş',	'Misilleme; aynı biçimde kötülük yapıp öç alma, kötülüğü yapandan acısını çıkarma. <b>Düşmanla artık göze göz, dişe diş mücadele edilecektir. </b>'),
(1111,	'Göz gezdirmek',	'1. Derinlemesine incelemeden okumak. <br />2. Bir şeyi, bir yeri pek fazla dikkat etmeden çabucak incelemek. <b>Raftaki mallara şöyle bir göz gezdirip çıkalım. </b>'),
(1112,	'Göz göre göre',	'Apaçık şekilde, herkesin gözü önünde. <b>Göz göre göre yaktılar zavallının evini. </b>'),
(1113,	'Göz gözü görmemek',	'Dumandan, karanlıktan ya da yoğun tozdan hiçbir şey görülmez olmak. <b>Sokağa çıkmıştık, ancak sisten göz gözü görmüyordu. </b>'),
(1114,	'Göz hakkı',	'Görülüp de imrenilen yiyeceklerden görenlere çıkarılan pay, imrenmelerini yok edecek küçük parça. <b>Çocukların göz hakkını ayırmayı da sakın unutmayın. </b>'),
(1115,	'Göz hapsine almak',	'Gözetlemek, bir şeyin üzerinden bakışlarını ayırmamak, birinin hiçbir davranışını gözden kaçırmamak. <b>Askerler, kaçak mahkûmun sığındığı evi bir saat kadar göz hapsine aldılar. </b>'),
(1116,	'Göz kamaştırmak',	'1. Hayran bırakmak. <br />2. Güçlü, parlak bir ışığın kısa bir zaman için görüşü bulandırması, bakılan yeri görmez etmesi. <b>Kapıdan çıkar çıkmaz göz kamaştıran bir ışığın etkisine girip donakaldılar. </b>'),
(1117,	'Göz kararı',	'Gözle oranlanarak belirtilen miktar, gözle yapılan ölçme ya da oranlama. <b>Kumaşı göz kararı ölçüp verdi. </b>'),
(1118,	'Göz kesilmek',	'Bütün dikkatiyle bakmak. <b>Yoldan geçen adama göz kesildi. </b>'),
(1119,	'Göz kırpmadan',	'1. Hiç duraksayıp çekinmeden. <br />2. Acımadan, merhamet etmeden. <b>Çocukları göz kırpmadan kurşuna dizdiler. </b>'),
(1120,	'Göz kırpmak',	'Karşısındakine göz kapağını açıp kapatarak işaret vermek, bu şekilde meramını anlatmaya çalışmak; bir şeyi onayladığını ya da doğru olmadığını gözünü açıp kapayarak belirtmek. <b>Kalabalık içinde birbirlerine göz kırparak gülümsediler. </b>'),
(1121,	'Göz kırpmamak',	'1. Hiç uyumamak. <br />2. Tehlikeye aldırmamak. <b>Bu gece hiç göz kırpmadım, hep seni düşündüm. </b>'),
(1122,	'Göz kulak olmak',	'1. Korumak, bakmak, gözetmek. <br />2. Görme ve işitme yoluyla öğrenmeye çalışmak. <b>Yolda ona göz kulak ol da başına bir şey gelmesin. </b>'),
(1123,	'Gözleri bulutlanmak',	'Gözleri yaşararak çevreyi bulanık görmek.'),
(1124,	'Gözleri dolmak',	'Ağlayacak gibi olmak, göz pınarlarına yaş yürümek. <b>Hiç beklemediği bir anda beni karşısında görünce gözleri dolu dolu oldu. </b>'),
(1125,	'Gözleri fal taşı gibi açılmak',	'Hayret, şaşkınlık ve öfke gibi sebeplerle gözleri iri iri açılmış olmak.'),
(1126,	'Gözleri fıldır fıldır etmek',	'Gözleri zekice, çabuk çabuk dönerek her tarafa bakmak.'),
(1127,	'Gözleri kan çanağına dönmek',	'Uykusuzluk, ağlama, kızgınlık ya da bir şeyin kaçması sebebiyle gözlerin çok kızarmış olması.'),
(1128,	'Gözleri kapanmak',	'1. Çok uykusu gelmiş olmak. <br />2. Ölmek. <b>Yemeği yer yemez gözleri kapandı, horlamaya başladı. </b>'),
(1129,	'Gözlerine inanmamak',	'Hiç beklemediği bir anda bir şeyi görüp çok şaşırmak, bu sebeple gördüğünün gerçek olduğuna inanmamak. <b>Gözlerime inanamıyorum, sen misin Ahmet?</b>'),
(1130,	'Gözlerini (gözünü) kan bürümek',	'Çok öfkeli, kinli olmak; her kötülüğü yapacak hâle gelmek. <b>Bir adamın gözlerini kan bürümesin, ondan her türlü belâ beklenebilir. </b>'),
(1131,	'Gözlerinin içi gülmek',	'Çok sevindiğini gözlerinden ve yüzünden belli etmek. <b>Sınıfını geçtiğini öğrenen Halim`in gözlerinin içi gülüyordu. </b>'),
(1132,	'Gözleri yaşarmak',	'Üzücü ve duygulandırıcı bir durum karşısında gözlerinden yaş gelmek. <b>Gurbetteki oğlundan gelen mektup eline tutuşturulunca gözleri yaşardı. </b>'),
(1133,	'Gözleri yollarda kalmak',	'Özlemle beklemek.'),
(1134,	'Göz nuru dökmek',	'Göz emeği harcamak; gözün dikkatini, elin emeğini gerektiren ince bir iş yapmak ve işte uzun süre çalışmak. <b>Onca göz nuru döktüğü el işleri ürünleri çok ucuza satılınca kahroldu. </b>'),
(1135,	'Göz önünde tutmak (bulundurmak)',	'Dikkate almak. Herhangi bir durumun nasıl bir sonuca yol açacağını hesaba katmak. <b>Yola çıkıyorsunuz ama yağmuru da göz önünde tutun. </b>'),
(1136,	'Göz ucuyla bakmak',	'Belli etmemeye çalışarak, başını çevirmeden göz kenarı ile yandan bakmak. <b>Yabancı askerlere göz ucuyla bakmaya başladı. </b>'),
(1137,	'Gözü aç',	'Aç gözlü, doymak bilmeyen, gerektiğinden fazlasını isteyen. <b>Gözü aç insanlar topluma huzur vermezler. </b>'),
(1138,	'Gözü açık',	'Uyanık, kurnaz, çıkarlarını iyi kollayan, becerikli, zeki. <b>Senin çocuk gözü açık birisi olacak galiba. </b>'),
(1139,	'Gözü açık gitmek',	'Çok istediği şeylere kavuşamadan ölmek. <b>Halam `gurbete giden oğluma kavuşamadan ölürsem gözüm açık gider` dedi. </b>'),
(1140,	'Gözü açılmak',	'Yararlıyı yararsızı, iyiyi kötüyü ayırt edebilir duruma gelmek. <b>Yaşı büyüdükçe gözü de açılmaya başladı. </b>'),
(1141,	'Gözü arkada kalmak',	'Kendisi ayrıldıktan sonra, bıraktığı şey veya kimse ile ilgili tedirginliği sürmek, merak etmek. <b>Köyden ayrılıyordu ama gözü de arkada kalmıştı. </b>'),
(1142,	'Gözü bağlı',	'1. Sorup soruşturmadan, anlayıp anlamadan. <br />2. Gafil, çevresinde olup bitenlerin farkında olmayan. <b>Hiçbir zaman gözü bağlı biri olmanı istemem senin. </b>'),
(1143,	'Gözü dalmak',	'Gözlerini bir noktaya dikerek dalgın dalgın bakmak. <b>Zavallı ihtiyar bir noktaya gözü dalmış öylece duruyordu. </b>'),
(1144,	'Gözü doymak',	'Çok istenen bir şeye kavuşup, artık istemez duruma gelmek. <b>Sanırım şimdi gözün doymuştur, daha istemezsin artık. </b>'),
(1145,	'Gözü gibi sakınmak (esirgemek)',	'Bir şeye aşırı derecede ilgi duymak, onu koruyup gözetmek, dikkatle muhafaza etmek. <b>Çocuğunu gözü gibi sakınıyordu kadıncağız. </b>'),
(1146,	'Gözü hiçbir şey görmemek',	'Heyecana, öfkeye ya da önem verdiği bir işe kapılıp başka hiçbir şeyle uğraşamaz duruma gelmek. <b>Kendinden öylesine geçmişti ki gözü hiçbir şeyi görmez olmuştu. </b>'),
(1147,	'Gözü ısırmak',	'Bir kimseyi sanki tanır gibi olmak.'),
(1148,	'Gözü ilişmek',	'İstemeden, birdenbire, rastgele görmek.'),
(1149,	'Gözü kesmek',	'Bir işi yapabilme konusunda başkalarına ve kendisine güvenmek. <b>Onca işi yapmaya gözün kesiyor mu?</b>'),
(1150,	'Gözü kara (veya pek)',	'Cesur, atak, korkusuz, tehlikeli işlere tereddüt etmeden girebilen. <b>O gözü kara bir insandı. </b>'),
(1151,	'Gözü korkmak',	'Daha önce başından geçen kötü bir denemeden sonra, birinden veya bir şeyden zarar gelebileceği endişesine kapılmak ve o işi yapmaktan çekinmek.'),
(1152,	'Gözünde büyümek',	'Olduğundan fazla büyük ya da güç görünmek. <b>Onca yolu nasıl yürüyeceğim, gittikçe gözümde büyüyor. </b>'),
(1153,	'Gözünde büyütmek',	'Bir şeyi, olayı, kimseyi veya işi abartmak.'),
(1154,	'Gözlerinden uyku akmak',	'Çok uykusu geldiği için göz kapakları kapanır gibi olmak. <b>Çocukcağızın gözlerinden uyku akıyor, şunu yatağına yatırın. </b>'),
(1155,	'Gözüne bakmak',	'1. Verilen emri yapmak üzere işaret beklemek, işareti verecek kimseyi gözlemek. <br />2. Gerektiğinden fazla dikkat göstermek, koruyup gözetmek. <b>Üç kuruş para verecek diye adamın gözünün içine bakıyor, ne derse yapıyoruz, daha ne istiyor bizden. </b>'),
(1156,	'Gözüne dizine dursun',	'Nankörlük eden kimseye karşı söylenen ilenme sözü. <b> Allah, bu nankörlüğünün cezasını versin. </b> anlamında kullanılır.'),
(1157,	'Gözüne girmek',	'Birinin sevgi ve ilgisini kazanmak.'),
(1158,	'Gözüne sokmak',	'1. Görmek istemediği bir şeyi zorla göstermek. <br />2. Bir çaba sonucu, bir kimseyi büyüğünün beğenmesini sağlamak. <b>Kalemi gözüne sokarcasına uzattı. </b>'),
(1159,	'Gözüne uyku girmemek',	'Uykusuz kalmak, hiç uyumamak. <b>Gözüme uyku girmedi bu gece. </b>'),
(1160,	'Gözünü açmak',	'1. Uyanık, dikkatli olmak. <br />2. Birisine bilgiler vererek görüşünü genişletmek. <b>Gözünü aç, işini kimseye kaptırma. </b>'),
(1161,	'Gözünü ayırmamak',	'Bir şeye devamlı bakmaktan kendini alamamak. <b>Devamlı yola bakıyor, gözünü ayıramıyordu. </b>'),
(1162,	'Gözünü çıkarmak',	'Zarara uğratmak, bir işi kötü biçimde yapmak, iyi yerine kötüyü seçmek. <b>Öyle bir taş attı ki az kalsın kuzunun gözünü çıkaracaktı. </b>'),
(1163,	'Gözünü daldan budaktan esirgememek (veya sakınmamak)',	'Tehlikeli işlere girişmekten çekinmemek. <b>Sen ki gençliğinde gözünü daldan budaktan sakınmazdın, ne oldu sana böyle?</b>'),
(1164,	'Gözünü dört açmak',	'Bir hileye düşmemek, aldanmamak için çok dikkatli olmak. <b>Gözünü dört aç da kuru odun yerine yaş odun koymasınlar. </b>'),
(1165,	'Gözünü kan bürümek',	'Birisini öldürecek kadar öfkelenmek. <b>Katillerin gözünü kan bürümüştü, önlerine çıkanı öldürüyorlardı. </b>'),
(1166,	'Gözünü kapamak',	'1. Görmezlikten gelmek, yapışına ses çıkarmamak. <br />2. Ölmek. <b>Dedem gözünü kapayınca o koca aile birdenbire dağılıvermiş. </b>'),
(1167,	'Gözünü korkutmak',	'Yıldırmak, karşı duramaz hâle getirmek. <b>İlk işi, adamlarıyla kasaba halkının gözünü korkutmak oldu. </b>'),
(1168,	'Gözünün önünden gitmemek',	'Unutamamak, her an görür gibi olmak. <b>Gözümün önünden gitmiyor onun hayâli. </b>'),
(1169,	'Gözünün yaşına bakmamak',	'Hiç acımamak, merhamet etmemek. <b>Gözünün yaşına bakmadan hapse attılar adamı. </b>'),
(1170,	'Gözü pek (kara)',	'Korkusuz, atılgan, cesur, tehlikelere aldırmayan. <b>Gözü pek insanlardan korkulmaz, çünkü onlar kartlarını açık oynarlar. </b>'),
(1171,	'Gözü sulu',	'En küçük sevinç ya da üzüntü karşısında hemen ağlayıveren, gözyaşlarını tutamayan. <b>Senin kız da amma gözü sulu biriymiş. </b>'),
(1172,	'Gözü tok',	'Elinde imkânlar olsun olmasın, mal-mülk veya paraya düşkün olmayan, cömert. <b>O mu? Gözü tok bir insandır, inanın. </b>'),
(1173,	'Gözü tutmak',	'Güvenmek, beğenmek. <b>O adamı gözüm tuttu benim. </b>'),
(1174,	'Gözü üzerinde olmak',	'Bir şeye, bir kimseye sık sık bakarak ne durumda olduğunu kontrol etmek, dolayısıyla kötü bir sonuca meydan vermemeye çalışmak. <b>Gözünüz üzerinde olsun, devamlı izleyin onu. </b>'),
(1175,	'Gözü yılmak',	'Daha önce denediği için o durumla karşılaşmaktan korkmak, o işe girişmekten çekinmek. <b>Sebzecilik işinden gözüm yıldı, bir daha bu işe girişeceğimi sanmıyorum. </b>'),
(1176,	'Gözü yükseklerde olmak',	'Hâlen bulunduğu durumdan daha yüksek bir duruma ya da mevkiye çıkmak istemek, böyle bir amacı gütmek. <b>Bundan böyle küçük şeylerle yetinme, gözün yükseklerde olsun daima. </b>'),
(1177,	'Göz yummak',	'Kabahatlerini, kusurlarını hoş karşılamak, görmezlikten gelmek, bağışlamak. <b>Sana bu yaşa gelinceye kadar göz yumdum, ama artık yeter. </b>'),
(1178,	'Göz yummamak',	'1. Hoş görmemek, bağışlamamak. <br />2. Hiç uyumamak. <b>Sabaha kadar gözlerimi yummadım. </b>'),
(1179,	'Gururunu okşamak',	'Bir kimseyi yüzüne karşı överek, becerilerini söyleyerek duygulandırmak.'),
(1180,	'Gücüne gitmek',	'Bir söz, bir davranış bir kimsenin onuruna dokunmak, o kimseye ağır gelmek. <b>Doğrusu onun bu sözleri gücüme gitti, çünkü hak etmedim o sözleri. </b>'),
(1181,	'Güllük gülistanlık',	'Sorunları bulunmayan; neşe, bolluk ve huzur içinde olan yer. <b>Ne zaman güllük gülistanlık içinde olacağız acaba?</b>'),
(1182,	'Gülmekten kırılmak',	'Aşırı ölçüde gülmek, çok gülmekten halsiz düşmek. <b>Ne matrak adamdı, hareketlerine gülmekten kırıldık hepimiz. </b>'),
(1183,	'Gülüp geçmek',	'Bir durumu umursamamak, aldırış etmemek, gülünç bulup üzerinde durmamak. <b>Gülüp geçilecek bir iş sanmayın sakın, ciddi durun üzerinde. </b>'),
(1184,	'Günaha girmek',	'Dini bakımdan suç sayılacak bir iş yapmak ya da söz söylemek. <b>Sebepsiz yere adam öldürmek, günaha girmek demektir. </b>'),
(1185,	'Günaha sokmak',	'Günah işlemesine yol açmak, dinin buyrukları dışına çıkmasına zemin hazırlamak. <b>Kes sesini de bizi günaha sokma. </b>'),
(1186,	'Günahını vermez',	'<b>Çok cimri, eli sıkı, hasis</b> kimselerin durumunu anlatmak için kullanılır.'),
(1187,	'Günah işlemek',	'Dince suç sayılan bir iş yapmak. <b>Yetimlerin malını yiyerek günah işleyenlerden mutlaka hesap sorulacaktır. </b>'),
(1188,	'Gün almak',	'1. Bir iş yapmak için ilgili kişiden gün ayırmasını; belirli bir tarih tespit etmesini istemek, randevu almak. <br />2. Yaşını bitirip daha sonraki yılın bir ya da birkaç gününü almak. <b>Doktordan gün almayı unutmamışsındır umarım. </b>'),
(1189,	'Gün batmak',	'Güneş batmak. <b>Gün batmadan yola çıkmalıyız. </b>'),
(1190,	'Güneş almak',	'Bir yere güneş ışığı ulaşmak. <b>Evin bir odası güneş almıyor. </b>'),
(1191,	'Gün görmek',	'Bolluk, mutluluk, esenlik içinde huzurlu günler geçirmek. <b>Kaygılanma evlâdım, daha çok günler göreceksin inşallah. </b>'),
(1192,	'Gün görmüş',	'Başından nice işler geçmiş, tecrübeli, görüp geçirmiş, çok yaşamış. <b>Gün görmüş insanlarla konuşmaktan zevk alırım. </b>'),
(1193,	'Gün ışığına çıkmak',	'Aydınlanmak, açıklığa kavuşmak, anlaşılır olmak. <b>İşlediği tüm suçlar yakında gün ışığına çıkacaktır. </b>'),
(1194,	'Günleri sayılı olmak',	'1. İçinde olunan günlerde ölecek olmak. <br />2. Bulunduğu yerde kalmak için birkaç günü kalmak. <b>Doktorlara bakılırsa anneannemin günleri sayılıymış. </b>'),
(1195,	'Günü birliğine',	'Sabah gidip akşam dönmek üzere. <b>Size günü birliğine konuk olmak istiyoruz. </b>'),
(1196,	'Günün adamı',	'1. Zamanın gereğine göre tutum ve yön değiştiren, çıkarını gözeten kimse. <br />2. Kendisinden o günlerde çok söz edilen.'),
(1197,	'Gününü doldurmak',	'Bir işin gerçekleşmesi için geçmesi gereken zamanı tamamlamak. <b>Gününü doldurur doldurmaz senetleri avukata verin. </b>'),
(1198,	'Gününü gün etmek',	'Eline geçen imkânları değerlendirmek, hiçbir şeyi dert edinmeyip hoşça vakit geçirmek. <b>Gününü gün eden yöneticilerden kurtulacağımız günler yakındır. </b>'),
(1199,	'Gürültüye (patırtıya) pabuç bırakmamak',	'Korkutmalara, tehditlere aldırış etmeyip dilediği gibi davranmak. <b>Öyle her gürültüye pabuç bırakacak bir adam mı sanıyorlar beni?</b>'),
(1200,	'Güven beslemek',	'Bir kimseye, bir şeye güven duymak, inanmak, itimat etmek. <b>O adama güven beslediğiniz için pişman olmayacaksınız. </b>'),
(1201,	'Güvendiği dağlara kar yağmak',	'Güvendiği kimselerden yardım alamamak, güvendiği bir şeyin işe yaramadığı anlaşılmak. <b>Çok umutlusun, inşallah güvendiğin dağlara kar yağmaz. </b>'),
(1202,	'Güven kazanmak',	'Söz, davranış ve yaptığı işlerle çevresindekileri kendisine inandırmak. <b>İnsan, önce güven kazanmalıdır. </b>'),
(1203,	'Güven vermek',	'Kendisinin güvenilir bir kişi olduğu, kendisine itimat edilebileceği duygusunu uyandırmak. <b>Oldukça güven veren birisin. </b>'),
(1204,	'Ha Hoca Ali, ha Ali Hoca',	'Farklı gibi gösterilen iki şeyin, gerçekte hiçbir değişikliği yoktur, <b>ikisi de birdir</b> anlamında kullanılır.'),
(1205,	'Ha babam (ha)',	'1. Devamlı olarak, hiç durmadan. <br />2. Karşısındakinin çabasını, gayretini artırmak için kullanılır. <b>Ha babam ha, az kaldı, bitireceğiz işi. </b>'),
(1206,	'Habbeyi kubbe yapmak',	'Önemsiz, küçük bir şeyi büyütüp mesele çıkarmak. <b>Söyle ona, habbeyi kubbe yapıp durmasın, ne olmuş çocuk biraz geç kalmış da!</b>'),
(1207,	'Haber uçurmak',	'Çabucak, gizlice haber göndermek. <b>Hemen haber uçurun köye, kaymakam bu gece misafir olacakmış!</b>'),
(1208,	'Ha bire',	'Durmadan, arka arkaya, sürekli olarak, ara vermeden. <b>Tarlada bir adam ha bire çalışıyordu. </b>'),
(1209,	'Hacet kalmamak',	'Gereği olmamak, lüzumu kalmamak. <b>Seni çağırmaya hacet kalmadı. </b>'),
(1210,	'Hacı ağa',	'Özellikle büyük kentlerde gereksiz yere çok para harcayan, taşralı bilgisiz zengin. <b>Ne bu israf! Hacı ağa mısın sen?</b>'),
(1211,	'Haddine mi düşmüş!',	'<b>Onun bunu yapmaya yetkisi yoktur; böyle bir işe nasıl, hangi yetenekle girişir? Bu işi yapması imkânsızdır</b> anlamında kullanılır. <b>Haddine mi düşmüş ki ona söz söyleyebilsin. </b>'),
(1212,	'Haddini bildirmek',	'Yetkisi dışındaki işlere karıştığı için sert bir karşılık vererek onu cezalandırmak, yola getirmek, uslandırmak, yetki sınırını bildirmek. <b>Haddini bildirin şu serseme de bir daha onun bunun malına el uzatmasın. </b>'),
(1213,	'Haddini bilmek',	'Kendi değer ve yeteneğini bilmek, üstün görmemek, kendi yapabileceği şeylerin ötesine geçmemek. <b>Merak etme sen, o haddini bilen bir çocuktur. </b>'),
(1214,	'Haddi zatında',	'Aslında. <b>Haddi zatında sen ona hakkını vermemiştin ki!</b>'),
(1215,	'Hafife almak',	'Küçümsemek, önem vermemek, <b>Beni hafife alıyorlar ama yanılıyorlar. </b>'),
(1216,	'Hak getire',	'<b>Yoktur, bulunmaz, Allah vermemiştir</b> anlamında kullanılır. <b>Öyle bir diyardayız ki su ve yiyecek Hak getire. </b>'),
(1217,	'Hak kazanmak',	'Davasında haklı olduğu meydan çıkmak, emeğinin karşılığını alabilecek duruma gelmek. <b>Emekliliğe yedi yıl sonra hak kazanacağım. </b>'),
(1218,	'Hakkı geçmek',	'1. Birisinin payından bir başkası almış olmak. <br />2. Bir şeyde veya bir kimsede emeği bulunmak. <b>Komşumun çok hakkı geçmiştir bana, onunla mutlaka helâlleşmeliyim. </b>'),
(1219,	'Hakkından gelmek',	'1. Güç bir işi başarı ile sonuçlandırmak. <br />2. Öç almak, yenmek veya cezasını vermek. <b>Siz onu bana bırakın, hakkından gelmesini bilirim. </b>'),
(1220,	'Hakkını helâl etmek',	'Geçen hakkını, emeğini bağışlamak. <b>Annem inşallah hakkını helâl eder bana. </b>'),
(1221,	'Hakkını vermek',	'1. Bir şeyin lâyıkıyla yapılması için ne gerekiyorsa ondan kaçınmamak. <br />2. Birinin çalışmasını gereğince değerlendirmek, hakkı olan şeyi vermek. <b>Çalıştırdığın kişinin hakkını vermek zorundasın. </b>'),
(1222,	'Hakkını yemek',	'Birinin hakkı olan şeyi vermemek, onu kendisine maletmek. <b>Dürüst ol, milletin hakkını yeme, yoksa boğazında kalır. </b>'),
(1223,	'Hakk-ı sükût (sus payı)',	'Bir konu üzerinde konuşmaması, bildiği şeyi söylememesi karşılığında bir kimseye sağlanan yarar.'),
(1224,	'Hak yolu',	'Cenab-ı Allah`ın insanlara kitapları ve peygamberleri ile bildirdiği, dünya hayatında tutmaları gereken yol, yaşama düzeni, doğru ve haklı yol.'),
(1225,	'Hâlden anlamak',	'Bir kimsenin içinde bulunduğu zor durumu kavrayarak, anlayıp sezerek hoşgörülü olmak, anlayış göstermek. <b>Dedem hâlden anlayan birisidir, bize iyi davranacağına eminim. </b>'),
(1226,	'Hâle yola koymak',	'Düzenlemek, tertiplemek, iyi işler bir duruma getirmek. <b>Hele şu işleri bir hâle yola koyalım, o zaman tatilini de düşünürüz. </b>'),
(1227,	'Hâli vakti yerinde',	'Zengin, oldukça varlıklı, para durumu iyi. <b>Hasan efendiler mi? Hâli vakti yerinde insanlardır onlar. </b>'),
(1228,	'Halis muhlis',	'Saf, katışıksız, temiz, eksiksiz, içinde yabancı madde bulunmayan. <b>Halis muhlis bir zeytin yağı satarız biz. </b>'),
(1229,	'Halka verir talkını kendi yutar salkımı',	'Kendi verdiği öğütlere kendisi uymaz.'),
(1230,	'Hallaç pamuğu gibi atmak',	'Bir arada, toplu bulunan şeyleri ya da kimseleri dağıtmak, parçalamak; bu yolla sağa sola, her birini bir yana atmak. <b>Sizin takımı hallaç pamuğu gibi atacağız sahadan. </b>'),
(1231,	'Halt etmek',	'Yakışıksız davranmak, uygunsuz bir söz söylemek veya kötü bir şey yapmak. <b>Halt etmişsin, bir de utanmadan anlatıyorsun. </b>'),
(1232,	'Ham ervah',	'Çiğ adam; yersiz ve yakışıksız sözleri, davranışları olan kaba kimse.'),
(1233,	'Hangi dağda kurt öldü?',	'Kendisinden hiç umulmayan, beklenilmeyen bir kimsenin olumlu davranışı görüldüğünde; <b>Nasıl oldu da böyle güzel bir iş, bir iyilik yaptı?</b> anlamında söylenir.'),
(1234,	'Hangi rüzgâr attı?',	'<b>Nasıl oldu da gelebildin? Hiç görünmüyordun, sen de gelir miydin?</b> anlamında, uzun süre bir yerde görünmeyen kimse için kullanılır.'),
(1235,	'Hangi taşı kaldırsan altından çıkar',	'1. Hemen her işte parmağı vardır. <br />2. Her işten anlar, her işe karışır ya da her işten anladığı izlenimi verir.'),
(1236,	'Hanım evlâdı',	'Nazlı büyütülmüş, zora gelmeyen, çıtkırıldım kimse. <b>Amma hanım evlâdıymışsın, çekil şuradan ben yaparım. </b>'),
(1237,	'Hapı yutmak',	'Kötü bir duruma düşmek, zarar ve ziyana uğramak. <b>Hapı yuttuk desene!</b>'),
(1238,	'Haram olmak',	'Bir şeyden gerektiği gibi yararlanamaz olmak. <b>Senin yüzünü görmek bana haram oldu. </b>'),
(1239,	'Haram para',	'Dinî bakımdan yasaklanmış yollardan elde edilen para. <b>Haram parayla ekmek alınmaz. </b>'),
(1240,	'Haram yemek',	'Dinî inançlara aykırı olarak kazanç sağlamak, haksız olarak bir şeye el atmak. <b>İnsan ol, haram yemek insana kâr getirmez. </b>'),
(1241,	'Harfi harfine',	'Tastamam, uygun, tıpatıp, gerçekte olduğu gibi. <b>Söylediklerimi harfi harfine yerine getirdin mi?</b>'),
(1242,	'Har vurup harman savurmak',	'Hesapsızca, düşüncesizce harcamak; malını, parasını ölçüsüzce, bol bol harcayıp tüketmek.'),
(1243,	'Hasret çekmek',	'Özlem duymak, epeydir ayrı kaldığı yere ya da kimseye kavuşma isteği içinde olmak. <b>Yıllardır yurdumun hasretini çekiyorum. </b>'),
(1244,	'Hasret gitmek',	'Özlediği, sevdiği bir yere ya da kimseye kavuşamadan ölmek.'),
(1245,	'Hasret kalmak',	'Özlemini duyduğu şeye uzun zaman kavuşamamak. <b>Hasret kaldım deresine, tepesine. . . </b>'),
(1246,	'Hastası olmak',	'Bir şeye çok düşkün olmak. <b>Bizim oğlan köpek hastası, hiç kapıdan eksik etmiyor. </b>'),
(1247,	'Haşir neşir olmak',	'Aralarında bulunduğu kimselerle kaynaşmak, bir arada bulunup uğraşmak; kimi işlerle ilgilenip durmak. <b>İnsanlarla haşir neşir olmayı sevdiğim söylenemez. </b>'),
(1248,	'Hatır belâsı',	'Sayılan ve sevilen kimse için katlanılan sıkıntı. <b>İnan bu işi hatır belâsına yapıyorum. </b>'),
(1249,	'Hatır gönül tanımamak (bilmemek)',	'1. İsterse en sevdiği ve saydığı olsun, gücenmesini göze alarak doğru bildiğini yapmak. <br />2. Kırıcı davranışlarda bulunmak.'),
(1250,	'Hatırı kalmak',	'Gücenmek, kırılmak. <b>Eğlenceye onu da çağıralım ki hatırı kalmasın. </b>'),
(1251,	'Hatırından çıkmamak',	'Sevdiği, saygı duyduğu birinin istediği bir şeyi yapmayı reddedememek, gönlünü kırmaktan çekinmek.'),
(1252,	'Hatırı sayılır',	'1. Önemli, saygı değer, saygın (kimse). <br />2. Oldukça çok. <b>Babam, hatırı sayılır bir kimsedir. </b>'),
(1253,	'Hava almak',	'1. Temiz havalı bir yere çıkarak dolaşmak, dinlenmek, ciğerlere temiz hava çekmek. <br />2. Eline bir şey geçmemek, umduğunu bulamamak. <br />3. İçine hava girmek. <b>Haydi, kıra çıkıp da biraz hava alalım. </b>'),
(1254,	'Hava basmak',	'1. Büyüklenmek, kibirlenmek, olduğundan fazla görünmeye çalışmak. <br />2. Bir şeyin içine hava doldurmak. <b>Amma da hava basıyorsun, onları korkutacağını mı sandın. ?</b>'),
(1255,	'Havada kalmak',	'1. Yüksek bir yerde durmak. <br />2. Sonuca bağlanamamak. <br />3. Bir iddia, dayanaksız olduğundan ispat edilememek. <b>Yaptığımız bütün iş havada kaldı. </b>'),
(1256,	'Havadan sudan konuşmak',	'Öylesine, gelişigüzel, rastgele konuşmak.'),
(1257,	'Hava hoş',	'Şu ya da bu şekilde olması arasında bir fark olmamak.'),
(1258,	'Havanda su dövmek',	'Bir işle boşuna uğraşmak. <b>Senin yaptığına havanda su dövmek derler, bırak artık şu işle uğraşmayı. </b>'),
(1259,	'Hava parası',	'Bir yeri tutmak, kiralamak ya da bir şeyi elde etmek için değeri dışında açıktan verilen para. <b>Yeri bize verecekler ama bir milyon lira hava parası istiyorlar. </b>'),
(1260,	'Havsalası almamak',	'Aklı kabul etmemek. <b>Nasıl yaparsın bana bunu, hâlâ havsalam almıyor. </b>'),
(1261,	'Hayal kırıklığı',	'Gerçekleşmesi istenilen veya umulan bir şeyin gerçekleşmemesinden duyulan üzüntü, düş kırıklığı.'),
(1262,	'Hayal meyal',	'Belli belirsiz, açık seçik belli olmayan, bulanık (bir şekilde hatırlanan). <b>O olayı hayal meyal hatırlıyorum. </b>'),
(1263,	'Hayatını kazanmak',	'Çalışıp elde ettiği para ile geçimini sağlamak. <b>Ben iyi ya da kötü hayatımı kazanıyorum, sen kendi işine bak. </b>'),
(1264,	'Hayatını yaşamak',	'Canının istediği gibi hayatını sürdürmek. <b>Bana karışmaya hakkınız yok, bırakın beni, artık hayatımı yaşamak istiyorum. </b>'),
(1265,	'Hayat memat meselesi',	'Sonucu çok tehlikeli olan, ölüm kokan bir durum. <b>Artık burada kalamam, iş hayat memat meselesine döndü. </b>'),
(1266,	'Hayat pahalılığı',	'Yiyecek, içecek ve giyecek gibi geçim için gerekli olan maddelerin pahalı olması. <b>Hayat pahalılığından herkes şikâyetçi olmaya başladı. </b>'),
(1267,	'Hayırdır inşallah!',	'1. Anlatılan bir rüyayı iyiye yormak için söylenir. <br />2. Şaşma, heyecan ve merak uyandıran durumlar karşısında söylenir.'),
(1268,	'Hayır işlemek',	'Dine ve insanlığa uygun, iyi davranışlarda bulunmak. <b>Hayır işle ki öbür dünyada kurtuluşa eresin. </b>'),
(1269,	'Hayır kalmamak',	'İşe yarar, beğenilecek bir yanı ve tarafı kalmamak. <b>Bu arabalarda hayır kalmamış, yenilerini almamız gerekecek. </b>'),
(1270,	'Hayır sahibi',	'İyiliksever, yardımsever kimse. <b>Şu yoksullara uzanacak bir hayır sahibi kalmadı mı acaba?</b>'),
(1271,	'Hayra yormak',	'Bir rüya ya da olayı iyi ve yararlı bir durumun işareti görmek.'),
(1272,	'Hazıra konmak',	'Hiçbir emek sarf etmeden, çaba göstermeden başkasının emeği ile ortaya çıkmış olan şeyden yararlanmak. <b>Hazıra konarak yaşamayı kural edinmiş bu adam. </b>'),
(1273,	'Hazır bulunmak',	'1. Bir yerde kendisi bulunmak, var olmak. <br />2. Bir yere hemen gidecek, bir şeyi anında yapacak durumda olmak. <b>Yarınki toplantıda sen de hazır bulunmalısın. </b>'),
(1274,	'Hazırdan yemek',	'Yenisini kazanmadan elindekini harcamak. <b>Hemen her gün bir bahane buluyor, çalışmıyor ve hazırdan yiyiyordu. </b>'),
(1275,	'Helâl süt emmiş olmak',	'İyi huylu, doğru yoldan sapmayan, temiz bir kişi. <b>İnanmıyorum onun yaptığına, o helâl süt emmiş birisidir. </b>'),
(1276,	'Helâl olsun (Helâl ü hoş olsun)',	'1. Bunu sana gönül hoşluğu ile veriyorum, hiç pişman değilim, Allah bunu sana bağışladığıma şahit olsun. <br />2. <b>Aferin, takdire değer iş yapıyorsun</b> anlamında kullanılır.'),
(1277,	'Hele şükür!',	'Allah`a hamdolsun, beklediğimiz sonuç gerçekleşti.'),
(1278,	'Hem kel hem fodul',	'<b>Bu kadar kusuruna, bu yeteneksizliğine rağmen bir de övünüyor, üstünlük taslıyor</b> anlamında kullanılır.'),
(1279,	'Hem nalına hem mıhına (vurmak)',	'Birbirine zıt olan iki yanı da desteklemek. <b>Ben hem nalına hem de mıhına vuran adamlardan korkarım. </b>'),
(1280,	'Hem suçlu hem güçlü',	'Gerçekte kendisi suçlu olduğu hâlde suç işlememiş gibi davranan ve karşısındakini suçlamaya çalışan kimse.'),
(1281,	'Hem ziyaret hem ticaret',	'Bir yeri veya kimseyi ziyarete giden kimsenin, bu görüşmeden yararlanarak başka bir işi de yapması durumunu anlatmak için kullanılır.'),
(1282,	'Her kafadan bir ses (çıkmak)',	'Bir konu üzerinde herkesin istediği gibi, rastgele konuşması ve bu konuşmalardan bir sonuç alınamaması. <b>Ortalık kızıştı, her kafadan bir ses çıkmaya başladı, kimin ne dediği anlaşılmaz oldu. </b>'),
(1283,	'Her telden çalmak',	'Pek çok konuda bilgi sahibi olmak, içinde bulunduğu ortamın şartlarına göre her çeşit iş yapabilir olmak.'),
(1284,	'Hesaba çekmek',	'Bir kişiyi, bir makamı yaptığı işler üzerine açıklama ve savunma yapmaya çağırmak. <b>Sakın oraya gitme, seni hesaba çekecekler. </b>'),
(1285,	'Hesaba dökmek',	'Bir konu ile ilgili işlemlerin hesabını kâğıt üzerinde yapmak.'),
(1286,	'Hesaba katmak (almak)',	'Bir işi yaparken ya da yürütürken bir başka şeyi de göz önünde bulundurmak. <b>Hasan`ı da hesaba katalım, az zorluk çıkarmayacaktır bize. </b>'),
(1287,	'Hesaba (kitaba) gelmez',	'1. Beklenmedik, umulmadık. <br />2. Sayılmayacak kadar çok, pek fazla, sayısız.'),
(1288,	'Hesabı kesmek',	'Alış verişi ya da ilgiyi kesmek. <b>Dükkân sahibi, uzun zamandır borcunu ödemeyen müşterisinin hesabını kesti. </b>'),
(1289,	'Hesabını bilmek',	'Boş yere para harcamamak, tutumlu davranmak. <b>Her ev kadını hesabını bilmek zorundadır. </b>'),
(1290,	'Hesabını görmek',	'1. Alacağını ödeyip ilişkisini kesmek. <br />2. Cezalandırmak, vücudunu ortadan kaldırmak ya da öldürmek. <b>Çabuk şu adamın hesabını görün!</b>'),
(1291,	'Hesap açmak',	'1. Hesap defterinde, bir kişiye alış veriş için alacağını borcunu kaydetmek üzere bir yer ayırmak. <br />2. Bankada, gereğinde çekilmek üzere yatırılan para için işlem yapmak. <br />3. Birine kredi açmak, birine borçlanma imkânı tanımak.'),
(1292,	'Hesap etmek',	'1. Kazançla gideri karşılaştırıp bir sonuca ulaşmak. <br />2. Düşünmek, tasarlamak, ayrıntıları gözden geçirip ihtimalleri değerlendirmek. <b>Hesap etmeden sakın işe girişmeyin!</b>'),
(1293,	'Hesap görmek',	'Taraflarca alacakla vereceği karşılaştırıp ödeşmek. <b>Çok uzadı, hesap görmek için ne zaman bir araya geleceğiz?</b>'),
(1294,	'Hesap kitap',	'Düşünüp taşındıktan sonra, hesap sonunda. <b>Hesap kitap, baktım işler kötüye gidiyor; hemen sizi çağırdım. </b>'),
(1295,	'Hesapsız kitapsız',	'1. Sorumsuz, ölçüsüz, tutumsuz. <br />2. Deftere geçirilmeden, herhangi bir belgeye dayanmadan. <b>Ne hesapsız kitapsız işlerin içine girmişiz de haberimiz yokmuş. </b>'),
(1296,	'Hesap sormak',	'Bir kimseyi kanunsuz, kural dışı, ahlâka aykırı, usulsüz davranış ve sözlerinden ötürü sorgulamak, o kişiden savunma istemek. <b>Size hesap sormak için mutlaka geri döneceğim. </b>'),
(1297,	'Hesaptan düşmek',	'Borçtan, alacaktan, hesaptan çıkarıp yok saymak. <b>Elli bin lirayı hesaptan düşmeyi unutmadın inşallah. </b>'),
(1298,	'Hesap tutmak',	'Alış verişle ilgili alacağı ve vereceği bir kâğıda ya da deftere yazmak.'),
(1299,	'Hesap vermek',	'1. Herhangi bir davranışının ya da sözünün sebebini açıklamak <br />2. Bir işin sorumluluğunu üstlenmek. <b>Rahat olun, bu konuda hesap vermek bana düşer. </b>'),
(1300,	'Hevesi kursağında kalmak',	'Çok istediği, imrendiği, kavuşmak dilediği şeyi elde edememek. <b>Pikniğe gitmek istiyorduk, yağmur yağınca hevesimiz kursağımızda kaldı. </b>'),
(1301,	'Hevesini almak',	'İmrendiği, çok istediği şeye kavuşup ona doymak.'),
(1302,	'Heyheyleri tutmak (üstünde)',	'Çok kızıp sinirlenmek.'),
(1303,	'Hık mık etmek',	'Bir işi yapmamak için bahaneler ileri sürmeye çalışmak, bir soruyu cevaplandırırken net şeyler söylememek. <b>Hık mık edip durma, bu işi eninde sonunda yapacaksın!</b>'),
(1304,	'Hık demiş burnundan düşmüş',	'<b>Her durumuyla ona çok benziyor</b> anlamında kullanılır.'),
(1305,	'Hır çıkarmak',	'Kavga, gürültü, patırtı ve olaya sebep olmak. <b>Orada hır çıkarmaya kalkışmayacaksın değil mi?</b>'),
(1306,	'Hızır gibi yetişmek',	'Dara düştüğü, çok sıkıştığı, çaresiz kaldığı bir zaman da, beklemediği bir kişi yardımına yetişmek.'),
(1307,	'Hiçe saymak',	'Hiç önem ve değer vermemek.'),
(1308,	'Hiç yoktan',	'Sebepsiz, ortada hiçbir neden yokken. <b>Hiç yoktan adamı dövemezsiniz ya!</b>'),
(1309,	'Hizaya gelmek',	'1. Düz çizgi durumunda dizilmek. <br />2. Aykırı, yanlış davranışlardan vazgeçmek; doğru yola gelmek, düzelmek.'),
(1310,	'Hodri meydan',	'<b>Kendine güvenen ortaya çıksın</b> anlamında kullanılır.'),
(1311,	'Hop oturup hop kalkmak',	'Ya heyecanından ya da öfkesinden yerinde duramaz olmak.'),
(1312,	'Hora tepmek',	'1. Ayaklarını yere vurarak oynamak. <br />2. Gürültü çıkarmak. <b>Yandaki sınıfta hora tepiyor, ortalığı birbirine katıyorduk ki. . . </b>'),
(1313,	'Hor görmek (veya bakmak)',	'Önem vermemek, değersiz saymak, adam yerine koymamak, küçümsemek. <b>Beni, yoksul diye hep hor gördüler. </b>'),
(1314,	'Hor kullanmak',	'Özen göstermeden, kabaca, dikkat etmeyerek, hırpalayarak kullanmak. <b>Çok hor kullanmışsınız bu dolabı. </b>'),
(1315,	'Hoş beş etmek',	'Şundan bundan konuşarak sohbet etmek. <b>O iki ihtiyar kadın hoş beş etmek için yaratılmışlar sanki. </b>'),
(1316,	'Hurdası çıkmak',	'İşe yaramayacak, kullanılamayacak hâle gelmek.'),
(1317,	'Huyuna suyuna gitmek',	'İsteklerine, alışkanlıklarına, yapısına göre onu kızdırıp ürkütmeyecek davranışlarda bulunmak.'),
(1318,	'Huyunu suyunu almak',	'Onun özelliklerini, davranışlarını ve karakterini yapısına geçirmek.'),
(1319,	'Huzur vermek',	'Gönül rahatlığı, iç dirliği vermek; dinlendirmek.'),
(1320,	'Huzurunu kaçırmak',	'Huzurunu bozmak, tedirgin ve rahatsız etmek.'),
(1321,	'Hüküm giymek',	'Mahkemece ya da birileri tarafından kendisine ceza verilmek.'),
(1322,	'Hüküm sürmek',	'1. İş başında olmak. <br />2. Yaygın olmak. <br />3. Bir şeyin güçlü varlığı sürüp gitmek. <b>Beşinci Kral beş yıl hüküm sürdü. </b>'),
(1323,	'Hükümet kapısı',	'Devlet dairesi. <b>Hükümet kapıları halka açık kılınmalıdır. </b>'),
(1324,	'Hür düşünüş',	'İstediğini, düşündüğünü baskı altında kalmadan söyleme.'),
(1325,	'Hüsn-ü kuruntu',	'İhtimalî bulunmadığı hâlde güzel bir şeyin olacağını sanma, hayal etme, buna kendini inandırma.'),
(1326,	'Hüd dağı gibi şişmek',	'Bir hastalık sebebi ile bir tarafı, özellikle de karın tarafı şişmek.'),
(1327,	'Icığını cıcığını çıkarmak',	'1. Her yanını ellemek, didiklemek. <br />2. Bir meseleyi en ince ayrıntılarına kadar soruşturmak, incelemek. <b>İyice ıcığını cıcığını çıkardınız meselenin. </b>'),
(1328,	'Ikınıp sıkınmak',	'Bir işi yapabilmek için kendini çok zorlamak. <b>Ikınıp sıkındı ama bir çare bulamadı. </b>'),
(1329,	'Isıtıp ısıtıp önüne koymak',	'Daha önce meydana gelmiş bir olayı ya da bir işi bir düşünceyi yeniden, sık sık tekrarlamak.'),
(1330,	'Iska geçmek',	'1. Hedefe isabet ettirememek, vuramamak. <br />2. Üzerinde durmamak, önem vermemek, atlamak. <b>Bu sefer de ıska geçersen kaybedeceksin. </b>'),
(1331,	'Iskartaya çıkarmak',	'İşi yaramaz, değersiz bularak bir yana atmak. <b>Beni hiç kimse ıskartaya çıkaramaz. </b>'),
(1332,	'Işığı altında',	'Bir durum veya düşüncenin konuyu aydınlatmasından yararlanarak, onu göz önünde tutarak.'),
(1333,	'Işık tutmak',	'1. Karanlık bir yeri ışıkla aydınlatmak. <br />2. Bilgisiyle, düşüncesiyle bir konuya açıklık getirmek, tutacağı yolu göstermek. <b>Kutlu Peygamber hemen her konuda ışık tutardı çevresindeki insanlara. </b>'),
(1334,	'İbret almak',	'Kötü bir olaydan etkilenerek ders almak. <b>Görmesini bilseydi ibret alırdı her hâlde. </b>'),
(1335,	'İcabına bakmak',	'1. Gereğini yerine getirmek. <br />2. Yok etmek, ortadan kaldırmak. <b>O adamın icabına bakarız, merak etme sen. </b>'),
(1336,	'İç çekmek',	'Üzüntüyle göğüs geçirmek, derin derin soluk alıp hıçkırıkla ağlamak. <b>Yavrucağın iç çekişi dayanılır gibi değildi. </b>'),
(1337,	'İç etmek',	'Eline geçen bir şeyi sahibine bildirmeden kendisine mal etmek, ortadan kaldırıp kimseye göstermemek. <b>Babasına bildirmeden o kadar parayı iç etmiş. </b>'),
(1338,	'İç gıcıklamak',	'1. Huylandırmak. <br />2. İstek uyandırmak.'),
(1339,	'İçi açılmak',	'Sıkıntısı dağılıp gitmek, ferahlamak. <b>Denizi, kuşları, ağaçları seyre dalarım, böylelikle içim açılır, rahatlarım. </b>'),
(1340,	'İçi cız etmek',	'Ansızın içi sızlamak, çok üzülmek. <b>O zavallı ihtiyarı birden bire karşımda görünce içim cız etti. </b>'),
(1341,	'İçi çekmek',	'Canı arzu etmek, istek duymak.'),
(1342,	'İçi çıfıt çarşısı',	'1. Başkaları için daima art niyet besleyen, içinden türlü kötülükler geçiren. <br />2. Çok karışık.'),
(1343,	'İçi dışı bir',	'İkircikli olmayan, iki yüzlü davranmayan, düşündüğünü açıkça söyleyen, özü sözü bir olan. <b>İçi dışı bir olan insanlara her zaman güvenebiliriz. </b>'),
(1344,	'İçi dışına çıkmak',	'1. Kusmaktan ötürü çok fena olmak. <br />2. Bindiği taşıtın çok sarsılması yüzünden bedenî rahatsızlık duymak.'),
(1345,	'İçi erimek',	'Kaygı duymak, çok üzülmek.'),
(1346,	'İçi geçmek',	'1. İstemediği hâlde uyuya kalmak. <br />2. İşe yaramaz duruma gelmek. <br />3. Yaşlılıktan, zayıflıktan gücü azalmış olmak; hiçbir şeye ilgi duymamak. <b>O artık içi geçmiş bir ihtiyardır. </b>'),
(1347,	'İçi gitmek',	'Çok fazla istek duymak. <b>Vitrindeki kızarmış tavuklara içim gidiyordu ama param olmadığı için alıp yiyemiyordum. </b>'),
(1348,	'İçi içine sığmamak',	'Çok heyecanlanmak, coşkunluk duymak ve sevincini belli etmekten kendini alamamak. <b>Annemi karşımda görünce ne yapacağımı şaşırdım, içim içime sığmıyordu, koşup boynuna sarıldım. </b>'),
(1349,	'İçi kabarmak (kalkmak)',	'1. Midesi bulanmak. <br />2. Duygulanıp heyecanlanmak. <br />3. Taşkın bir ağlama duygusu içinde olmak. <b>Ne berbat bir koku, içimiz kabarmadan kalkalım buradan. </b>'),
(1350,	'İçi kan ağlamak',	'İçten, büyük bir üzüntü duymak; dıştan belli etmeyerek çok acımak. <b>Çocuğunun yüzüne bakarken içim kan ağlıyordu. </b>'),
(1351,	'İçi kazınmak',	'Çok acıktığından ötürü midesinde eziklik duymak. <b>Sabahtan beri açtı, içi kazınıyor ama belli etmemeye çalışıyordu. </b>'),
(1352,	'İçinden gülmek',	'Birisine sezdirmeden içten içe gülmek, eğlenmek.'),
(1353,	'İçinden okumak',	'1. Dudaklarını kıpırdatmadan, hiç ses çıkarmadan okumak. <br />2. Ses çıkarmadan sövmek, beddua etmek. <b>Hikâyeyi şimdi de içinizden okuyacaksınız. </b>'),
(1354,	'İçinden pazarlıklı',	'Sinsi, yapacağı kötülükleri sezdirmeyen. <b>Senin gibi içten pazarlıklı adamlarla iş yapmam ben. </b>'),
(1355,	'İçine atmak',	'1. Derdini, sıkıntısını kimseye söylememek. <br />2. Kendisine yapılan kötülüğe karşı sesini çıkarmamakla beraber, bunu unutmamak. <b>O her şeyi içine atar, bir gün kanser olacak diye korkuyorum. </b>'),
(1356,	'İçine dert olmak',	'Yapmak istediği bir şeyi yapamadığı için kaygılanıp üzüntü duymak. <b>Hastahanedeki arkadaşımı ziyarete bir türlü gidemedim, bu da içime dert oldu. </b>'),
(1357,	'İçine doğmak',	'Malûm olmak, bir işin olduğunu ya da olacağını sezinlemek, tahmin etmek. <b>Onun bize geleceği sanki içime doğmuştu. </b>'),
(1358,	'İçine işlemek',	'Duygulanmak, etkilenmek, dokunmak. <b>Babamın o etkili sözleri âdeta içime işlemişti sanki. </b>'),
(1359,	'İçine çekilmek (kapanmak)',	'Duygularını kimseye açmamak, çevresindeki kişilerle ilişkisini kesmek, yalnızlığa gömülmek. <b>Kardeşinin ölümünden sonra içine çekildi, kimseyle görüşmüyor. </b>'),
(1360,	'İçine kurt düşmek',	'Kuşkulanmak, kendisine zarar geleceğinden şüphe etmek. <b>Tilkiyi civarda dolaşırken gördüğü andan itibaren içine kurt düşmüştü. </b>'),
(1361,	'İçine sindirmek',	'Benimsemek, iyice kabul etmek.'),
(1362,	'İçine sinmemek',	'1. İçi rahat etmemek, yaptığı şeyden memnun olmamak. <br />2. İstediği gibi olmadığı için rahatlık, mutluluk duymamak; tadına varamamak. <b>İşi bitirdim ama hiç de içime sinmedi. </b>'),
(1363,	'İçine sokacağı gelmek',	'Birini aşırı ölçüde, çok sevmek.'),
(1364,	'İçine yedirememek',	'Benimsememek, kabul edememek.'),
(1365,	'İçini dökmek',	'Dertlerini, sıkıntılarını, üzüntülerini anlatmak. <b>Şu koca dünyada içimi dökecek bir insan bulamadım. </b>'),
(1366,	'İçini kemirmek',	'Bir üzüntü ve düşünce dolayısıyla rahatsızlık duymak. <b>İçini kemiren bu düşünceden kurtulmak istiyordu. </b>'),
(1367,	'İçini (bir) kurt yemek',	'Sürekli olarak bir kaygı içinde olmak.'),
(1368,	'İçi parçalanmak (paralanmak)',	'Birine acıyarak çok üzülmek. <b>Onun bu hâlini gördükçe içim parçalanıyor. </b>'),
(1369,	'İçi rahat etmek',	'Endişelenecek bir durum bulunmadığını öğrenerek sıkıntıdan kurtulmak, rahatlamak. <b>Ne yapayım, ben anneyim, onlar sağ salim dönerlerse içim rahat edecektir ancak. </b>'),
(1370,	'İçi sızlamak',	'Bir şey veya kişinin içine düştüğü durum sebebiyle üzülmek.'),
(1371,	'İçi titremek',	'1. Çok üşümek. <br />2. Çok istek duymak. <br />3. Bir zarar gelecek korkusu içinde bulunmak. <b>Hava iyice soğudu, içim titremeye başladı, haydi içeri girelim. </b>'),
(1372,	'İçi yanmak',	'1. Çok susamak. <br />2. Büyük bir acı sebebiyle çok fazla üzülmek. <b>Sanki yalnız onun içi yanıyordu. </b>'),
(1373,	'İçler acısı',	'Oldukça üzücü, çok acıklı.'),
(1374,	'İçli dışlı olmak',	'Teklifsiz, çok samimi, sıkı fıkı, senli benli olmak. <b>Biz Fatma`yla iyice içli dışlı olduk. </b>'),
(1375,	'İçtikleri su ayrı gitmemek',	'Sıkı fıkı dost, samimi arkadaş olmak; birbirlerinden saklayacakları bir şeyleri bulunmamak.'),
(1376,	'İdare etmek',	'1. Yönetmek, çekip çevirmek. <br />2. Tutumlu olmak, kullanmak. <br />3. Elvermek, yetmek, yetişmek, korumak, kurtarmak. <br />4. Hoş görmek, göz yummak. <br />5. Örtbas etmek. <b>Bu ayakkabıyı bu fiyata veremem, çünkü idare etmez. </b>'),
(1377,	'İfade vermek',	'Sorguya cevap vermek.'),
(1378,	'İflâhını kesmek',	'Gücünü tamamiyle yok edip bir daha karşı koyamayacak, düzelemeyecek, iş yapamayacak duruma getirmek. <b>Ben adamın iflâhını keserim, anladın mı?</b>'),
(1379,	'İfrit olmak',	'Çok öfkelenmek; aşırı ölçüde, kendini kaybedecek kadar sinirlenip kızmak. <b>İfrit oluyorum şu adamın hareketlerine. </b>'),
(1380,	'İğne atsan yere düşmez',	'Çok kalabalık, yürünecek gibi değil.'),
(1381,	'İğne ile kuyu kazmak',	'Zor denecek bir işi yetersiz araç ve gereçlerle başarmaya çalışmak.'),
(1382,	'İğne ipliğe dönmek',	'Aşırı derecede zayıflamak, kilo vermek. <b>O iri yarı adam hapisten çıktı ki iğne ipliğe dönmüş. </b>'),
(1383,	'İğneli söz',	'Dokunaklı, kırıcı, üzücü söz. <b>O iğneli sözlere ben bile dayanamazdım doğrusu. </b>'),
(1384,	'İki ahbap çavuşlar',	'Hemen her yerde birlikte görülen, birbirlerinden ayrılmayan iki arkadaş, dost.'),
(1385,	'İki arada bir derede (kalmak)',	'Sıkışık, zor şartlar altında (kalmak).'),
(1386,	'İki ayağını bir pabuca sokmak',	'Bir kimseyi, bir işi yapması için zorlamak, sıkıntıya sokmak.'),
(1387,	'İki cami arasında kalmış beynamaza dönmek',	'İki yoldan hangisini tutacağını; şöyle mi, böyle mi yapacağını bilememek; şaşırıp bir şey yapamaz olmak.'),
(1388,	'İki cihanda yüzü ak olmak',	'Doğru ve faziletli yaşayıp dünya ve ahrette mükâfat görmek.'),
(1389,	'İki çift söz etmek',	'Bir araya gelip birkaç söz söylemek. <b>Ne zamandır seninle bir araya gelip de iki çift söz edemedik. </b>'),
(1390,	'İki eli kanda olsa',	'Ne kadar önemli olursa olsun, elindeki iş hiç bırakılamayacak derecede olsa bile. <b>Söyleyin ona, iki eli kanda olsa da durmasın gelsin. </b>'),
(1391,	'İki eli (birinin) yakasında olmak',	'Ahrette, hesap gününde ondan davacı olmak; hakkını istemek.'),
(1392,	'İki gözü iki çeşme',	'Sürekli, çok ağlayarak. <b>Kadıncağız iki gözü iki çeşme ağlayıp duruyormuş. </b>'),
(1393,	'İkili oynamak',	'Birbirine karşı olanlardan hem birini, hem ötekini çıkarı için destelemek. <b>Sendika başkanı ikili oynuyormuş. </b>'),
(1394,	'İki paralık etmek',	'Değerini, onurunu çok düşürmek. <b>Seni arlanmaz utanmaz seni, beni iki paralık ettin, senin yüzünden topluma çıkamaz oldum!</b>'),
(1395,	'İki rahmetten biri',	'Ağır hasta olan birisi için <b>ya şifa, ya ölüm</b> anlamında kullanılır.'),
(1396,	'İki sözü bir araya getirememek',	'Düşüncelerini, duygularını düzgün bir biçimde anlatamamak, güzel konuşma becerisinden yoksun olmak.'),
(1397,	'İki yakası bir araya gelmemek',	'Geçim sıkıntısı içinde olmak ve borçtan kurtulamamak, gelir ve giderini denkleştirememek. <b>Bilmiyorum ne zaman iki yakamız bir araya gelecek. </b>'),
(1398,	'İleri geri konuşmak',	'Yersiz, kırıcı, yaralayıcı biçimde konuşmak.'),
(1399,	'İleri gitmek',	'Söz ve davranışta ölçü dışına çıkmak; gereksiz, aşırı davranışta bulunmak ve haddi aşmak. <b>O saygısız adamın daha fazla ileri gitmesine fırsat verilmemelidir. </b>'),
(1400,	'İlk göz ağrısı',	'1. İlk doğan çocuk. <br />2. İlk sevgili.'),
(1401,	'İmana gelmek',	'1. Hak dini olan İslâm`ı kabul etmek. <br />2. En sonunda doğruyu söylemek. <br />3. Önceden kabul etmediği şeyi sonradan kabul edip uymak. <b>İmana gel, tövbe et ki öbür dünyada mutluluğa eresin. </b>'),
(1402,	'İnce eleyip sık dokumak',	'Titizlik göstermek, bir şeyi en ince ayrıntılarına kadar araştırmak, gözden geçirmek. <b>O kadar da ince eleyip sık dokunacak bir iş değil, kaygılanma. </b>'),
(1403,	'İn cin top oynamak',	'Issız, sessiz olmak, bir yerde hiçbir canlı yaratık bulunmamak. <b>Adada in cin top oynuyordu sanki. </b>'),
(1404,	'İncir çekirdeğini doldurmaz',	'Çok az veya pek önemsiz. <b>Ne akılsız adam bunlar, kavga etmelerine sebep olan mesele incir çekirdeğini doldurmaz bile, ayırın şunları. </b>'),
(1405,	'İnme inmek',	'Felç olmak, bedenin bir yeri hareketsiz ve duygusuz duruma gelmek. <b>Adamın sağ yanına inme inmiş diyorlar. </b>'),
(1406,	'İnsan eti yemek',	'Birini çekiştirmek.'),
(1407,	'İnsan evlâdı',	'İyi, anlayışlı, ahlâk sahibi insan. <b>İnsan evlâdı olmasaydı, tanımadığı birine onca yardım yapar mıydı?</b>'),
(1408,	'İnsan hâli',	'Olabilir, doğaldır, hoş karşılamak gerekir.'),
(1409,	'İnsanlıktan çıkmak',	'1. Çok zayıflamış, bir deri bir kemik kalmış olmak. <br />2. İnsanî niteliklerini yitirmek, insana yakışmayacak davranışlarda bulunmak.'),
(1410,	'İnsan sarrafı (olmak)',	'İnsanların karakterini çabucak anlayacak duruma gelmiş (olmak). <b>Dedem insan sarrafıdır, onu bir görse ne biçim bir adam olduğunu hemen anlayıverir. </b>'),
(1411,	'İpe çekmek',	'Asarak öldürmek.'),
(1412,	'İpe un sermek',	'İstenilen işi yapmamak için birtakım bahaneler, sebepler ileri sürmek, güçlük çıkarmak, engeller göstermek.'),
(1413,	'İpi koparmak',	'Bağlı bulunduğu yer ya da kişi ile ilişkisini kesmek, aradaki anlaşmazlığı artırmak.'),
(1414,	'İpin ucunu kaçırmak',	'Bir yeri yönetmede veya bir şeyi kullanmada gereken ölçüyü kaçırıp, artık duruma hâkim olamamak; çıkmaza girmek. <b>Biraz daha dikkatli olmalıyız, yoksa ipin ucunu kaçıracağız. </b>'),
(1415,	'İpi sapı yok',	'Birbirini tutmaz, yersiz, anlamsız, işsiz, yersiz yurtsuz, saçma sapan. <b>İpi sapı yok bu sözlerin, daha inandırıcı olmalısın. </b>'),
(1416,	'İpiyle kuyuya inilmez',	'Kendisine güvenilmez, ona güvenilerek bir işe girilmez. <b>O ipiyle kuyuya inilmez adamla yola çıkmam ben. </b>'),
(1417,	'İple çekmek',	'Zamanın gelmesini sabırsızlıkla beklemek, çok istemek. <b>Yarını iple çekiyorum. </b>'),
(1418,	'İpucu vermek',	'Aranılan şeyi bulmaya yarayan işareti, onu açıklamaya yarayan bilgiyi vermek. <b>Bir ipucu vermezsen bu bilmeceyi çözemeyeceğim. </b>'),
(1419,	'İsabet etmek',	'1. Nişan alınan yere değmek, rastlamak. <br />2. Çıkmak. <br />3. Yerinde iş görmüş olmak. <b>Böyle karar vermekte çok isabet ettiniz. </b>'),
(1420,	'İskele vermek',	'Vapura binmek, vapurdan inmek için iskeleyi uzatmak.'),
(1421,	'İsmi var, cismi yok',	'1. Sözü edilen bir kimse veya şeyin gerçekte var olmadığını anlatmak için kullanılır. <br />2. Adı olmasına karşılık görevini ve etkinliğini yerine getirmeyen, varlığı ile yokluğu arasında bir fark bulunmayan.'),
(1422,	'İster istemez',	'1. Zorunlu olarak, elinde olmadan. <br />2. İstemesi üzerine, hiç vakit geçirmeden, istediği anda. <b>İster istemez ben de ona bağırdım. </b>'),
(1423,	'İstifini bozmamak',	'Bir olay karşısında aldırış etmemek, durum ve davranışını hiç değiştirmemek. <b>Karşıma geçmiş avazı çıktığı kadar bağırıyordu, bense istifimi bozmadan bekledim. </b>'),
(1424,	'İş ayağa düşmek',	'İş sorumsuz, yetkisiz ve beceriksizlerin elinde kalmak. <b>Bunlar da işi iyice ayağa düşürdüler. </b>'),
(1425,	'İş başa düşmek',	'Beklediği yardım gelmeyince, kendi işini kendisi yapmak zorunda kalmak. <b>İş başa düştü desene!. . </b>'),
(1426,	'İş çatallanmak (çatallaşmak)',	'Bir işin sonuca oluşması konusunda türlü güçlüklerle karşılaşmak, ya da çeşitli seçeneklerle yüz yüze gelmek, sonuca nasıl ulaştırılacağı bilinemez olmak. <b>İş gittikçe çatallaşıyor, sense aldırmıyorsun bile. </b>'),
(1427,	'İş çığırından çıkmak',	'Bir iş asıl amaçtan çıkarak düzelmesi güç bir durum almak, bir bozukluk ve kargaşalık baş göstermek.'),
(1428,	'İş inada binmek',	'Bir işi yapmakta direnmek.'),
(1429,	'İşi düşmek',	'Birinin yardımına ihtiyaç duymak. <b>Eh, onun da bize işi düşecek bir gün. </b>'),
(1430,	'İşe koşmak',	'Birini bir iş yapmak üzere görevlendirmek, göndermek.'),
(1431,	'İşi ağırdan almak',	'Acele etmemek, bir işi yapmak için isteksiz görünmek. <b>Söyle onlara, işi ağırdan almasınlar, müşteriler mal bekliyor. </b>'),
(1432,	'İşi azıtmak',	'Yanlış ve aşırı yollara sapmak. <b>Bu çocuk da işi iyice azıttı. </b>'),
(1433,	'İşi Allah`a kalmak',	'Güç şartlar altında, beşerden hiçbir yardım umudu kalmamak. <b>Kime baş vurduysa bir sonuç alamadı, artık işi Allah`a kalmıştı. </b>'),
(1434,	'İşi başından aşmak',	'Pek çok işi olmak, iş içinde kaybolmak.'),
(1435,	'İşi bitmek',	'1. Hâli, gücü kalmamak. <br />2. Yaptığı işi sona ermek. <b>Git de bak, babanın işi bitmiş mi?</b>'),
(1436,	'İşi duman olmak',	'İşi ve durumu kötü olmak, berbat bir durumda bulunmak.'),
(1437,	'İşi iş olmak',	'İşi yolunda, iyi olmak; hâlinden memnun bulunmak. <b>İşi iş herifin, baksana yan gelip yatıyor her gün. </b>'),
(1438,	'İşinden olmak',	'Bir süredir yaptığı işi elinden gitmek, görevini yitirmek. <b>Haydi canım, yoluna git de patronunla kavga etme; yoksa işinden olacaksın. </b>'),
(1439,	'İşi sıkı tutmak',	'Gevşekliğe yol açmamak, işe gereken önemi vermek ve sağlıklı yürümesini sağlamak.'),
(1440,	'İşi tıkırında olmak',	'İşi çok uygun ve iyi olmak. <b>O konuşmayacak da ben mi konuşacağım, işi tıkırında adamın. </b>'),
(1441,	'İşi yokuşa sürmek',	'Yapılabilir, görülebilir işi yapmamak için güçlük çıkarmak, bahaneler ileri sürmek.'),
(1442,	'İşkembeden atmak',	'Uydurarak söylemek, tutarı olmayan sözler sarf etmek. <b>Ona sakın inanmayın, işkembeden atıyor. </b>'),
(1443,	'İş sarpa sarmak',	'İş, içinden çıkılması zor bir durum almak; engellerle karşılaşmak. <b>İşler sarpa sarmadan çekip gidelim buradan. </b>'),
(1444,	'İşten el çektirmek',	'Görevden uzaklaştırmak. <b>Yolsuzluk yaptığı iddiası ile işten el çektirdiler ona. </b>'),
(1445,	'İş yok',	'O şeyde yarar yok, faydası olmaz. <b>O arabada hiç iş yok, almaya değmez. </b>'),
(1446,	'İte kaka',	'Zorla, güçlükle. <b>Adamı her sabah ite kaka işe götürüyoruz. </b>'),
(1447,	'İtibar kazanmak',	'Saygınlık görmek, kendisine değer verilmek.'),
(1448,	'İt sürüsü kadar',	'Gereğinden fazla, oldukça çok, kalabalık. <b>İt sürüsü kadar adam, nasıl başa çıkacağız bunlarla. </b>'),
(1449,	'İyi etmek',	'1. Hastalıktan kurtarmak, sıhhatine kavuşturmak. <br />2. Yerinde bir davranışta bulunmak. <br />3. Bir şeyi gizlice almak, kendisine mal etmek.'),
(1450,	'İyi gözle bakmamak',	'Birisi hakkında iyi düşünmemek, kötü niyet beslemek. <b>Komşuları ona hiçbir zaman iyi gözle bakmadılar. </b>'),
(1451,	'İyi gün dostu',	'Dostlarının sıkıntılı günlerinde onlardan kaçan kimse. <b>Bize iyi gün dostu gerekli değil. </b>'),
(1452,	'İyi saatte olsunlar',	'Cinlerden söz edilirken kullanılır.'),
(1453,	'İzinden yürümek',	'Birine içten bağlanarak onun başladığı işi aynı anlayışla sürdürmek, fikirlerini ve hareketlerini aynen benimsemek.'),
(1454,	'İzi silinmek',	'Yok olmak, ortadan kaybolmak. <b>Çiçek hastalığının bu kasabada izi silindi hemen hemen, çünkü çocuklar aşılanıyorlar. </b>'),
(1455,	'Kabak (birinin) başına (başında) patlamak',	'Birçok kimsenin ilgili olduğu olaydan yalnızca bir kimse zararlı çıkmak; beklenmediği hâlde, bir işin zararlı sonucuna katlanmak.'),
(1456,	'Kabak tadı vermek',	'Bıktırmak, usanç vermek, tatsız olmaya başlamak. <b>Senin bu konuşmaların da artık kabak tadı vermeye başladı. </b>'),
(1457,	'Kabına sığmamak',	'Sevinç ve heyecanından taşkın hareketlerde bulunmak.'),
(1458,	'Kabir azabı çekmek',	'Çok sıkılmak, eziyet çekmek. <b>Kabir azabı çekmeye daha ne kadar devam edeceğiz. </b>'),
(1459,	'Kabuğuna çekilmek',	'Tek başına kalmak, dış dünya ile ilgisini kesmek, kimse ile görüşmemek. <b>Geçirdiği kazadan sonra iyice kabuğuna çekildi. </b>'),
(1460,	'Kaçın kur`ası',	'Aldatılması güç, kurnaz; gün görmüş, geçirmiş; tecrübeli. <b>O kaçın kur`ası, boşuna uğraşma, sen onu kandıramazsın. </b>'),
(1461,	'Kafadan atmak',	'Bir konu üzerinde inceleme yapmadan, rast gele konuşmak. <b>Derse hiç çalışmadığın belli, öyle kafadan atıyorsun ki. . . </b>'),
(1462,	'Kafadan kontak (sakat)',	'Düşüncesiz, delice işler yapan, aklı kıt. <b>Bırak şu elindeki baltayı, kafadan kontak mısın nesin?</b>'),
(1463,	'Kafa dengi',	'Davranışları, anlayışları, dünya görüşleri birbirine uymuş kimselerden her biri. <b>Kafa dengi bir arkadaşa öylesine ihtiyacım var ki. </b>'),
(1464,	'Kafa patlatmak',	'Bir konu üzerinde pek çok düşünmek, zihin yormak. <b>Bu makine üzerinde az kafa yormamışsın, öyle karışık ki. </b>'),
(1465,	'Kafa tutmak',	'Karşı gelmek, direnmek, boyun eğmemek. <b>Her önüne gelene kafa tutmakla bir yere varacağını mı sanıyorsun?</b>'),
(1466,	'Kafası almamak',	'1. Anlayıp kavrayamamak. <br />2. Zihin yorgunluğundan ötürü anlayamaz olmak. <br />3. Olabileceğine inanmamak. <b>Boşuna nefes tüketme, kafası almaz onun. </b>'),
(1467,	'Kafası işlemek (çalışmak)',	'Bir konu üzerinde kavrayışı çok iyi olmak.'),
(1468,	'Kafası kazan (gibi) olmak, (veya kafası şişmek)',	'1. Zihni yorulmak. <br />2. Gürültülü, patırtılı şeyler dinlemekten rahatsız olmak, yorgunluk duymak. <b>Kesin artık şu makinenin sesini, kafam kazan gibi oldu. </b>'),
(1469,	'Kafası kızmak',	'Çok öfkelenip sinirlenmek. <b>Kafamı kızdırmadan çekip gidin buradan. </b>'),
(1470,	'Kafasına dank etmek (demek)',	'Çoktandır anlayamadığı bir meseleyi bir olay sebebiyle birden bire kavramak, doğruyu yakalamak.'),
(1471,	'Kafasına koymak',	'Bir şeyi yapmaya kararlı olup zamanını beklemek. <b>Yarın onunla görüşmeyi kafama koydum. </b>'),
(1472,	'Kafası yerinde olmamak',	'1. O anda kafası çok yorgun olmak. <br />2. Başka şeyler düşündüğünden, o anda konuşulana hemen intibak edememek. <b>Kusura bakmayın, ne söylediğinizi anlayamadım, kafam yerinde değildi de. </b>'),
(1473,	'Kafese girmek',	'1. Hapse girmek. <br />2. Aldatılmak, hile yoluyla kendisinden çıkar sağlanmak, oyuna gelmek. <b>Zavallı kafese girmekten kurtulduğunu sanmıştı. </b>'),
(1474,	'Kafese koymak',	'Tuzağa düşürüp çıkar sağlamak.'),
(1475,	'Kâğıda dökmek',	'Düşüncelerini, duygularını yazıya geçirmek.'),
(1476,	'Kâğıt üzerinde kalmak',	'Yapılması kararlaştırıldığı hùlde uygulanmamak; konuşulan, kararlaştırılan yazıda kalmak. <b>O kadar yol yapımı, sulama kanalı hep kâğıt üzerinde kaldı. </b>'),
(1477,	'Kalbini kırmak',	'İncitmek, küstürecek kadar üzmek, gönlünü kırmak, gücendirmek. <b>Onu, kalbini kırmadan uyarmaya çalış. </b>'),
(1478,	'Kalburla su taşımak',	'Verimsiz, verim alınamayacak, olmayacak bir işle uğraşmak.'),
(1479,	'Kalbur üstü',	'Benzerleri arasında üstün, seçkin, görünür.'),
(1480,	'Kaldırım mühendisi',	'İşsiz güçsüz, sokaklarda dolaşan kimse.'),
(1481,	'Kaale almamak',	'Önemsiz görmek, sözünü etmeye değer bulmamak. <b>O, kaale alınacak bir insan değil. </b>'),
(1482,	'Kalem efendisi',	'Kalemde çalışan görevli, yazman.'),
(1483,	'Kalem oynatmak',	'1. Yazı yazmak. <br />2. Bir yazıyı düzeltmek. <br />3. Bir yazıda değişiklik yapmak. <b>Ben senin gibi kalem oynatmayı beceremiyorum. </b>'),
(1484,	'Kaleyi içinden fethetmek',	'Karşı taraftan birinin yardımını alarak davasını kazanmak.'),
(1485,	'Kalıbını basmak',	'Bir şeye bütün içtenliği ile güvenmek, bir şeyi doğrulamak. <b>Kalıbımı basarım ki o, bu işi yapmamıştır. </b>'),
(1486,	'Kalıbının adamı olmamak',	'Görünüşünden bekleneni yapamaz olmak, umulanı ortaya koymamak.'),
(1487,	'Kalıptan kalıba girmek',	'1. Sık sık iş değiştirmek. <br />2. Çıkar sağlamak için değişik kılıklara girmek.'),
(1488,	'Kalp kazanmak',	'Güzel bir davranış ve sözle birilerinin sevgisini kazanmak, ilgisini çekmek. <b>Bir demet çiçekle annemizin kalbini kazanabiliriz. </b>'),
(1489,	'Kambersiz düğün olmaz (olur mu?)',	'<b>Bir toplantı, eğlence veya iş, en çok ilgili kişiler bulunmadan yapılırsa tadı çıkmaz</b> anlamında alay yollu kullanılır.'),
(1490,	'Kambur üstüne kambur (kambur kambur üstüne)',	'<b>Sıkıntı üstüne sıkıntı, terslik üstüne terslik, borç üstüne borç, aksilikler birbirini kovalıyor</b> anlamında kullanılır.'),
(1491,	'Kanadı altına almak',	'Korumak, gözetmek, himayesi altına almak. <b>Yeğenini kanadının altına aldı. </b>'),
(1492,	'Kan ağlamak',	'Büyük bir üzüntü içinde olup yakınmak. <b>Dört çocuk tek başıma kaldım, çaresizim, içim kan ağlıyor ama kimseye açılamıyorum. </b>'),
(1493,	'Kana susamak',	'Birini öldürme hırsı içinde olmak. <b>Bırak elindeki bıçağı dedim ama dinletemedim, kana susamış gibiydi. </b>'),
(1494,	'Kanat germek',	'Birini korumak, gözetimi altına almak.'),
(1495,	'Kan başına sıçramak (beynine çıkmak)',	'Çok sinirlenmek, öfkelenmek, <b>Kan başına sıçramıştı, sağa sola bağırıp duruyordu. </b>'),
(1496,	'Kancayı takmak',	'Bir kimsenin zararı, kötülüğü için uğraşmak.'),
(1497,	'Kan çıkmak',	'Cinayet işlenmek, kan dökülmek. <b>Şu adamı götürün gözümün önünden, yoksa kan çıkacak. </b>'),
(1498,	'Kandilli temenna',	'Eli yere kadar uzatarak yapılan selâmlama.'),
(1499,	'Kan dökmek',	'Ölüme yol açmak, yaralanıp ölmek veya birini yaralayıp öldürmek.'),
(1500,	'Kan gövdeyi götürmek',	'Çok kan akıtılmış olmak, çok insan öldürülmek. <b>Düşmanla göğüs göğüse gelmiştik, biliyordum ki birazdan kan gövdeyi götürecek ve pek çoğumuz ölecekti. </b>'),
(1501,	'Kan gütmek',	'Kan dökerek öç almayı istemek.'),
(1502,	'Kanı ağır',	'Davranışları yavaş, sevimsiz, konuşması insana sıkıntı veren, hoşa gitmeyen kimse.'),
(1503,	'Kanı bozuk',	'Soysuz, iğrenç işler yapmaktan geri durmayan. <b>Toplum bu kanı bozuk insanlardan temizlenmelidir. </b>'),
(1504,	'Kanı kaynamak',	'1. Hareketli, coşkun olmak. <br />2. Birine içten bir sevgi beslemek, yakınlık duymak. <b>Çocuğa, ilk rastladığımda kanım kaynamıştı. </b>'),
(1505,	'Kanına girmek',	'1. Birini öldürtmek veya öldürmek. <br />2. Bir şeyi harcamak, ziyan etmek.'),
(1506,	'Kanına susamak',	'Belâsını aramak, kendisinin öldürülmesine yol açacak bir davranışta bulunmak. <b>Kanına mı susadın sen, o katilin üstüne böyle gidilir mi hiç!</b>'),
(1507,	'Kanını emmek',	'Hiç insaf etmeden sömürmek, varını yoğunu elinden almak. <b>Yıllardır kanımızı emiyor bu soysuz herifler!</b>'),
(1508,	'Kanı pahasına',	'Yaralanmayı veya öldürülmeyi göze alarak. <b>Kanım pahasına da olsa, o adamlara, buradan adımlarını attırmayacağım. </b>'),
(1509,	'Kanı sıcak',	'Sevimli, kendisini sevdiren, sempatik, sıcakkanlı.'),
(1510,	'Kanıyla ödemek',	'Yaptığı işin cezasını hayatıyla ödemek. <b>Yaptığını kanıyla ödettiler zavallıya. </b>'),
(1511,	'Kan kusmak',	'Çok eziyet, sıkıntı çekmek.'),
(1512,	'Kan kusturmak',	'Çok büyük sıkıntı ve eziyet çektirmek. <b>Bana kan kusturmaya yemin etmişler, haydi görelim. </b>'),
(1513,	'Kanlı bıçaklı olmak',	'Birbirlerinin kanını dökecek, birbirlerini öldürecek kadar birbirlerine düşman olmak. <b>Küçücük bir tarla yüzünden kanlı bıçaklı olduk. </b>'),
(1514,	'Kanlı canlı',	'Sağlıklı, sapasağlam, dinç ve diri olduğu yüzünden belli olan. <b>Kanlı canlı oluncaya kadar hastanede tutuldum. </b>'),
(1515,	'Kan ter içinde kalmak',	'Çok yorgun, terli, bitkin ve perişan durumda olmak. <b>Elindeki kazmayı bırakmaya niyetli değildi, kan ter içinde kalmış bedenini doğrultarak yüzüme baktı. </b>'),
(1516,	'Kan tutmak',	'1. Kan görünce bayılmak. <br />2. (Adam öldüren kimse korku ve heyecandan) şok geçirmek, kaçamamak, olduğu yere yığılıp kalmak.'),
(1517,	'Kapağı atmak',	'Sıkıntılı bir yerden kurtulup rahat edeceği bir yere kavuşmak; uygun bir yere yerleşmek, işe girmek. <b>Evimize kapağı attık mı tamam, gel keyfim gel o zaman. </b>'),
(1518,	'Kapalı kutu',	'İçinde ne sakladığını belli etmeyen, niteliği gizli kalan.'),
(1519,	'Kapı dışarı etmek',	'Kovmak, dışarı atmak. <b>Ben de bu evin insanıyım, beni kapı dışarı edemezsiniz!</b>'),
(1520,	'Kapı kapı dolaşmak',	'1. Ev ev gezmek, her eve uğramak. <br />2. Hemen her devlet dairesine başvurmak. <b>Kapı kapı dolaştı, ne var ki bir iş bulamadı. </b>'),
(1521,	'Kapı komşu',	'Bitişikte oturan komşu, evleri yan yana olan ailelerden her biri. <b>Kapı komşum öyle iyi bir insan ki. . </b>'),
(1522,	'Kapısında büyümek',	'Birinin evinde eğitim görüp yetişmek. <b>Onun kapısında büyümüştü, ona bu kötülüğü nasıl yapmıştı aklı almıyordu. </b>'),
(1523,	'Kapısını aşındırmak',	'İstediğini elde edinceye kadar birinin yanına çok sık gidip gelmek.'),
(1524,	'Kapı yoldaşı',	'Herhangi bir yerde aynı hizmette bulananlardan her biri.'),
(1525,	'Kapıyı açmak',	'1. Başlama. <br />2. Bir işte birilerine örnek olmak. <b>Açık artırmada kapı bir milyon liradan açıldı. </b>'),
(1526,	'Karaborsa',	'Piyasada olmayan malın gizlice, el altından yüksek fiyatla alınıp satılması. <b>Karaborsacılar toplumun kanını emiyorlar. </b>'),
(1527,	'Kara cahil',	'Hiçbir şey bilmeyen, çok bilgisiz. <b>Onun kara cahil birisi olduğunu ilk konuşmamızda fark etmiştim. </b>'),
(1528,	'Kara çalı',	'İki kişi, iki dost arasına girerek arayı bozan kimse.'),
(1529,	'Kara çalmak',	'Birine iftira etmek, leke sürmek, haksız yere suçlamak. <b>Kadıncağıza yok yere kara çaldılar. </b>'),
(1530,	'Kara gün',	'Sıkıntılı, üzüntülü, büyük bir yasa düşülen gün. <b>Allah kimseye kara gün göstermesin. </b>'),
(1531,	'Kara gün dostu',	'Yalnız iyi günlerde değil sıkıntılı, üzücü, düşkünlük günlerinde de insanın yardımına koşan, dostunu yalnız bırakmayan kimse.'),
(1532,	'Kara haber',	'Ölüm veya felâket haberi, çok üzücü haber. <b>Fatma kadına bu kara haberi vermeye kimse yanaşmadı. </b>'),
(1533,	'Karalar bağlamak (giymek)',	'Bir felâket dolayısıyla yas tutmak, siyah elbise giymek ya da siyah örtü bağlamak.'),
(1534,	'Kara liste',	'Zararlı görülüp cezalandırılmaları, öldürülmeleri düşünülen kimseler hakkında tutulan liste. <b>Köy muhtarını da kara listeye almışlar. </b>'),
(1535,	'Karaman`ın koyunu sonra çıkar oyunu',	'<b>Dış görünüşe aldanmamalı, bir kişi ya da iş olağan görünebilir, ancak altından neler çıkabileceği hiç belli olmaz, o sonra görünür. </b> anlamında kullanılır.'),
(1536,	'Karar kılmak',	'Dönüp dolaşıp o şeyin üstünde durmak, onu tercih etmek, birçok şeyi deneyip onu seçmek. <b>Ben bu elbisede karar kıldım. </b>'),
(1537,	'Karda gezip izini belli etmemek',	'Kimsenin sezemeyeceği biçimde gizli bir iş çevirmek, uygunsuz işler yapmak. <b>Onun ne biçim bir insan olduğunu bana sorun; o, karda gezer izini belli etmez biridir. </b>'),
(1538,	'Kargacık burgacık',	'Eğri büğrü, kötü, okunması güç, çarpık, düzensiz (yazı).'),
(1539,	'Kardeş payı yapmak',	'Eşit oranlarda bölmek, taksim etmek, paylaştırmak. <b>Çok açtılar, buldukları ekmeği oracıkta kardeş payı yaptılar. </b>'),
(1540,	'Karga tulumba etmek',	'Birkaç kişi, birini kollarından bacaklarından tutup havaya kaldırmak. <b>Hep birlikte babalarını karga tulumba edip havuzun başına getirdiler. </b>'),
(1541,	'Karınca duası gibi',	'Çok küçük, sık ve okunaksız, birbirine girmiş (yazı).'),
(1542,	'Karınca yuvası gibi kaynamak',	'Çok kalabalık ve hareketli olmak (bir yer). <b>Pasajın girişi âdeta karınca yuvası gibi kaynıyordu. </b>'),
(1543,	'Karınca kararınca',	'Az, önemsiz ve küçük de olsa, gücü yettiği kadar, elinden geldiğince. <b>Caminin yapımına karınca kararınca o da katkıda bulunmaya karar verdi. </b>'),
(1544,	'Karman çorman',	'Karmakarışık, çok karışık, düzensiz, alt üst olup birbirine girmiş. <b>Ortalık karman çormandı, nereden işe başlayacağını bilemiyordu. </b>'),
(1545,	'Karnı geniş',	'Hiçbir şeyi tasa etmeyen, titizlenmeyen, gamsız, umarsız.'),
(1546,	'Karnı karnına geçmek',	'Çok acıkmak, çok zayıflamış olmak. <b>Günlerdir ağzına bir lokma koymamıştı, karnı karnına geçmiş ve bitap düşmüştü. </b>'),
(1547,	'Karnım tok',	'<b>O sözlerine kanmıyorum, önem vermiyorum</b> anlamında kullanılır. <b>Geç babam, geç bu sözleri, karnımız tok bu sözlere, paradan söz et sen, verecek misin, vermeyecek misin?</b>'),
(1548,	'Karnı tok sırtı pek',	'Geçimi iyi, hâli vakti yerinde, para sıkıntısı olmayan, birinin yardımına ihtiyaç duymayan (kimse). <b>Herkesin karnı tok sırtı pek olacaktır, bize güvenin!</b>'),
(1549,	'Karnı zil çalmak',	'Çok acıkmış olmak. <b>Bugün hiçbir şey yiyemedim, karnım zil çalıyor!</b>'),
(1550,	'Karşı çıkmak',	'1. Gelenleri karşılamak üzere yola ya da kapı önüne çıkmak. <br />2. İleri sürülen fikrin, tutulan yolun yanlış olduğunu söylemek. <b>Her fikrime karşı çıkmak zorunda mısın?</b>'),
(1551,	'Karşı durmak',	'Bir güce boyun eğmemek, direnmek. <b>Düşmana karşı durmak boynumuzun borcudur. </b>'),
(1552,	'Karşı koymak',	'Engel olmaya çalışmak, direnmek, güç kullanarak dayanmak, boyun eğmemek. <b>Hırsızlar polise silâhla karşı koymaya çalıştılar. </b>'),
(1553,	'Kasıp kavurmak',	'1. Bir afet çok zarar vermek, mahvetmek. <br />2. Baskı yaparak, kıyıcı davranışlarda bulunarak bir topluluğu ezmek; zulmetmek, ortalığı korku ve dehşet içinde bırakmak. <b>Eşkıyalar ortalığı kasıp kavurmaya başladılar!</b>'),
(1554,	'Kaş göz etmek',	'Kaş ve göz hareketleriyle bir işaret vermeye, istediğini bu yolla anlatmaya çalışmak. <b>Kalabalıkta kaş göz ederek Hasan`ı çağırmayı düşündü. </b>'),
(1555,	'Kaşıkla yedirip, sapıyla göz çıkarmak',	'Bir iyilik yaptıktan sonra, bu iyiliği hiçe indirecek bir kötülük yapmak.'),
(1556,	'Kaşla göz arasında',	'Çok çabuk, kimsenin sezmesine fırsat vermeyecek kadar az bir zaman içinde. <b>Kaşla göz arasında kapıverdi mendili. </b>'),
(1557,	'Kaşlarını çatmak',	'Kızgın, öfkeli ve sinirli olduğunu kaşlarını birbirine yaklaştırarak göstermeye çalışmak. <b>Bana öyle kaşlarını çatıp durma!</b>'),
(1558,	'Kaş yapayım derken göz çıkarmak',	'İşi düzelteyim, bir iyilik yapayım derken büsbütün bozmak ve büyük bir zarar vermek.'),
(1559,	'Katı yürekli',	'Acımasız, merhametsiz, acı veren şeylere aldırmayan. <b>Onun gibi katı yürekli bir insan daha görmedim desem yeridir. </b>'),
(1560,	'Kayıtsız kalmak',	'Umursamamak, önem vermemek, ilgi göstermemek. <b>Onun bu kötülüklerine kayıtsız kalmak mümkün mü?</b>'),
(1561,	'Kazan kaldırmak',	'Yönetime karşı topluca karşı gelmek, baş kaldırmak. <b>Maden işçileri kazan kaldırmış diyorlar. </b>'),
(1562,	'Kazık yutmuş gibi',	'Dimdik (duran, oturan, yürüyen).'),
(1563,	'Kazın ayağı öyle değil',	'<b>Durum, mesele senin sandığın gibi değil</b> anlamında kullanılır.'),
(1564,	'Keçileri kaçırmak',	'Düşünme yeteneğini kaybetmek, aklını oynatmak, delirmek, bunalım içinde olmak, <b>Doktor, keçileri kaçırmış diyorlar!</b>'),
(1565,	'Kedi ciğere bakar gibi (bakmak)',	'İmrenerek, iştahla, ele geçirme isteği ile bakmak.'),
(1566,	'Kedi gibi dört ayak üstüne düşmek',	'En zor, en tehlikeli durumdan zarar görmeden kurtulmak.'),
(1567,	'Kedi olalı bir fare tuttu',	'İlk defa, neden sonra kendisinden beklenen bir iş yapabildi. <b>Temsilcimiz, nihayet kedi olalı bir fare tuttu, yüklü bir iş yakaladı. </b>'),
(1568,	'Kefeni yırtmak',	'Ağır bir hasta ölüm tehlikesini atlamak. <b>Üzülmeyin, kefeni yırttı büyük anneniz. </b>'),
(1569,	'Kel başa şimşir tarak',	'Pek çok ihtiyaç giderilmeyi beklerken gereksiz özenti ve gösterişi belirtmek için kullanılır.'),
(1570,	'Keli görünmek',	'Bir kabahati, kusuru ortaya çıkmak. <b>Kelinin görünmeyeceğini sanıyordu şapşal!</b>'),
(1571,	'Kel kâhya',	'Bilgisi olsun olmasın her işe karışan, burnunu sokan.'),
(1572,	'Kelle götürür gibi',	'Gerekli olmayan bir acelecilikle, bir şey ulaştıracakmış gibi çok hızlı koşarak.'),
(1573,	'Kelleyi koltuğuna almak',	'Ölümü göze alarak bir işe kalkışmak. <b>Kelleyi koltuğuna alıp düşman karşısına çıkmak her babayiğidin harcı değil. </b>'),
(1574,	'Kemerleri sıkmak',	'Tutumlu davranmak, açlığa ve susuzluğa katlanmak. <b>Kemerleri sıktıra sıktıra millette hâl bırakmadılar. </b>'),
(1575,	'Kem küm etmek',	'Anlatmak istediğini açık seçik ifade edememek, bir soru karşısında bocalayıp cevap bulamayarak anlamsız sözler söylemek. <b>Kem küm etme de ne söyleyeceksen söyle çabuk!</b>'),
(1576,	'Kendi hâlinde',	'Sessiz, hiçbir şeye karışmayan, karışmak istemeyen, sakin (kimse). <b>Yazık olmuş, kendi hâlinde biriydi, kimsenin etlisine sütlüsüne karışmazdı. </b>'),
(1577,	'Kendi göbeğini kendi kesmek',	'İstediği yardım gelmeyince kendi işini kendi yapmak durumunda kalmak. <b>O her zaman kendi göbeğini kendisi kesmiş, kimseden yardım beklememiştir. </b>'),
(1578,	'Kendi kendine gelin güvey olmak',	'Başkalarının ne diyeceğini hesaba katmadan, bir işi sadece kendi başına tasarlayıp olmuş sayarak sevinmek. <b>Kendi kendine gelin güvey olmayı bırak, bakalım kız ne diyecek bu işe. </b>'),
(1579,	'Kendi kendini yemek',	'İstediği iş olmadı diye gizli gizli üzülmek, kaygı duymak. <b>Kendi kendimi yedim bitirdim bu iş yüzünden. </b>'),
(1580,	'Kendinden geçmek',	'1. Kendini kaybetmek, bayılmak, bilinci işlemez olmak. <br />2. Sevindirici bir olay karşısında coşkuya kapılmak, duygulanmak. <b>Dün gece bizim adam yine kendinden geçti, hastaneye zor yetiştirdik. </b>'),
(1581,	'Kendinden pay (paha) biçmek',	'Bir durumu kendi durumu ile ölçüştürmek.'),
(1582,	'Kendine gelmek',	'1. Sarhoşluktan, bayıldıktan sonra ayılmak. <br />2. Aklı başına gelmek. <br />3. Bozuk olan durumu düzelmek. <b>Oh, nihayet kendine geldi bizim adam!</b>'),
(1583,	'Kendine yedirememek',	'Yapılan bir işi onur kırıcı görüp, kişiliğine dokunmuş sayarak tepki göstermek; kendisinin başkasına yapması söz konusu olan işi, kişiliği için uygun görmeyip yapmamak.'),
(1584,	'Kendine yontmak',	'Ortaya çıkan fırsattan yararlanıp başkalarını düşünmeyerek hep kendi çıkarını sağlayacak yönde hareket etmek. <b>Hep kendine yontma, biraz da bizi düşün, biz de insanız!</b>'),
(1585,	'Kendini ağır satmak',	'Kendisinden yapılması istenen işi, birçok ricadan, birçok ısrardan sonra yapmayı kabul etmek. <b>Kendini ağır satmakla adam olduğunu mu kanıtlayacak?</b>'),
(1586,	'Kendini alamamak',	'İstemeyerek bir işi yapmak durumunda kalmak, yapmamayı edememek, kendini tutamayıp yapmak. <b>Ona bir tokat atmaktan kendimi alamadım işte!</b>'),
(1587,	'Kendini ateşe atmak',	'Bilerek zor ve tehlikeli bir işe girişmek. <b>Kendisini ateşe atmasına izin mi vereceksiniz?</b>'),
(1588,	'Kendini bulmak',	'1. İyi bir duruma kavuşmak. <br />2. Kişilik kazanıp olgunluğa erişmek. <br />3. Farkında olmadan bir yere ulaşmış olmak. <b>Nihayet kendimi buldum, bundan böyle ekonomik sıkıntı çekmeyeceğim. </b>'),
(1589,	'Kendini dev aynasında görmek',	'Kendisini olduğundan büyük bir adam sanmak; üstün, yetenekli, güçlü görmek. <b>Kendini dev aynasında görmekten ne zaman vaz geçeceksin ha!. . </b>'),
(1590,	'Kendini dinlemek',	'1. Önemsiz, küçük rahatsızlıkları büyütmek; hastalık kuruntusu içinde bulunmak. <br />2. Yalnız, sakin kalmak. <b>Uzun bir süre kendimi dinledim, olup biteni tekrar tekrar gözden geçirdim. </b>'),
(1591,	'Kendini göstermek',	'1. Ortaya çıkmak, belirmek. <br />2. Beğenilecek, takdir edilecek niteliklerini ortaya koymak; gücünü göstermek. <b>Uzun bir aradan sonra sergi açmaya, kendini göstermeye karar verdi. </b>'),
(1592,	'Kendini kaptırmak',	'Bir şeyin etkisinden kendini kurtaramamak. <b>Bu yaştan sonra kendimi sigaraya kaptıracağım hiç aklıma gelmezdi doğrusu. </b>'),
(1593,	'Kendini kaybetmek',	'1. Düşüp bayılmak. <br />2. Kızgınlık, öfke yüzünden ne yaptığını bilmeyecek hâle gelmek. <b>Bir iki söz söyledikten sonra kendini kaybetti, oraya yığılıverdi. </b>'),
(1594,	'Kendini toplamak',	'1. Kötü, bozuk olan durumunu düzeltmek. <br />2. Bir konu üzerinde dikkatini yoğunlaştırmak. <br />3. Şişmanlamak. <b>Bizim oğlan kendini iyice toparladı, şimdi ev almayı düşünüyor. </b>'),
(1595,	'Kendini tutamamak',	'Bir durum karşısında sessiz ve heyecana kapılmadan durmayı başaramamak, kendine hâkim olamamak. <b>Kendimi tutamadım, ben de ağlamaya başladım. </b>'),
(1596,	'Kendini vermek',	'Bir şeye bütün varlığıyla bağlanmak, başka şeylerle ilgisini kesip yalnızca onunla ilgilenmek, bir şeyi tüm gücüyle yapmaya çalışmak. <b>İşe henüz kendini vermiş sayılmaz. </b>'),
(1597,	'Kendi payıma',	'<b>Bana gelince, bana kalırsa, fikrime göre, bana sorarsanız</b> anlamlarında kullanılır.'),
(1598,	'Kendi yağıyla kavrulmak',	'Elindekiyle yetinmeye, kimseye muhtaç olmadan yaşamaya çalışmak; ihtiyaçlarını kendi karşılayarak kimseden yardım istememek. <b>Nasıl olalım, kendi yağımızla kavrulup gidiyoruz işte. . . </b>'),
(1599,	'Kene gibi yapışmak',	'Yakasını bir türlü bırakmamak; istenmediği hâlde, çıkar sağladığı için birinin peşini bırakmamak. <b>Kene gibi yapışmıştı adamın yakasına, peşini bir türlü bırakmıyordu. </b>'),
(1600,	'Kesenin ağzını açmak',	'Bol para harcamaya başlamak. <b>Babam kesenin ağzını açtı nihayet. </b>'),
(1601,	'Keyfinin kâhyası (olmamak)',	'Birisine karışmaya hakkı olmamak, istediği gibi yaşamasına engel olmamak. <b>O benim keyfimin kâhyası olamaz, ben dilediğim gibi yaşarım, karışamaz bana!</b>'),
(1602,	'Keyif çatmak',	'Neşeli olmak, hoş ve eğlenceli zaman geçirmek. <b>İşi nihayet bitirmiştik, sıra şimdi keyif çatmaya gelmişti. </b>'),
(1603,	'Keyif ehli',	'Rahatına düşkün kimse, zevkinden bol bol yararlanan. <b>Oldukça rahat, keyif ehli bir insandı. </b>'),
(1604,	'Kılı kırk yarmak',	'Titizlenmek, çok dikkat ederek en ince ayrıntılarına kadar incelemek, önemle üstünde durmak. <b>Bir malı almadan önce kılı kırk yararcasına evirir çevirir ve öyle alırdı. </b>'),
(1605,	'Kılına dokunmamak',	'Bir kimseye, zarar verebilecek en ufak davranıştan bile kaçınmak. <b>İnan anne, kılına bile dokunmadım kardeşimin!</b>'),
(1606,	'Kılını bile kıpırdatmamak (veya oynatmamak)',	'Bir durum karşısında en küçük bir tepki bile göstermemek, ilgisiz kalmak, harekete geçmemek. <b>Onca insan üstüme yürüdü ama o kılını bile kıpırdatmadı. </b>'),
(1607,	'Kıl payı (kalmak)',	'Çok az, az bir fark (kalmak). <b>Araba o hızla virajı alamadı, uçuruma yuvarlanmasına kıl payı kalmıştı. </b>'),
(1608,	'Kıran girmek',	'1. Daha önce bulunan şey bulunmaz olmak. <br />2. Hayvanlar ya da insanlar arasında öldürücü bir hastalık yayılmak. <b>Kıran girdi, bütün koyunlar telef oldu. </b>'),
(1609,	'Kırık dökük',	'1. Eski çürük, sağlam olmayan, değersiz (şey). <br />2. Düzgün olmayan, parça parça, dağınık (söz). <b>Şu kırık dökük eşyaları ortadan kaldırın hemen!</b>'),
(1610,	'Kırıp geçirmek',	'1. Yakıp yıkarak, baskı yaparak, öldürerek büyük zarar vermek. <br />2. Çok sert davranarak darıltmak. <br />3. Garip olan söz ve davranışlarıyla herkesi güldürmekten katıltmak.'),
(1611,	'Kırk dereden su getirmek',	'Birini kandırmak için çok dolambaçlı gerekçeler ileri sürmek, ikna edebilmek için çok uğraşmak. <b>Ne inatçı adammış, bir evet demek için kırk dereden su getirtti bana. </b>'),
(1612,	'Kırklara kırışmak',	'Bir kimse artık ortalıkta görünmez olmak.'),
(1613,	'Kırk tarakta bezi bulunmak',	'Birbirinden farklı birçok işle uğraşmak, birçok ilişkisi bulunmak, gizli ilişkileri olmak. <b>Ne iş yaptığı belli değil, kırk tarakta bezi var adamın. </b>'),
(1614,	'Kısmeti açılmak',	'1. Kazancı artıp bolluğa erişmek. <br />2. Bir kızı isteyenlerin çoğalması. <b>Bu miras kızın kısmetini de açtı hani!</b>'),
(1615,	'Kısmetini (nimetini) ayağıyla tepmek',	'Kavuşacağı iyi bir durumu, kıymetini bilmeyerek reddetmek; istememek, değerlendirememek.'),
(1616,	'Kıssadan hisse almak',	'Bir olaydan, anlatılan bir hikâyeden ders almak.'),
(1617,	'Kıt kanaat (geçinmek)',	'Yoksulluk içinde, zar zor ve güçlükle (geçinmek). <b>Bir zamanlar biz de kıt kanaat geçiniyorduk. </b>'),
(1618,	'Kıvamına gelmek (bulmak)',	'En uygun zamanında olmak, gerekli ve istenilen şartlar yerine gelmek, istenilen duruma gelmek.'),
(1619,	'Kıyamet kopmak',	'1. Kıyamet günü gelmek. <br />2. Bir yerde çok gürültü ve patırtı kavga, telâş olmak. <b>Kıyamet günü gelecek ve insanlar sonunda hesaba çekilecekler. </b>'),
(1620,	'Kızarıp bozarmak',	'Utanarak renkten renge girmek, kimi duyguların etkisiyle yüzünün rengi değişmek. <b>Pot kırdığını anlayınca ne yapacağını şaşırdı, kızarıp bozaran yüzünü kapatmaya çalıştı. </b>'),
(1621,	'Kızıl (kızılca) kıyamet kopmak',	'Bir meselede büyük, aşırı, gürültülü bir kavgaya yol açmak; yüksek sesli tartışma başlatmak. <b>Sizin bostanlara su vermeyeceğim deyince kızılca kıyamet koptu. </b>'),
(1622,	'Kilit noktası',	'Bütün işlerin çözümlenmesi ona bağlı olan önemli unsur, üzerinde durulması gereken en önemli nokta, makam veya yer.'),
(1623,	'Kimseye eyvallah etmemek',	'Kimseden yardım ve iyilik beklememek, kimsenin minneti altına girmemek. <b>Bu yaşa kadar kimseye eyvallah etmedim, bundan sonra da edecek değilim. </b>'),
(1624,	'Kim vurduya gitmek',	'Bir kargaşa anında ve kalabalık arasında kimin tarafından vurulduğu veya dövüldüğü belli olmamak.'),
(1625,	'Kirişi kırmak',	'Kaçıp gitmek, bulunduğu yerden gizlice ve çabucak ayrılmak. <b>Kavga başlayınca kirişi kırarım diye düşündü. </b>'),
(1626,	'Kirli çamaşırlarını ortaya dökmek',	'Ayıp, suç ve kusurlarını, gizli kalmış yolsuzluklarını açığa çıkarmak; açıklamak, söylemek. <b>Kirli çamaşırları ortaya dökülünce ne yapacağını şaşırdı. </b>'),
(1627,	'Kitaba el basmak',	'Elini kutsal kitap olan Kur`ân-ı Kerim üzerine koyarak yemin etmek.'),
(1628,	'Kitabına uydurmak',	'Kanunî olmayan bir işi kimi boşluklardan yararlanarak kanunî imiş gibi göstermek. <b>İşi kitabına uydurmuşlar, çok zengin olmuşlardı. </b>'),
(1629,	'Kof çıkmak',	'İşe yaramadığı, sanıldığı gibi olmadığı, boş ve değersiz bir kişi olduğu anlaşılmak.'),
(1630,	'Kokusu çıkmak',	'Gizli yapılmış bir iş, daha sonra herkes tarafından bilinir olmaya başlamak. <b>Bu işin kokusu çıkar diye korkuyorum. </b>'),
(1631,	'Kolaçan etmek',	'Çevresini ya da kendisinden istenilen yeri dolaşıp ne var ne yok diye bakmak, olup biteni anlamak amacıyla dolaşmak. <b>Bir kişi etrafı şöyle bir kolaçan etsin de gelsin. </b>'),
(1632,	'Kol kanat olmak',	'Yardım etmek, gözetmek, bir kimseyi koruyuculuğu altına almak.'),
(1633,	'Koltukları kabarmak',	'Kendisine ya da yakınlarına yapılan övgüden ötürü kıvanç duyup büyüklenmek, böbürlenmek. <b>Oğlun oldukça becerikli dedikleri zaman koltuklarım kabardı doğrusu. </b>'),
(1634,	'Kolu kanadı kırılmak',	'Çaresiz duruma düşmek, bir şey yapamaz hâle gelmek. <b>Kolu kanadı kırılmış bir vaziyette dolaşıyordu. </b>'),
(1635,	'Korktuğu başına gelmek',	'Endişe duyduğu, kaygılandığı, olmasını istemediği şeyle karşı karşıya gelmek. <b>Korktuğum başıma geldi, ne yapacağım şimdi ben!</b>'),
(1636,	'Koyun kaval dinler gibi',	'Düşünmeden, hiçbir şeyi anlamadan, ne denildiğini kavramadan dinlemek. <b>Beni koyun dinler gibi dinleyip çekip gittiler. </b>'),
(1637,	'Kozunu paylaşmak',	'Aradaki anlaşmazlığı zora başvurarak, üstün olan güce dayandırarak çözümlemek, sona erdirmek. <b>Onunla kozunu paylaşmaya can atıyordu. </b>'),
(1638,	'Kök salmak',	'1. Bir yere iyice, ayrılmamacasına yerleşmek. <br />2. İyice tutunmak, köklenmek, sağlamlaşmak, yayılmak. <b>Onun sevgisi, içine iyice kök salmıştı. </b>'),
(1639,	'Kök söktürmek',	'Uğraştırmak, güçlük çıkarmak, engel olmak. <b>O takıma kök söktürmeye yemin ettik. </b>'),
(1640,	'Köküne kibrit suyu dökmek',	'Bir daha belirmeyecek, ortaya çıkmayacak biçimde yok etmek, ortadan kaldırmak.'),
(1641,	'Köprüleri atmak',	'Girişilen, başlanılan bir işten vazgeçmeye ya da geri dönmeye imkânı kalmayacak şekilde kesin bir davranış göstermek; ilişkileri bir daha kurulamayacak biçimde bozmak.'),
(1642,	'Kör değneğini beller gibi',	'Bir değişiklik, yenilik düşünmeden, hep aynı biçimde davrananların durumunu anlatmak için kullanılır.'),
(1643,	'Kör dövüşü',	'Sonuç alınamayacak ve birbirini engelleyecek biçimde, bir birinden habersiz düzensiz ve uyumsuz çabalama.'),
(1644,	'Kör kadı',	'Sözünü esirgemeyen; doğru bildiğini hatır gönül dinlemeden her yerde, herkesin yüzüne karşı söyleyen.'),
(1645,	'Köstek olmak',	'Engel olmak. <b>Sen köstek olma yeter. </b>'),
(1646,	'Körü körüne',	'Düşünüp taşınmadan, nasıl sonuçlanacağını hesaplamadan, dikkat etmeden. <b>Bu işe öyle körü körüne giremem, anladın mı?</b>'),
(1647,	'Köşe bucak',	'Göze çarpmayan, önemsiz yer.'),
(1648,	'Kötüye kullanmak',	'Suiistimal etmek, yetkisini yanlış bir yolda kullanmak, istenilmeyen yolda yararlanmak. <b>Benim yumuşaklığımı kötüye kullandı. </b>'),
(1649,	'Kraldan çok kralcı olmak',	'Birinin davasını ondan daha çok savunur olmak.'),
(1650,	'Kucak açmak',	'İhtiyaç sahibi birine sığınacak yer vermek, onu korumak. <b>Muhtaçlara kucak açmak insanlık görevidir. </b>'),
(1651,	'Kumkumav gibi',	'Yapayalnız, tek başına.'),
(1652,	'Kulağı delik',	'Olup bitenleri çabuk haber alan, hemen her şeyden haberi olan. <b>Hasan mı, ne kulağı delik adamdır o, ne öğreneceksen ona sor. </b>'),
(1653,	'Kulağı kirişte (olmak)',	'Söylenecek sözü, gelecek haberi dikkatlice (beklemek). <b>Kulağınız kirişte olsun, ne duyarsanız iletin hemen. </b>'),
(1654,	'Kulağına çalınmak',	'Bir söz, bir haber başkasına söylenirken kendisi de şöyle böyle duymak. o<b>Senin şehre gideceğin kulağıma çalındı, ne diyorsun?</b>'),
(1655,	'Kulağına kar suyu kaçmak',	'Rahatını bozan bir haber işitmek, sıkışık bir duruma düşmek.'),
(1656,	'Kulağına küpe olmak',	'Başına gelen bir işten, gördüğü olaydan ders alıp hiç unutmamak. <b>Umarım bu iş senin kulağına küpe olur da aynı hataya bir daha düşmezsin. </b>'),
(1657,	'Kulağını açmak',	'Bütün dikkatini vererek dinlemek, söylenenlere dikkat etmek. <b>Kulağını aç da beni iyi dinle!</b>'),
(1658,	'Kulağını bükmek',	'Dikkatli olması için uyarıda bulanmak.'),
(1659,	'Kulağını çekmek',	'1. Uyarmak için hafif bir ceza vermek. <br />2. Ceza olarak kulağını büküp çekmek. <b>Şimdi bana kulağınızı çektireceksiniz!</b>'),
(1660,	'Kulak asmamak',	'Aldırıp önemsememek, dinlememek. <b>Kulak asma sen onun söylediklerine. </b>'),
(1661,	'Kulak dolgunluğu',	'Duya duya elde edinilen yarı buçuk bilgi.'),
(1662,	'Kulak kabartmak',	'Çaktırmadan, belli etmemeye çalışarak dinlemek. <b>Dayanamayıp yanındakilerin konuşmalarına kulak kabarttı. </b>'),
(1663,	'Kulak kesilmek',	'Çok iyi, bütün dikkatini vererek dinlemek; dikkatini toplayarak duymaya çalışmak. <b>Ne konuştuklarını merak ediyordum, yanlarına yaklaşarak kulak kesildim. </b>'),
(1664,	'Kulaklarını çınlatmak',	'Birini iyi duygularla anmak.'),
(1665,	'Kul hakkı',	'İslâm dinine göre, insanların birbirleri üzerindeki hakları. <b>Öte dünyaya kul hakkıyla gitmem inşallah. </b>'),
(1666,	'Kul köle (veya kurban) olmak',	'Tam bir doğruluk içinde gönülden bağlanmak, bağlılığın gerektirdiği fedakârlığı yapmaya hazır olmak.'),
(1667,	'Kulp takmak',	'Bir kusur, bir bahane bulmak.'),
(1668,	'Kumpas kurmak',	'Birini aldatmak için tuzak kurmak, gizli bir iş düzenlemek.'),
(1669,	'Kundak sokmak',	'1. Yangın çıkarmak için bir yere tutuşmuş yağlı bez parçası koymak. <br />2. Ara bozacak bir söz ya da davranışta bulunmak.'),
(1670,	'Kurban olayım',	'1. Aşırı sevgi ve hayranlık anlatmak için kullanılır. <br />2. Yalvarmak için söylenir. <b>Kurban olayım yavruma dokunmayın!</b>'),
(1671,	'Kurşuna dizmek',	'Ölüm cezasını askerî bir birliğin attığı kurşunlarla yerine getirmek, sıkılan kurşunlarla öldürmek. <b>Bütün köy halkını kurşuna dizdiler!</b>'),
(1672,	'Kurtlarını dökmek',	'Öteden beri yapmak istediği şeyi bol bol yapıp hevesini almak. <b>Bu akşam biraz kurtlarımızı dökelim, ne dersin?</b>'),
(1673,	'Kurt masalı okumak',	'İnandırıcı, gereksiz, asılsız sözler (söylemek).'),
(1674,	'Kuru iftira',	'Hiçbir kanıtı olmayan suçlama. <b>Allah kuru iftiradan korusun hepimizi!</b>'),
(1675,	'Kuru kalabalık',	'1. Yararsız kırık dökük eşya. <br />2. Hiçbir işe yaramayan insan topluluğu. <b>Bu kuru kalabalığa güvenip de sakın yola çıkma. </b>'),
(1676,	'Kuru kuruya',	'Boşuna, boş yere.'),
(1677,	'Kuru sıkı',	'1. Korkutmak amacıyla söylenen sözler, blöf. <br />2. Yalnız barutla sıkılanmış tüfek veya fişek dolgusu.'),
(1678,	'Kuş beyinli',	'Akılsız, aptal, ahmak.'),
(1679,	'Kuş kadar canı olmak',	'Küçük, cılız, zayıf, çelimsiz bir vücuda sahip olmak.'),
(1680,	'Kuş sütüyle beslemek',	'En pahalı, değerli az bulunur besinlerle yiyip içirmek.'),
(1681,	'Kuş uçmaz, kervan geçmez',	'Çok ıssız, sapa, kır, insanın uğramadığı yer. <b>Başını alıp kuş uçmaz kervan geçmez bir diyara gitti. </b>'),
(1682,	'Kuş uçurmamak',	'Hiç kimsenin geçmesine, kaçmasına izin vermemek; imkân tanımamak, bunun için çok dikkatli davranmak. <b>Sıkı gözcülerdir, kuş uçurtmazlar, merak etme!</b>'),
(1683,	'Kuvvetten düşmek (kesilmek)',	'Gücü iyice azalmak.'),
(1684,	'Kuyruğuna basmak',	'Birini tahrik etmek, incitip saldırmasına yol açmak.'),
(1685,	'Kuyruklu yalan',	'İnsanın kanması için süslenmiş büyük yalan. <b>İnanmayın ona, söyledikleri kuyruklu yalandan başka bir şey değil!</b>'),
(1686,	'Kuyruk sallamak',	'Yaltaklanmak, birisine yaranmak için yapmacık davranışlarda bulunup şirin görünmeye çalışmak. <b>Bütün gece boyunca şirket müdürüne kuyruk sallayıp durdu. </b>'),
(1687,	'Kuyusunu kazmak',	'Birinin kötü duruma düşmesi, felâkete uğraması, zarar görmesini sağlamak için zemin hazırlamak, tuzak kurmak. <b>Adamın kuyusunu kazıp da elinize ne geçecek. </b>'),
(1688,	'Küçük dilini yutmak',	'Çok şaşmak, hayrete düşmek, donakalmak, hiçbir şey söyleyemez hâle gelmek. <b>Ne o dostum, küçük dilini mi yuttun?</b>'),
(1689,	'Küçük düşürmek',	'Onurunu kırmak, birilerinin yanında itibarını sarsmak ve değerini düşürmek. <b>Dikkatli ol, bir pot kırıp da kendini küçük düşürme sakın. </b>'),
(1690,	'Küçük görmek',	'Önemsememek, değer vermemek. <b>Hasmınızı sakın küçük görmeyin çocuklar!</b>'),
(1691,	'Külâhıma anlat',	'<b>Söylediklerin hiç de inandırıcı değil, sana inanmıyorum</b> anlamında kullanılır.'),
(1692,	'Külâhını ters giydirmek',	'Çok kurnaz olmak; oyuna getirmek, kendisine iyi davranmayanları bir hile ile yaptıklarına pişman etmek.'),
(1693,	'Külâhları değişmek',	'<b>Araları bozulmak, bozuşmak</b> anlamında tehdit olarak kullanılır. <b>Hareketlerini düzeltmezsen külâhları değişiriz, ona göre!</b>'),
(1694,	'Kül kedisi',	'1. Çok üşüyen, ateşin yanından ayrılmayan (kimse). <br />2. Uyuşuk, miskin, rahatına düşkün, tembel.'),
(1695,	'Kül kesilmek',	'Heyecan ve korkudan yüzünün rengi atmak, solmak. <b>Katili karşısında görünce yüzü kül kesildi. </b>'),
(1696,	'Kül olmak',	'1. Bir şey bütünüyle yanmak. <br />2. Varını yoğunu yitirmek, elinde bulunanlar yok olmak. <br />3. Büyük bir felâkete uğrayıp çok üzülmek.'),
(1697,	'Külünü (göğe) savurmak',	'Bir şeyi tamamiyle bitirip yok etmek, harcayıp tüketmek, telef edip bir şey bırakmamak.'),
(1698,	'Kül yutmamak',	'Oyuna gelmemek, tuzağa düşmemek, kurnazca yapılan bir hileye aldanmamak. <b>Bana kül yutturamazsınız diyemem ama yeterince dikkatli olduğumu söyleyebilirim. </b>'),
(1699,	'Künyesi bozuk',	'Eskiden kötü durumları görülmüş olan, kötü işlere girmiş bulunan. <b>Künyesi bozuk diye, bu adama hiç kimse iş vermeyecek mi?</b>'),
(1700,	'Küplere binmek',	'Haddinden fazla öfkelenme, kızmak, sağa sola ateş saçmak. <b>Yeni saatimi kırdığımı öğrenen annem küplere bindi. </b>'),
(1701,	'Küpünü doldurmak',	'Eline geçen fırsatları değerlendirerek çok para biriktirmek. <b>Küpünü doldurmayı becerebilenlerden olamadım hiç. </b>'),
(1702,	'Kürek kadar (pabuç kadar) dili olmak',	'Hemen her söze cevap yetiştirmek, büyüklerine karşı saygısızca karşılıklar verir olmak.'),
(1703,	'Laçka olmak',	'1. Herhangi bir iş gevşek ve düzensiz yürütülmek. <br />2. Mil ya da vida gibi makine bölümleri eskiyip aşınarak işe yaramaz hâle gelmek. <b>Bu vidalar laçka olmuş, kol tutmuyor. </b>'),
(1704,	'Lafa boğmak',	'Birinin söz söylemesine fırsat vermeyip meseleyi gereksiz ve boş sözlerle anlaşılmaz kılmak, gürültüye getirip uzatmak.'),
(1705,	'Laf (söz) altında kalmamak',	'Bir münakaşa sırasında söylenen her dokunaklı söze karşılık vermek, söz altında ezilmemek.'),
(1706,	'Laf (söz) aramızda',	'<b>Söyleyeceğim sözleri başka biri duymasın, bilmesin, konuştuklarımız aramızda kalsın</b> anlamında kullanılır. <b>Laf aramızda, Ali yine öç alacağım demeye başlamış. </b>'),
(1707,	'Laf atmak',	'1. Dokunaklı sözlerle sataşmak, uzaktan işittirmek. <br />2. Karşılıklı söyleşmek, konuşmak. <br />3. Sözle sarkıntılık etmek. <b>Laf atarak beni tahrik etmeye çalışıyorlardı. </b>'),
(1708,	'Lafa tutmak',	'Birini konuşarak, gereksiz meseleler anlatarak işinden alıkoymak. <b>Onu biraz lafa tutup oyalamaya başladılar. </b>'),
(1709,	'Laf ebesi',	'Söyleyecek sözü bol olan, her söze karışan, herkese söz yetiştiren, çok konuşan. <b>Laf ebeliğini bırak da ne söyleyeceksen söyle!</b>'),
(1710,	'Laf etmek',	'1. Konuşmak. <br />2. Bir şeyi dedikodu konusu yapmak. <b>Akşam buluşalım da iki çift laf edelim. </b>'),
(1711,	'Lafı (sözü) ağzına tıkamak',	'Birinin sözünü bitirmesine fırsat vermemek, onu susmak zorunda bırakmak, konuşmasını önlemek. <b>Ağzını açar açmaz lafı ağzına tıkadılar adamcağızın. </b>'),
(1712,	'Lafı (sözü) ağzında gevelemek',	'Söylemek istediğini açık olarak bir türlü söyleyememek, şundan bundan bahsetmek. <b>Beni görünce şaşırdı, lafı ağzında gevelemeye başladı. </b>'),
(1713,	'Lafı ağzında kalmak',	'Söyleyeceğini söylemeye zaman bulamamak, konuşmasını bitirememek.'),
(1714,	'Lafı (sözü) çevirmek',	'Konuşmasının sakıncalı bir biçim aldığını fark edince söze başka bir yön vermek, başka konuya geçmek. <b>Beni görünce birden nasıl da sözü çevirdi. </b>'),
(1715,	'Lafını (sözünü) etmek',	'Bir şey üzerinde konuşmak. <b>Artık lafını etmeyin şu adamın!</b>'),
(1716,	'Lafını (sözünü) bilmek',	'Tutarlı ve mantıklı konuşmak, sakıncalı olmayan ve birini kırmayan sözler söylemek, saygılı ve yerinde konuşmak. <b>O daima lafını bilir bir insan olmuştur. </b>'),
(1717,	'Laf işitmek',	'Birisi tarafından paylanmak, azarlanmak, <b>Çabuk ol, senin yüzünden laf işiteceğiz öğretmenden. </b>'),
(1718,	'Laf olsun diye',	'Rastgele, belli bir amaç gütmeden. <b>Kızma canım, laf olsun diye söylemiştir o sözleri. </b>'),
(1719,	'Laf (söz) taşımak',	'Aralarını açmak maksadıyla birinin bir kimse hakkında söylediği hoş olmayan sözlerini o kimseye ulaştırmak, söz getirip götürmek. <b>O laf taşıyıcı adamdan uzak durmalısın. </b>'),
(1720,	'Laf (söz) yetiştirmek',	'Bir söze karşılık vermekte gecikmemek, durmadan konuşmak.'),
(1721,	'Laf (söz) yok',	'<b>Kusursuz, eksiksiz, eleştirilecek bir yanı dahi yok</b> anlamında kullanılır. <b>Arkadaşıma laf yok, o mert mi mert biridir. </b>'),
(1722,	'Lâhavle çekmek',	'Sıkıntıyı, öfkeyi gidermek, sabır telkin etmek için <b>Lâhavle</b> ile başlayan duayıokumak. <b>Lâhavle çekmeden başka bir şey yapamadım. </b>'),
(1723,	'Lamı cimi yok',	'<b>Hiçbir bahane, itiraz, mazeret, duraksama, karşı gelme yok</b> anlamında kullanılır. <b>Lamı cimi yok, bu akşam bize geleceksiniz, tamam mı?</b>'),
(1724,	'Lastikli söz',	'Değişik mânâlara gelen söz.'),
(1725,	'Leb demeden leblebiyi anlamak',	'Daha sözün başında ne demek istediğini anlamak, anlayışlı ve kavrayışlı olmak.'),
(1726,	'Leke sürmek',	'Suç yüklemek, birinin onurunu sarsacak biçimde iftirada bulunmak. <b>Zorla kadıncağıza kara bir leke sürdüler, Allah`tan hiç korkmadılar. </b>'),
(1727,	'Leşini çıkarmak',	'Çok feci dövmek. <b>Beş kişiydiler, adamın leşini çıkardılar. </b>'),
(1728,	'Leşini sermek',	'Öldürmek. <b>Ben de onun leşini sermezsem. . . </b>'),
(1729,	'Leyleğin yuvadan attığı yavru',	'Yakınlarından ilgi görmeyen, çevresinin uzaklaştırdığı kimse.'),
(1730,	'Lokma ağzında büyümek',	'Herhangi bir sebepten, acı ya da üzüntüden dolayı lokmasını yutamamak, yiyememek. <b>Ağzında lokmalar büyümeye başladı, gözleri dolu dolu oldu. </b>'),
(1731,	'Lokmasını saymak',	'Birinin ne kadar yediğine bakmak, çok yiyeceğinden korkmak.'),
(1732,	'Lök gibi oturmak',	'Bir yere bütün ağırlığıyla çökmek, oturup kalmak. <b>Sedire lök gibi oturunca gacur gucur sesler duyuldu. </b>'),
(1733,	'Lügat paralamak',	'Anlaşılmaz, süslü, parlak, ağdalı, konuşma dilinde geçmeyen kelimelerle konuşmak. <b>Lügat paralamak hoşuna gitmeye başlamıştı. </b>'),
(1734,	'Lüpe konmak',	'Değerli bir şeyi bedavadan, emek sarf etmeden ele geçirmek.'),
(1735,	'Maaşa geçmek',	'Aylığa geçmek, çalıştığı yerden ücret almaya başlamak. <b>Maaşa geçtiği günün ertesinde onu işten çıkardılar. </b>'),
(1736,	'Madalyanın ters (öteki) yüzü',	'Olumlu bir olay, iş ya da durumun düşünülmesi, hesaba katılması gereken olumsuz yönü.'),
(1737,	'Madik atmak',	'Hile, düzen ve oyunla aldatmak; dolap çevirmek. <b>Ona kolay kolay kimse madik atamaz. </b>'),
(1738,	'Mahalle karısı',	'Kaba, terbiyesiz, görgüsüz, kavgacı kadın.'),
(1739,	'Mahalleyi ayağa kaldırmak',	'Bağırıp çağırarak, gürültü kopararak konu komşuyu rahatsız etmek, telâşlandırmak. <b>Bağırıp durma öyle, mahalleyi ayağa kaldıracaksın. </b>'),
(1740,	'Mahkemelik olmak',	'Kavga veya anlaşmazlık sonucu mahkemeye düşmek. <b>Bu gidişle mahkemelik olacağız galiba. </b>'),
(1741,	'Mahşer midillisi',	'Kısa boylu, fitneci kimse.'),
(1742,	'Mahşer gibi',	'Çok kalabalık. <b>Meydan mahşer gibiydi. </b>'),
(1743,	'Makaraları koyvermek',	'Kendini tutamayıp kahkahayla gülmeye başlamak, uzun uzun gülmek. <b>Yüzükoyun çamura düşen arkadaşını görünce makaraları koy verdi. </b>'),
(1744,	'Makas almak',	'Birinin yanağını orta parmakla gösterme parmağı arasında sıkmak.'),
(1745,	'Mal bulmuş mağribi gibi',	'Büyük bir zenginliğe kavuşmuşcasına büyük sevinç ve coşku ile.'),
(1746,	'Mal etmek',	'1. Bir malı hakkı olmadığı hâlde kendisininmiş gibi göstermek veya saymak. <br />2. Bir mala, bir değer karşılığında sahip olmak. <b>O tarlayı kendisine mal etmesine göz yummayacağım. </b>'),
(1747,	'Malın gözü',	'1. Aşağılık ve düzenci kimse. <br />2. İffetsiz. <br />3. İyi mal.'),
(1748,	'Mânâ çıkarmak',	'Yanlış bir yargıya varmak, bir söz ya da hareketten kendine göre bir anlam çıkarmak. <b>Öyle alıngandı ki her sözümden bir mânâ çıkarıyordu. </b>'),
(1749,	'Mânâ vermek',	'Kendine göre bir yargıya varmak, yorumlamak. <b>Senin bu davranışına bir mânâ veremiyorum. </b>'),
(1750,	'Maneviyatı bozulmak',	'Moral gücü sarsılmak, kendine güveni yitirmek, kendini güçsüz ve dirençsiz hissetmek. <b>Düşmanlar, toplumumuzun önce maneviyatını bozdular. </b>'),
(1751,	'Mantar gibi yerden bitmek',	'Birdenbire ya da kendiliğinden ortaya çıkmak. <b>Adamlar mantar gibi yerden bitmişlerdi, bir anda etrafımızı sarıverdiler. </b>'),
(1752,	'Maraza çıkarmak',	'Anlaşmazlığa yol açacak işler yapmak, kavgaya yol açmak.'),
(1753,	'Martaval atmak',	'İnanılmayacak şeyler uydurmak, yalan söylemek. <b>Amma da martaval atıyordu adam. </b>'),
(1754,	'Mart içeri pire dışarı',	'Birbirinden hoşlanmayan iki kişiden biri gelince ötekinin dışarı çıkışını anlatmak için kullanılır.'),
(1755,	'Masal okumak',	'İnandırıcı olmayan, oyalayıcı ve avutucu sözler söylemek. <b>Bana masal okuma, olayın gerçek yüzünü anlat. </b>'),
(1756,	'Maskara olmak',	'Gülünç hâllere düşmek, alay konusu olmak. <b>Kim düşmanının maskarası olmak ister?</b>'),
(1757,	'Maskesi düşmek',	'Gerçek yüzü, kimliği, niteliği ortaya çıkmak. <b>Nihayet maskesi düştü, herkes onun ne mal olduğunu anlayacak. </b>'),
(1758,	'Masrafa girmek',	'Çok para harcamak. <b>Evi yaptılar ama çok da masrafa girdiler. </b>'),
(1759,	'Masrafı çekmek',	'Bir iş için gereken parayı ödemek, gideri karşılamak. <b>Yarınki gezide bütün masrafları Ahmet çekecekmiş. </b>'),
(1760,	'Maşallahı var',	'Bir şey ya da kimsenin iyi durumda olduğunu anlatmak için kullanılır. <b>Adamın maşallahı var, hiçbir yoksulu geri çevirmedi. </b>'),
(1761,	'Maşası olmak',	'Sakıncalı bir işte, biri tarafından araç olarak kullanılmak. <b>İşverense işveren, onun maşası olamam ben!</b>'),
(1762,	'Mat etmek',	'1. Satranç oyununda yenmek. <br />2. Bir tartışmada, karşı tarafı söz söyleyemeyecek duruma getirmek. <b>İleri sürdüğü kanıtlar ile karşısındakileri kısa zamanda mat etti. </b>'),
(1763,	'Matrak geçmek',	'Alay etmek, karşısındakiyle eğlenmek, dalga geçmek. <b>İnsanlarla matrak geçmeye bayılıyorsun. </b>'),
(1764,	'Maval okumak',	'Tutarlı, inandırıcı olmayan, yalan sözler söylemek. <b>Kes sesini, maval okumandan bıktım artık!</b>'),
(1765,	'Mayası bozuk',	'Karaktersiz, kötü yaradılışlı, aşağılık (kişi). <b>Şu mayası bozuk adamın çenesini kapayın, sesini duymak istemiyorum. </b>'),
(1766,	'Maymun iştahlı',	'Kararsız, hevesi çabuk geçen; bugün şunu yarın ötekini beğenen. <b>Maymun iştahlılığı yüzünden başına olmadık işler geldi. </b>'),
(1767,	'Mekik dokumak',	'İki yer arasında durmadan gidip gelmek. <b>Mağaza ile ev arasında tam elli beş yıl mekik dokumuştu rahmetli. </b>'),
(1768,	'Mendil açmak',	'Dilenmek.'),
(1769,	'Merak etmek',	'1. Kaygılanmak. <br />2. Öğrenmek, anlamak isteği taşımak. <b>Merak etmeye başladım, bu saate kadar gelmeliydiler. </b>'),
(1770,	'Merhabası olmak',	'Birisiyle selâmlaşacak kadar tanışıklığı, yakınlığı bulunmak.'),
(1771,	'Merhabayı kesmek',	'Biriyle ilgiyi kesmek, arkadaşlığa son vermek. <b>Onunla merhabayı keseli epey zaman olmuştu. </b>'),
(1772,	'Mesele çıkarmak',	'Üzüntü verecek, içinden zor çıkılacak, bir anlaşmazlığa sebep olacak bir durum oluşturmak. <b>Haydi, bir mesele çıkarmadan çekip gidin buradan. </b>'),
(1773,	'Mesken tutmak',	'Yerleşmek. <b>Yarim İstanbul`u mesken mi tuttun!</b>'),
(1774,	'Meteliğe kurşun atmak',	'Parasız pulsuz kalmak, hiç parası olmamak. <b>Dün meteliğe kurşun atıyordu, ya bugün. . . </b>'),
(1775,	'Metelik vermemek',	'Değer vermemek, umursamamak, aldırış etmemek. <b>Onun gibilere metelik vermem mi diyorsun?</b>'),
(1776,	'Mevki sahibi olmak',	'Yüksek bir görevde, bir işte önemli bir aşamada bulunmak. <b>Mevki sahibi olmak için yıllarca çalışıp durdu. </b>'),
(1777,	'Meydana çıkmak',	'1. Görünmek. <br />2. Belli olmak. <br />3. Yetişmek, büyümek, olmak. <b>Korkak herif meydana çık da yüzünü görelim. </b>'),
(1778,	'Meydana gelmek',	'1. Olmak, oluşmak, vücut bulmak. <br />2. Ortaya çıkmak. <b>Olay akşam üzeri meydana geldi diyorlar. </b>'),
(1779,	'Meydanı boş bulmak',	'Kendisine mâni olacak kimse bulunmadığı için aşırı davranışlarda bulunmak, bir şeyden çekinmemek. <b>Meydanı boş bulan eşkıyalar ortalığı kasıp kavurmaya başlamışlardı. </b>'),
(1780,	'Meydan okumak',	'Kavga ya da yarışmaya çağırmak, korkmadığını ve çekinmediğini açıkça bildirmek. <b>Bir an meydan okumayı içinden geçirdi, sonra bundan vazgeçti. </b>'),
(1781,	'Meydan vermemek',	'Olumsuz bir olay ya da durumun gerçekleşmesine imkân ve zaman vermemek, engel olmak. <b>Onların kavga etmesine sakın meydan vermeyin çocuklar. </b>'),
(1782,	'Mezhebi geniş',	'Namus konusunda gerekli olan titizliği göstermeyen, kadın-erkek ilişkilerinde dini kaidelere aldırış etmeyen, iffetsizliğe meydan veren, geniş davranan.'),
(1783,	'Mezar kaçkını',	'Çok zayıf, bitkin, güçsüz düşmüş kişi.'),
(1784,	'Mırın kırın etmek',	'Bir isteği yerine getirmemek için çeşitli bahaneler ileri sürüp nazlanmak. <b>Mırın kırın etmeyi bırak da yak şu sobayı. </b>'),
(1785,	'Mızıkçılık etmek',	'Bir oyunu ya da birlikte yapılan bir işi çeşitli bahaneler ileri sürerek bozmaya çalışmak, razı olmamak.'),
(1786,	'Mide bulandırmak',	'1. Kusacak bir duruma getirmek. <br />2. Kuşkulandırmak. <b>Çekil çabuk karşımdan, midemi bulandırıyorsun!</b>'),
(1787,	'Midesi bulanmak',	'1. Kusacak gibi olmak. <br />2. İğrenmek, tiksinmek. <br />3. Kuşkulanmak. <b>Yaptığınız iş, mide bulandırıcı bir işti!</b>'),
(1788,	'Mideye oturmak',	'Yenilen bir şeyin sindirim zorluğu vermesi.'),
(1789,	'Mihenk (taşı)',	'Birinin değerini, ahlâkını anlamaya yarayan ölçüt.'),
(1790,	'Mim koymak',	'1. (Bir şey) unutulmaması için işaret koymak. <br />2. Önemli bularak üstünde durmak, dikkate almak, önemli şeyler arasında saymak. <b>Bu ata sözüne bir mim koy, dedi öğretmenim. </b>'),
(1791,	'Minnet etmek',	'Boyun eğmek, yalvarmak. <b>Ona buna minnet etmeden yaşamak istediğimi biliyorsun değil mi?</b>'),
(1792,	'Moda olmak',	'Yaygın duruma gelmek, gözde olmak, beğenilir ve arzu edilir olduğu için yapılır olmak. <b>Saçları kısa kestirmek bu yıl moda oldu. </b>'),
(1793,	'Modası geçmek',	'Yaygın olmaktan çıkmak, önemini yitirmek. <b>Bu elbisenin modası geçti artık. </b>'),
(1794,	'Mola vermek',	'Bir süre ara vermek; uzun süren yolculuğun, çalışmanın, yürüyüşün yorucu etkisini atmak için bir süre dinlenmek. <b>Yarım saat sonra mola verecekler, onlara mola yerinde yetişebiliriz. </b>'),
(1795,	'Muhallebi çocuğu',	'Nazlı, el bebek gül bebek büyütülmüş, dayanıksız, narin kimse. <b>Senin gibi muhallebi çocuklarıyla iş yapamam ben. </b>'),
(1796,	'Mukabelede bulunmak',	'Karşılık vermek.'),
(1797,	'Mumla aramak',	'Çok istek ve özlemle aramak. <b>O anneyi siz mumla arayacak ama bir daha bulamayacaksınız. </b>'),
(1798,	'Mum (gibi) olmak',	'1. Yaramazlığı, hırçınlığı, uyumsuzluğu bırakıp yola gelmek. <br />2. Razı olmak. <b>Askerde onun da mum gibi olacağına eminim. </b>'),
(1799,	'Muradına ermek',	'Dileği gerçekleşmek, çok istediği şeye kavuşmak. <b>İnşallah muradına erersin kızım. </b>'),
(1800,	'Mümkün mertebe',	'Olabildiğince, yapabildiği kadar. <b>Zararınızı mümkün mertebe karşılama yoluna gideceğimizden emin olun lütfen. </b>'),
(1801,	'Mürekkebi kurumadan',	'Bir şeyin yazılmasından çok kısa bir süre sonra.'),
(1802,	'Mürekkebi kurumadan bozmak',	'Bir kararı, sözleşmeyi, anlaşmayı yazılmasından kısa bir süre sonra bozmak.'),
(1803,	'Mürekkep yalamış',	'Az çok öğrenim görmüş, okuyup yazmış, belli bir kültüre sahip olmuş kimse. <b>Maval okumayı bırakın, biz de mürekkep yalamışlardan sayılırız. </b>'),
(1804,	'Mürüvvetini görmek (anne, baba için)',	'1. Özellikle evlâdının evlendiğini, çoluk çocuk sahibi olduğunu görmek. <br />2. Çocuklarının sevinçli günlerini görerek mutluluk duymak. <b>Acaba çocuklarımın mürüvvetini görecek miyim?</b>'),
(1805,	'Müslüman adam',	'Hak yemeyen, doğruluktan ayrılmayan, İslâm`ın emirlerine uyan kimse. <b>Müslüman adam, başı daima dik olan adamdır. </b>'),
(1806,	'Na (nah) kafa',	'<b>Akılsız, düşüncesiz, kavrayışsız</b> anlamında alay yollu söylenir. <b>Anlaması mümkün değil, na kafa!</b>'),
(1807,	'Nabza göre şerbet vermek',	'Birinin hoşuna gidecek, eğilimlerine cevap verecek biçimde davranmak. <b>Nabza göre şerbet vermeyi iyi biliyorsun. </b>'),
(1808,	'Nabzını yoklamak',	'Eğilimini, niyetini, düşüncelerini, arzularını anlamaya çalışmak. <b>İşçilerin nabzını yoklayın da zam konusunu öyle düşünelim. </b>'),
(1809,	'Nalıncı keseri gibi kendine yontmak',	'Hemen her işte kendi çıkarını düşünerek hareket etmek.'),
(1810,	'Nam almak',	'Tanınmak, ünü her yerde duyulmak.'),
(1811,	'Namus belâsı',	'Namusunu, şerefini, itibarını korumak için katlanılan sıkıntılı durum, kabullenilen zarar ziyan. <b>Namus belâsına az kaldı canından oluyordu delikanlı. </b>'),
(1812,	'Nane molla',	'1. Dirençsiz, güçsüz kimse. <br />2. Çok sık hastalanan, sağlıksız kimse. <br />3. Üşengeç, bir iş yapmaktan kaçınan. <b>Ne nane molla bir adamsın, kalk da biraz çalış. </b>'),
(1813,	'Nara atmak',	'Yüksek bir sesle haykırmak, kabadayıca bağırmak. <b>Birahaneden çıkan sarhoşlar edepsizce nara atmaya başladılar. </b>'),
(1814,	'Nato kafa nato mermer',	'<b>Söz anlamaz, söz dinlemez taş gibi kafa</b> anlamında kullanılır.'),
(1815,	'Naza çekmek',	'Kendini ağır satmak, bir isteği yerine getirmekte yapmacıklı davranışlarla isteksiz gibi davranmak. <b>Kendini naza çekmeye bayılır bizim kız. </b>'),
(1816,	'Nazı geçmek',	'İstediklerini yaptıracak kadar hatırı sayılır olmak. <b>Babası, kasabada oldukça nazı geçen bir insandı. </b>'),
(1817,	'Ne akar ne kokar',	'Kimseye ne faydası ne de zararı dokunan pısırık, çekingen kimseler için kullanılır.'),
(1818,	'Ne çare',	'Çaresi yok, elden bir şey gelmez. <b>Ne çare ki onu durdurmamız mümkün değil. </b>'),
(1819,	'Ne çıkar',	'1. Ne zararı var? <br />2. Bir sonuç vermez. <br />3. Ne fayda, ne zarar umulur. <b>Biraz sert konuşmuşsam, ne çıkar bundan?</b>'),
(1820,	'Neden sonra',	'Bir süre geçince, her şey olup bittikten sonra, çok zaman sonra. <b>Neden sonra babam da geldi. </b>'),
(1821,	'Ne de olsa',	'Ne denli eksiği, kusuru olursa olsun; böyle olmakla birlikte.'),
(1822,	'Ne dese beğenirsin?',	'<b>Nasıl, beklenmeyen bir söz söyledi biliyor musun?</b> anlamında kullanılır.'),
(1823,	'Ne fayda',	'Artık neye yarar.'),
(1824,	'Nefes aldırmamak',	'Dinlenmesine fırsat vermemek, sıkıştırmak, rahat bırakmamak. <b>Nefes aldırmadı bize, sabaha kadar çalıştırdı. </b>'),
(1825,	'Nefesi kesilmek (tıkanmak)',	'Güç soluk alacak duruma gelmek veya soluğu büsbütün durmak. <b>Bir yumrukta nefesini kesti adamın. </b>'),
(1826,	'Nefes nefese gelmek',	'Koşarak, sık sık soluyarak, heyecanlı ve yorulmuş bir şekilde (gelmek). <b>Kapıdan içeri nefes nefese girdi. </b>'),
(1827,	'Nefes tüketmek',	'Bir şeyi anlatmaktan çok yorulmak. <b>Boşuna nefes tüketiyorsun, baksana anlamıyor. </b>'),
(1828,	'Nefsine yedirememek',	'Kendine yakıştıramamak, o şeyi yapmayı kendisi için onur kırıcı, ağır bulmak. <b>İki yüzlülüğü bir türlü nefsine yediremiyordu. </b>'),
(1829,	'Nefsini körletmek',	'Birtakım yollarla iştah duygusunu dindirmek. <b>Nefsini körletmeden iyi bir kul olamazsın. </b>'),
(1830,	'Ne güne duruyor?',	'<b>Şimdi yapmazsa, ne zaman yapacak</b> anlamında kullanılır. <b>Gitsin istesin kızı, daha ne güne duruyor?</b>'),
(1831,	'Nefsini yenmek',	'Arzularının, ihtiraslarının önüne geçebilmek.'),
(1832,	'Ne günlere kaldık!',	'<b>Eskiden daha iyiydi, zaman değişti, düzen ve usuller başkalaştı, çok kötü günler geçiriyoruz</b> anlamında kullanılır.'),
(1833,	'Ne hâli varsa görsün!',	'Uyarılara, öğütlere kulak asmayan insanlar için <b>ne yaparsa yapsın, beni ilgilendirmiyor</b> anlamında kullanılır.'),
(1834,	'Ne idiği belirsiz',	'Ne olduğu, niteliği, soyu sopu, nereli olduğu bilinmeyen. <b>Ne idiği belirsiz bir yığın insan hükümette yer almış. </b>'),
(1835,	'Ne mal olduğunu anlamak',	'Asıl niteliğini, işe yaramaz oluşunu, kötü niyet beslediğini anlamak. <b>Onun ne mal olduğunu şimdi anlarız. </b>'),
(1836,	'Ne mene',	'Ne türlü, nasıl, ne çeşit?'),
(1837,	'Ne od var ne ocak',	'Aşırı yoksulluğu, geçim darlığını anlatmak için kullanılır.'),
(1838,	'Ne oldum delisi olmak',	'Beklemediği bir duruma yükselip şımarmak, ölçüsüz hareketler yapmak. <b>Dikkat et, ne oldum delisi olan insanlar gibi olma. </b>'),
(1839,	'Ne olur',	'<b>Yalvarırım, rica ederim, lütfen</b> anlamında kullanılır. <b>Ne olur beni de götürün köye!</b>'),
(1840,	'Ne olur ne olmaz',	'Her ihtimale karşı, ne olacağı belli değil. <b>Şemsiyeni al, ne olur ne olmaz, yağmura yakalanabilirsin. </b>'),
(1841,	'Ne pahasına olursa olsun',	'Her türlü sıkıntı ve tehlikeyi göze alarak, ne kadar büyük fedakârlık isterse istesin. <b>Ne pahasına olursa olsun ben bu işi bitireceğim. </b>'),
(1842,	'Nerede akşam orada sabah',	'<b>Gece kalacağı bir yeri yok, neresi rast gelirse orada kalıp yatar</b> anlamında kullanılır.'),
(1843,	'Nereden nereye',	'1. Uzak, dolaylı bir ilişki ile. <br />2. Şaşılacak şey, olacak gibi değil!<b>Nereden nereye, kim derdi ki biz karşılaşacağız!</b>'),
(1844,	'Ne şiş yansın ne kebap',	'<b>İki taraf da korunsun, gücendirilmesin, ikisinin de zarar görmeyeceği bir yol bulunsun</b> anlamında kullanılır.'),
(1845,	'Ne tadı var ne tuzu',	'Hoşa gidecek, zevk alınacak, beğenilecek bir şey değil. <b>Ne tadı var ne tuzu yaptığım işin. </b>'),
(1846,	'Nevri dönmek',	'Çok öfkelenmek, sinirlenip kızmak ve bu sebeple rengi değişmek. <b>Saygısızca konuşmaya başlayınca nevri döndü, öfkeyle elini kaldırdı. </b>'),
(1847,	'Ne yardan geçer ne serden',	'İstediği şey fedakârlığı gerektirdiği hâlde, fedakârlığa yanaşmayan ama istediğinden de vazgeçmeyen kimseler için kullanılır.'),
(1848,	'Ne yer ne yedirir',	'Kimsenin yararlanmasını istemez, kendi de yararlanmaz.'),
(1849,	'Neye uğradığını bilememek',	'Beklenmedik bir durumla karşılaşıp hiçbir şey yapamamak, şaşırıp kalmak. <b>Ocak birden alev alınca neye uğradığını bilemedi. </b>'),
(1850,	'Niyet etmek',	'Bir şeyi yapmayı zihninde tasarlamak, düşünmek. <b>Ona hediye almaya niyet etmişti. </b>'),
(1851,	'Niyeti bozuk',	'Kötü bir davranışta bulunması beklenen, kötülük düşündüğü sezilen. <b>Niyeti bozuk bunların, sakın ilişmeyin. </b>'),
(1852,	'Noktası noktasına',	'Tastamam, eksiksiz, tamamen, birbiriyle tıpatıp aynı. <b>Noktası noktasına hatırlıyorum o kavgayı. </b>'),
(1853,	'Not düşmek',	'Yazılı metnin bulunduğu sayfanın bir köşesine, konuyla ilgili birkaç cümle yazmak.'),
(1854,	'Notunu vermek',	'Kıymetini tespit etmek, ne nitelikte bir kişi olduğu konusunda kanıya varmak. <b>Hâlâ notunu veremedin mi o adamın?</b>'),
(1855,	'Nuh der peygamber demez',	'Son derece inatçıdır, düşüncelerini bir türlü değiştirmez, söylediklerinde ve inançlarında direnir.'),
(1856,	'Nuh Nebi`den kalma',	'Çok eski modası geçmiş, köhnemiş (eşya, bina). <b>Nuh Nebi`den kalma bir koltukta oturuyordu. </b>'),
(1857,	'Numara yapmak',	'Bir hareketi yalandan yapmak, bir şeyi gerçekmiş gibi söyleyerek karşısındakini aldatmak. <b>Ona öyle bir numara yapacağım ki şaşkına dönecek. </b>'),
(1858,	'Nur topu',	'Gürbüz, sağlıklı, çok güzel ve temiz çocuklar için söylenir.'),
(1859,	'Nutku tutulmak',	'Korkudan, üzüntüden, heyecandan konuşamaz olmak. <b>Katili karşısında görünce nutku tutuldu. </b>'),
(1860,	'Ocağı kör kalmak',	'Soyunu sürdürecek çocuğu bulanmamak, soyu tükenmiş olmak.'),
(1861,	'Ocağına düşmek',	'Birine yardım etmesi için yalvarmak, koruması için sığınmak. <b>Ocağına düştüm ağam, beni bu işten ancak sen kurtarırsın!</b>'),
(1862,	'Ocağına incir dikmek',	'Birinin evini barkını dağıtmak, düzenini alt üst etmek, yuvasını yıkıp toparlanamaz hâle getirmek. <b>Bende senin ocağına incir dikmezsem dedi ama dediğine pişman oldu. </b>'),
(1863,	'Ocağını söndürmek',	'Ailenin dağılmasına sebep olmak, çoluk çocuğunu yok etmek. <b>Ocağımı söndürdü katiller!</b>'),
(1864,	'Oğul balı',	'1. Evlât, evlâdın ana babaya yansıyan geliri. <br />2. Oğul arılarının yaptığı bal.'),
(1865,	'Oğul vermek',	'Oğul arılarının bir bölüğü kovandan ayrılıp başka bir kovana gitmek, yeni bir oğul arısı topluluğu meydana getirmek.'),
(1866,	'Okkalı kahve',	'Bol kahve ile yapılmış ve büyük fincana konmuş kahve. <b>Bir okkalı kahve daha çek usta!</b>'),
(1867,	'Okka çekmek',	'Hacminden daha fazla ağır gelmek.'),
(1868,	'Okkanın altına girmek',	'Haksız yere eziyet çekmek, zarar ve ceza görmek. <b>Uyanık ol da okkanın altına gireyim deme, tamam mı?</b>'),
(1869,	'Ok yaydan çıkmak',	'Geri dönülemeyecek bir iş yapmak, söz söylemek ya da bir harekette bulunmak. <b>Ok yaydan çıktı bir kere, çaresiz dövüşeceğiz. </b>'),
(1870,	'Ola ki. . .',	'Belki olur ya, olabilir ki. . . <b>Ola ki bir daha karşılaşırız. </b>'),
(1871,	'Olan biten',	'Olup geçenler, olanların hepsi, meydana gelenler. <b>Olan bitenden hiç haberim olmadı. </b>'),
(1872,	'Oldu bittiye getirmek',	'Emrivaki yapmak, geri dönülmesi güç ve imkânsız bir durum oluşturmak. <b>Oldu bittiye getirerek tarlayı satın aldılar. </b>'),
(1873,	'Oldum bittim (veya oldum olası)',	'Başından beri, öteden beri, ilk zamandan beri, kendimi bildiğimden beri. <b>Oldum bittim kızarım bu adamlara. </b>'),
(1874,	'Oldu olacak kırıldı nacak',	'<b>Olanlar oldu, iş işten geçti, olanlar geri dönülemeyecek bir durum aldı, bunu kabul etmek gerek</b> anlamında kullanılır.'),
(1875,	'Olmayacak duaya amin demek',	'Sonuç vermeyecek bir işle uğraşmak ya da buna destek vermek.'),
(1876,	'Olur olmaz',	'1. Meydana gelmesinden hemen sonra. <br />2. Rast gele, sıradan. <br />3. Gerekli gereksiz, yerli yersiz, önemli önemsiz durumu gözetilmeden yapılan (iş) ya da söylenen (söz).'),
(1877,	'Oluruna bırakmak',	'Bir işin yapılabildiği, olabildiği kadarıyla yetinmek, müdahale etmeden bekleyip sonucuna ne olursa olsun razı olmak. <b>Artık oluruna bıraktık işi. </b>'),
(1878,	'Omuz omuza',	'1. Birbirine destek vererek, dayanışarak. <br />2. Yan yana, çok sıkışık. <b>Omuz omuza vererek bu zorluğun altından kalkmamız mümkün. </b>'),
(1879,	'Omuz silkmek',	'Aldırmamak, önem vermemek, benimsememek. <b>Sana bunu alacağım dedim ama o, omuz silkti. </b>'),
(1880,	'On parmağında on kara',	'İnsanlara leke sürmeyi, kara çalmayı, iftira atmayı huy edinmiş (kimse).'),
(1881,	'On parmağında on marifet',	'Çok hünerli, becerikli, ustalığı çok, elinden her iş gelir.'),
(1882,	'Onuruna dokunmak',	'Onurunu, haysiyetini incitmek. <b>Dikkatli ol, birinin onuruna dokunacak iş yapma. </b>'),
(1883,	'Oralarda (oralı) olmamak',	'Anlamamış, sezmemiş gibi davranmak. <b>O sözler ona söyleniyordu ama hiç oralı olmadı. </b>'),
(1884,	'Ortada kalmak',	'1. Yersiz yurtsuz kalmak, barınacak yer bulamamak. <br />2. İki şey arasında kalmak. <br />3. (Bir şeyi) kimse üzerine almamak. <b>Belediye evlerini yıkınca çoluk çocuk öylece ortada kaldılar. </b>'),
(1885,	'Ortadan kalkmak',	'1. Görünmez, bulunmaz olmak. <br />2. Yok olmak. <b>Sis ortadan kalktı. </b>'),
(1886,	'Ortadan kaybolmak',	'Nereye gittiği bilinmemek, sezdirmeden gitmek, görünmez hâle gelmek. <b>Ali ortadan kayboldu. </b>'),
(1887,	'Orta hâlli',	'Ne zengin ne yoksul, ne iyi ne kötü, ne çirkin ne güzel. <b>Onlar orta hâlli bir ailedirler. </b>'),
(1888,	'Ortalığı birbirine katmak',	'Kargaşa çıkarmak, herkesi birbirine düşürmek. <b>Şimdi gelip ortalığı birbirine katacak diye korkuyorum. </b>'),
(1889,	'Ortalık düzelmek',	'Tedirginlik kalmamak, toplum içindeki karışıklık yok olmak. <b>Çok şükür ortalık düzeldi. </b>'),
(1890,	'Ortalık karışmak',	'Kargaşa çıkmak, toplumda düzensizlik baş göstermek. <b>Ortalık yine karıştı, insanlar birbirine girdi. </b>'),
(1891,	'Orta malı',	'1. Herkesin yararlandığı (şey). <br />2. Her isteyenle ilişkide bulunan. <b>Benim bisikletim orta malı mı ki herkes binmeye çalışıyor. </b>'),
(1892,	'Ortaya dökmek',	'1. Gizli olan ne varsa açıklamak. <br />2. Çıkarıp göstermek. <b>Bütün sırlarını ortaya dökmek için harekete geçti. </b>'),
(1893,	'O tarakta bezi olmamak',	'Bir şeyle, bir işle ilişiği bulunmamak, o şeyle ilgilenmemek. <b>O tarakta bezi olacağını hiç sanmam. </b>'),
(1894,	'Ot yoldurmak',	'Çok güçlük çıkarmak, zor bir iş gördürmek, çok uğraştırmak.'),
(1895,	'Oya koymak',	'Bir işin sonucunu belirlemek üzere oy verilmesini istemek, oylama yoluyla bir topluluğun görüşünü almak. <b>Bu görüşü oya koymayı teklif ediyorum, kabul edenler el kaldırsınlar. </b>'),
(1896,	'Oy birliği',	'Bir toplantıya katılan, bir meseleyi konuşan kimselerin aynı düşüncede olup aynı yönde oy kullanmaları. <b>Sınıf başkanını oy birliği ile seçtik. </b>'),
(1897,	'Oyuna gelmek',	'Aldatılmak, tuzağa düşürülmek. <b>Onların oyununa gelmemeye çalış, dikkatli ol. </b>'),
(1898,	'Oyunbozanlık etmek',	'Mızıkçılık etmek, birlikte yapılması gereken işten tek taraflı vazgeçmek. <b>Oyunbozanlık etme de gel birlikte eğlenelim. </b>'),
(1899,	'Oyun etmek',	'Aldatmak, kurnazlıkla birini tuzağa düşürmek. <b>Bana kötü bir oyun ettiler. </b>'),
(1900,	'Öbür (öteki) dünya',	'Ahiret, insanların öldükten sonra gidecekleri ve ebedî olarak kalacakları âlem. <b>Öteki dünyada inşallah yüzümüz güler. </b>'),
(1901,	'Öç almak',	'Yapılan bir kötülüğün acısını aynı derecede bir kötülük yaparak çıkarmak. <b>Öç alma fikrinden vazgeçirmeliyiz onu. </b>'),
(1902,	'Ödü patlamak',	'Ani bir olay sebebiyle çok korkmak. <b>Fareden ödüm kopar. </b>'),
(1903,	'Öküzün altında buzağı aramak',	'Kimi sebepler, bahaneler uydurarak suç ve suçlu bulma çabasında olmak.'),
(1904,	'Öküz öldü, ortaklık bozuldu',	'Aradaki yakınlık dayanağı kalktı, yakınlık da kalmadı.'),
(1905,	'Ölçüyü kaçırmak',	'Uygun derecenin üstüne çıkmak, aşırı gitmek, <b>Sofraya her oturuşunda ölçüyü kaçırırdı. </b>'),
(1906,	'Ölme eşeğim ölme (yaza yonca bitecek)',	'Umutsuz bir bekleyişi anlatmak için kullanılır.'),
(1907,	'Ölmek var, dönmek yok',	'<b>Neye mal olursa olsun, iş sonuna kadar götürülecektir, yapılmasından kaçınılmayacaktır</b> anlamında kullanılır. <b>Özgürlük yolunda ölmek var, dönmek yok bize. </b>'),
(1908,	'Ölü fiyatına',	'Yok pahasına, değerinden çok ucuza, az bir para ile. <b>Arsaları ölü fiyatına satmak zorunda kaldık. </b>'),
(1909,	'Ölü mevsim',	'İşin veya alışverişin az olduğu, durgun geçtiği zaman dilimi. <b>Bizim iş en ölü mevsimini yaşıyor. </b>'),
(1910,	'Ölüm Allah`ın emri',	'1. Herkes ölecek, ölüm mukadderdir. <br />2. Kesin karar verme durumunda kullanılır.'),
(1911,	'Ölümü göze almak',	'Yaptığı iş uğruna ölmekten korkmamak, yürekli davranmak. <b>Allah yolunda ölümü göze aldı yiğitler. </b>'),
(1912,	'Ölümüne susamak',	'Yapmakta olduğu tehlikeli işte ölümü kendi üzerine çekecek davranışta bulunmak. <b>Ölümüne mi susadın, çekil şu arabanın önünden!</b>'),
(1913,	'Ölüp ölüp dirilmek',	'1. Çok ağır bir hastalıktan kurtulmak. <br />2. Ard arda gelen sıkıntılı, acı veren durumlara düşmek.'),
(1914,	'Ölür müsün, öldürür müsün?',	'<b>Öyle ters bir iş yaptı ki ona mı ceza vermeliyim kendime mi?</b> anlamında kullanılır.'),
(1915,	'Ömrü billah',	'Hiçbir zaman, ya da şimdiye kadar. <b>Ömrü billah yalan söylememiştir o. </b>'),
(1916,	'Ömrüne bereket',	'<b>Var ol, sağ ol, ömrün uzun olsun</b> anlamında kullanılır.'),
(1917,	'Ömrü vefa etmemek',	'Bir şeye kavuşamadan, bir sonuca ulaşamadan ölmek. <b>Okulunu bitirip doktor olacaktı ama ömrü vefa etmedi. </b>'),
(1918,	'Ömür adam',	'Beğenilen, çok hoşa giden, değişik düşünceleri olan adam.'),
(1919,	'Ömür çürütmek',	'Uzun süre bir şey için emek vermiş olmak, ya da boşuna zaman harcamış olmak. <b>Bu ev için bir ömür çürüttüm ben. </b>'),
(1920,	'Ömür sürmek',	'İyi ve rahat yaşamış olmak. <b>Uzun bir ömür sürdü dedem. </b>'),
(1921,	'Ömür törpüsü',	'İnsanı yıpratan, yoran, sıkıntıya sokan, uzun ve yorucu iş.'),
(1922,	'Ön ayak olmak',	'Bir işin yapılmasında ilk başlayan olup herkesi arkasından sürüklemek. <b>Haydi ön ayak olda koşsunlar biraz. </b>'),
(1923,	'Öne düşmek',	'1. Önderlik ya da kılavuzluk etmek. <br />2. En önde yürümek.'),
(1924,	'Önüne gelen',	'Olur olmaz kimse, herkes, karşısına çıkan. <b>Önüne gelene sordu ama bulamadı. </b>'),
(1925,	'Öpüp başına koymak',	'Bir şeyi minnetle karşılamak, seve seve kabul etmek. <b>Adam sana iş verecekmiş, daha ne istiyorsun, öpüp başına koy. </b>'),
(1926,	'Örtbas etmek',	'Kötü bir durumu gizlemek, yayılmasını önlemek. <b>Dairede yapılan yolsuzlukları örtbas edeceklerini sandılar. </b>'),
(1927,	'Örümcek kafalı',	'Geri düşünceli, yenilikleri kolay kabul etmeyen (kimse).'),
(1928,	'Öteden beri',	'Oldukça uzun zamandan beri, eskiden beri. <b>Öteden beri sevmem ben onu. </b>'),
(1929,	'Ötesi çıkmaz sokak',	'<b>Takip edilen yol yanlıştır, bu yolla bir yere gidilemez, sonuç alınamaz, bir yere kadar gidilir ama daha fazla gidilemez</b> anlamında kullanılır.'),
(1930,	'Özenip bezenmek',	'Çok özen gösterip titizlikle, ayrıntılarına varıncaya değin ele almak.'),
